import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

function Products() {
  const [products, setProducts] = useState([]);
  const [currentProduct, setCurrentProduct] = useState({
    name: '',
    description: '',
    price: '',
    costPrice: '',
    stock: '',
    category: 'Otros',
    brand: '',
    minimumStock: '5'
  });
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState('');

  const categories = [
    'Electrónica',
    'Ropa',
    'Alimentos',
    'Bebidas',
    'Hogar',
    'Oficina',
    'Otros'
  ];

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await api.getProducts();
      setProducts(data.data);
    } catch (error) {
      setError('Error al cargar productos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const productData = {
        ...currentProduct,
        price: Number(currentProduct.price),
        costPrice: Number(currentProduct.costPrice),
        stock: Number(currentProduct.stock),
        minimumStock: Number(currentProduct.minimumStock)
      };

      if (currentProduct._id) {
        await api.updateProduct(currentProduct._id, productData);
      } else {
        await api.createProduct(productData);
      }
      setShowModal(false);
      loadProducts();
      setError('');
    } catch (error) {
      setError(error.response?.data?.message || 'Error al guardar el producto');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este producto?')) {
      try {
        await api.deleteProduct(id);
        loadProducts();
      } catch (error) {
        setError('Error al eliminar el producto');
      }
    }
  };

  const resetForm = () => {
    setCurrentProduct({
      name: '',
      description: '',
      price: '',
      costPrice: '',
      stock: '',
      category: 'Otros',
      brand: '',
      minimumStock: '5'
    });
    setError('');
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-4">
        <h2>Productos</h2>
        <button 
          className="btn btn-primary" 
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          Nuevo Producto
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="table-responsive">
        <table className="table">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Costo</th>
              <th>Stock</th>
              <th>Categoría</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>${product.price}</td>
                <td>${product.costPrice}</td>
                <td>{product.stock}</td>
                <td>{product.category}</td>
                <td>
                  <button 
                    className="btn btn-sm btn-primary me-2"
                    onClick={() => {
                      setCurrentProduct(product);
                      setShowModal(true);
                    }}
                  >
                    Editar
                  </button>
                  <button 
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(product._id)}
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {currentProduct._id ? 'Editar Producto' : 'Nuevo Producto'}
                </h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => {
                    setShowModal(false);
                    resetForm();
                  }}
                ></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Nombre *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={currentProduct.name}
                      onChange={(e) => setCurrentProduct({
                        ...currentProduct,
                        name: e.target.value
                      })}
                      required
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label">Descripción</label>
                    <textarea
                      className="form-control"
                      value={currentProduct.description || ''}
                      onChange={(e) => setCurrentProduct({
                        ...currentProduct,
                        description: e.target.value
                      })}
                    />
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Precio Venta *</label>
                      <input
                        type="number"
                        className="form-control"
                        value={currentProduct.price}
                        onChange={(e) => setCurrentProduct({
                          ...currentProduct,
                          price: e.target.value
                        })}
                        required
                        min="0"
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Precio Costo *</label>
                      <input
                        type="number"
                        className="form-control"
                        value={currentProduct.costPrice}
                        onChange={(e) => setCurrentProduct({
                          ...currentProduct,
                          costPrice: e.target.value
                        })}
                        required
                        min="0"
                      />
                    </div>
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Stock *</label>
                      <input
                        type="number"
                        className="form-control"
                        value={currentProduct.stock}
                        onChange={(e) => setCurrentProduct({
                          ...currentProduct,
                          stock: e.target.value
                        })}
                        required
                        min="0"
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Stock Mínimo</label>
                      <input
                        type="number"
                        className="form-control"
                        value={currentProduct.minimumStock}
                        onChange={(e) => setCurrentProduct({
                          ...currentProduct,
                          minimumStock: e.target.value
                        })}
                        min="0"
                      />
                    </div>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Categoría *</label>
                    <select
                      className="form-select"
                      value={currentProduct.category}
                      onChange={(e) => setCurrentProduct({
                        ...currentProduct,
                        category: e.target.value
                      })}
                      required
                    >
                      {categories.map(cat => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Marca</label>
                    <input
                      type="text"
                      className="form-control"
                      value={currentProduct.brand || ''}
                      onChange={(e) => setCurrentProduct({
                        ...currentProduct,
                        brand: e.target.value
                      })}
                    />
                  </div>

                  <button type="submit" className="btn btn-primary w-100">
                    {currentProduct._id ? 'Actualizar' : 'Crear'}
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Products;
