import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';

export function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    document: '',
    email: '',
    phone: '',
    address: '',
    creditLimit: 0
  });
  const [paymentData, setPaymentData] = useState({
    amount: 0,
    type: 'PAGO',
    description: ''
  });

  useEffect(() => {
    loadCustomers();
  }, []);

  const loadCustomers = async () => {
    try {
      setLoading(true);
      const response = await api.getCustomers();
      if (response.success) {
        setCustomers(response.data);
      } else {
        setError('Error al cargar los clientes');
      }
    } catch (error) {
      setError('Error al cargar los clientes');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePaymentInputChange = (e) => {
    const { name, value } = e.target;
    setPaymentData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedCustomer) {
        await api.updateCustomer(selectedCustomer._id, formData);
        toast.success('Cliente actualizado exitosamente');
      } else {
        await api.createCustomer(formData);
        toast.success('Cliente creado exitosamente');
      }
      setShowModal(false);
      loadCustomers();
      resetForm();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al procesar la operación');
    }
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    try {
      await api.registerCustomerPayment(selectedCustomer._id, paymentData);
      toast.success('Pago registrado exitosamente');
      setShowPaymentModal(false);
      loadCustomers();
      resetPaymentForm();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al registrar el pago');
    }
  };

  const handleEdit = (customer) => {
    setSelectedCustomer(customer);
    setFormData({
      name: customer.name,
      document: customer.document,
      email: customer.email,
      phone: customer.phone,
      address: customer.address,
      creditLimit: customer.creditLimit
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Está seguro de eliminar este cliente?')) {
      try {
        await api.deleteCustomer(id);
        toast.success('Cliente eliminado exitosamente');
        loadCustomers();
      } catch (error) {
        toast.error(error.response?.data?.message || 'Error al eliminar el cliente');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      document: '',
      email: '',
      phone: '',
      address: '',
      creditLimit: 0
    });
    setSelectedCustomer(null);
  };

  const resetPaymentForm = () => {
    setPaymentData({
      amount: 0,
      type: 'PAGO',
      description: ''
    });
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(value);
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid mt-4">
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Gestión de Clientes</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          <i className="fas fa-plus"></i> Nuevo Cliente
        </button>
      </div>

      <div className="table-responsive">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Documento</th>
              <th>Email</th>
              <th>Teléfono</th>
              <th>Límite de Crédito</th>
              <th>Deuda Actual</th>
              <th>Estado</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {customers.map(customer => (
              <tr key={customer._id}>
                <td>{customer.name}</td>
                <td>{customer.document}</td>
                <td>{customer.email}</td>
                <td>{customer.phone}</td>
                <td>{formatCurrency(customer.creditLimit)}</td>
                <td>{formatCurrency(customer.currentDebt)}</td>
                <td>
                  <span className={`badge bg-${
                    customer.status === 'AL_DIA' ? 'success' :
                    customer.status === 'EN_MORA' ? 'warning' : 'danger'
                  }`}>
                    {customer.status === 'AL_DIA' ? 'Al día' :
                     customer.status === 'EN_MORA' ? 'En mora' : 'Bloqueado'}
                  </span>
                </td>
                <td>
                  <div className="btn-group">
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => handleEdit(customer)}
                      title="Editar"
                    >
                      <i className="fas fa-edit"></i>
                    </button>
                    <button
                      className="btn btn-sm btn-outline-success ms-1"
                      onClick={() => {
                        setSelectedCustomer(customer);
                        setShowPaymentModal(true);
                      }}
                      title="Registrar Pago"
                    >
                      <i className="fas fa-dollar-sign"></i>
                    </button>
                    <button
                      className="btn btn-sm btn-outline-danger ms-1"
                      onClick={() => handleDelete(customer._id)}
                      title="Eliminar"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal de Cliente */}
      <div className={`modal fade ${showModal ? 'show' : ''}`} style={{ display: showModal ? 'block' : 'none' }}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">
                {selectedCustomer ? 'Editar Cliente' : 'Nuevo Cliente'}
              </h5>
              <button
                type="button"
                className="btn-close"
                onClick={() => setShowModal(false)}
              ></button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Nombre</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Documento</label>
                  <input
                    type="text"
                    className="form-control"
                    name="document"
                    value={formData.document}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Teléfono</label>
                  <input
                    type="tel"
                    className="form-control"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Dirección</label>
                  <input
                    type="text"
                    className="form-control"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Límite de Crédito</label>
                  <input
                    type="number"
                    className="form-control"
                    name="creditLimit"
                    value={formData.creditLimit}
                    onChange={handleInputChange}
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Cancelar
                </button>
                <button type="submit" className="btn btn-primary">
                  {selectedCustomer ? 'Actualizar' : 'Crear'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Modal de Pago */}
      <div className={`modal fade ${showPaymentModal ? 'show' : ''}`} style={{ display: showPaymentModal ? 'block' : 'none' }}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Registrar Transacción</h5>
              <button
                type="button"
                className="btn-close"
                onClick={() => setShowPaymentModal(false)}
              ></button>
            </div>
            <form onSubmit={handlePayment}>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Cliente</label>
                  <input
                    type="text"
                    className="form-control"
                    value={selectedCustomer?.name || ''}
                    disabled
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Tipo de Transacción</label>
                  <select
                    className="form-select"
                    name="type"
                    value={paymentData.type}
                    onChange={handlePaymentInputChange}
                    required
                  >
                    <option value="PAGO">Pago</option>
                    <option value="CARGO">Cargo</option>
                  </select>
                </div>
                <div className="mb-3">
                  <label className="form-label">Monto</label>
                  <input
                    type="number"
                    className="form-control"
                    name="amount"
                    value={paymentData.amount}
                    onChange={handlePaymentInputChange}
                    min="0"
                    step="0.01"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Descripción</label>
                  <textarea
                    className="form-control"
                    name="description"
                    value={paymentData.description}
                    onChange={handlePaymentInputChange}
                    rows="3"
                  ></textarea>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowPaymentModal(false)}
                >
                  Cancelar
                </button>
                <button type="submit" className="btn btn-primary">
                  Registrar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Overlay para los modales */}
      {(showModal || showPaymentModal) && (
        <div className="modal-backdrop fade show"></div>
      )}
    </div>
  );
}

export default Customers;
