import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';

// Registrar componentes de Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

export function Dashboard() {
  const [stats, setStats] = useState({
    salesStats: {
      totalSales: 0,
      monthlySales: [],
      averageTicket: 0,
      totalTransactions: 0,
      topProducts: [],
      salesByDayOfWeek: []
    },
    customerStats: {
      totalCustomers: 0,
      totalDebt: 0,
      customersByStatus: {
        AL_DIA: 0,
        EN_MORA: 0,
        BLOQUEADO: 0
      },
      debtRanges: [],
      topDebtors: []
    },
    inventoryStats: {
      totalProducts: 0,
      totalValue: 0,
      categoryStats: [],
      stockStatus: [],
      lowStock: []
    }
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const { salesStats, debtStats, inventoryStats } = await api.getStats();

      setStats({
        salesStats: {
          totalSales: salesStats?.data?.totalSales || 0,
          monthlySales: salesStats?.data?.monthlySales || [],
          averageTicket: salesStats?.data?.averageTicket || 0,
          totalTransactions: salesStats?.data?.totalTransactions || 0,
          topProducts: salesStats?.data?.topProducts || [],
          salesByDayOfWeek: salesStats?.data?.salesByDayOfWeek || []
        },
        customerStats: {
          totalCustomers: debtStats?.data?.totalCustomers || 0,
          totalDebt: debtStats?.data?.totalDebt || 0,
          customersByStatus: debtStats?.data?.customersByStatus || {
            AL_DIA: 0,
            EN_MORA: 0,
            BLOQUEADO: 0
          },
          debtRanges: debtStats?.data?.debtRanges || [],
          topDebtors: debtStats?.data?.topDebtors || []
        },
        inventoryStats: {
          totalProducts: inventoryStats?.data?.totalProducts || 0,
          totalValue: inventoryStats?.data?.totalValue || 0,
          categoryStats: inventoryStats?.data?.categoryStats || [],
          stockStatus: inventoryStats?.data?.stockStatus || [],
          lowStock: inventoryStats?.data?.lowStock || []
        }
      });
    } catch (error) {
      console.error('Error al cargar estadísticas:', error);
      setError('Error al cargar las estadísticas del dashboard');
      toast.error('Error al cargar las estadísticas del dashboard');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  const monthlySalesData = {
    labels: stats.salesStats?.monthlySales?.map(sale => sale.month) || [],
    datasets: [{
      label: 'Ventas Mensuales',
      data: stats.salesStats?.monthlySales?.map(sale => sale.total) || [],
      fill: false,
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  const customerStatusData = {
    labels: ['Al Día', 'En Mora', 'Bloqueados'],
    datasets: [{
      data: [
        stats.customerStats?.customersByStatus?.AL_DIA || 0,
        stats.customerStats?.customersByStatus?.EN_MORA || 0,
        stats.customerStats?.customersByStatus?.BLOQUEADO || 0
      ],
      backgroundColor: [
        'rgb(75, 192, 192)',
        'rgb(255, 205, 86)',
        'rgb(255, 99, 132)'
      ]
    }]
  };

  const stockStatusData = {
    labels: ['Sin Stock', 'Bajo Stock', 'Stock Moderado', 'Stock Suficiente'],
    datasets: [{
      data: stats.inventoryStats.stockStatus.map(status => status.count),
      backgroundColor: [
        'rgb(255, 99, 132)',
        'rgb(255, 205, 86)',
        'rgb(54, 162, 235)',
        'rgb(75, 192, 192)'
      ]
    }]
  };

  return (
    <div className="container-fluid mt-4">
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Resumen General */}
      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card bg-primary text-white h-100">
            <div className="card-body">
              <h6 className="card-title">Ventas Totales</h6>
              <h3 className="card-text">{formatCurrency(stats.salesStats?.totalSales || 0)}</h3>
              <p className="mb-0">
                Ticket Promedio: {formatCurrency(stats.salesStats?.averageTicket || 0)}
                <br />
                Transacciones: {stats.salesStats?.totalTransactions || 0}
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-warning text-dark h-100">
            <div className="card-body">
              <h6 className="card-title">Deuda Total</h6>
              <h3 className="card-text">{formatCurrency(stats.customerStats?.totalDebt || 0)}</h3>
              <p className="mb-0">
                Clientes: {stats.customerStats?.totalCustomers || 0}
                <br />
                En Mora: {stats.customerStats?.customersByStatus?.EN_MORA || 0}
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-success text-white h-100">
            <div className="card-body">
              <h6 className="card-title">Valor del Inventario</h6>
              <h3 className="card-text">{formatCurrency(stats.inventoryStats?.totalValue || 0)}</h3>
              <p className="mb-0">
                Productos: {stats.inventoryStats?.totalProducts || 0}
                <br />
                Categorías: {stats.inventoryStats?.categoryStats?.length || 0}
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-danger text-white h-100">
            <div className="card-body">
              <h6 className="card-title">Productos Bajo Stock</h6>
              <h3 className="card-text">{stats.inventoryStats?.lowStock?.length || 0}</h3>
              <p className="mb-0">
                Sin Stock: {stats.inventoryStats?.stockStatus?.find(s => s._id === 'Sin Stock')?.count || 0}
                <br />
                Bajo Mínimo: {stats.inventoryStats?.stockStatus?.find(s => s._id === 'Bajo Stock')?.count || 0}
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="row mb-4">
        {/* Gráfico de Ventas */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Tendencia de Ventas</h5>
              <Line data={monthlySalesData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                  title: {
                    display: true,
                    text: 'Ventas Mensuales'
                  }
                }
              }} />
            </div>
          </div>
        </div>

        {/* Estado de Clientes */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Estado de Clientes</h5>
              <Pie data={customerStatusData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'bottom'
                  }
                }
              }} />
            </div>
          </div>
        </div>
      </div>

      <div className="row mb-4">
        {/* Estado del Inventario */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Estado del Inventario</h5>
              <Pie data={stockStatusData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'bottom'
                  }
                }
              }} />
            </div>
          </div>
        </div>

        {/* Productos con Stock Crítico */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Productos con Stock Crítico</h5>
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Producto</th>
                      <th>Categoría</th>
                      <th>Stock Actual</th>
                      <th>Stock Mínimo</th>
                      <th>Estado</th>
                      <th>Valor</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.inventoryStats.lowStock.map((product, index) => (
                      <tr key={index}>
                        <td>{product.name}</td>
                        <td>{product.category}</td>
                        <td>{product.stock}</td>
                        <td>{product.minStock}</td>
                        <td>
                          <span className={`badge ${
                            product.status === 'Sin Stock' ? 'bg-danger' : 'bg-warning'
                          }`}>
                            {product.status}
                          </span>
                        </td>
                        <td>{formatCurrency(product.value)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Top Productos */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Productos Más Vendidos</h5>
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Producto</th>
                      <th>Cantidad</th>
                      <th>Total</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.salesStats.topProducts.map((product, index) => (
                      <tr key={index}>
                        <td>{product.name}</td>
                        <td>{product.quantity}</td>
                        <td>{formatCurrency(product.total)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Top Deudores */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Principales Deudores</h5>
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Cliente</th>
                      <th>Documento</th>
                      <th>Deuda</th>
                      <th>Estado</th>
                      <th>Último Pago</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.customerStats.topDebtors.map((debtor, index) => (
                      <tr key={index}>
                        <td>{debtor.name}</td>
                        <td>{debtor.document}</td>
                        <td>{formatCurrency(debtor.debt)}</td>
                        <td>
                          <span className={`badge ${
                            debtor.status === 'AL_DIA' ? 'bg-success' :
                            debtor.status === 'EN_MORA' ? 'bg-warning' :
                            'bg-danger'
                          }`}>
                            {debtor.status.replace('_', ' ')}
                          </span>
                        </td>
                        <td>{new Date(debtor.lastPayment).toLocaleDateString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
