import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import { Modal } from './common/Modal';
import { Button } from './common/Button';
import { Table } from './common/Table';

export function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    sortBy: 'name'
  });
  
  const [modalConfig, setModalConfig] = useState({
    show: false,
    type: null, // 'create', 'edit', 'payment'
    title: '',
    data: null
  });

  useEffect(() => {
    loadCustomers();
  }, [filters]);

  const loadCustomers = async () => {
    try {
      const response = await api.getCustomers(filters);
      setCustomers(response.data);
    } catch (error) {
      toast.error('Error al cargar la lista de clientes');
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (type, customer = null) => {
    const titles = {
      create: 'Nuevo Cliente',
      edit: 'Editar Cliente',
      payment: 'Registrar Pago'
    };

    setModalConfig({
      show: true,
      type,
      title: titles[type],
      data: customer || {
        name: '',
        document: '',
        creditLimit: 0,
        status: 'AL_DIA'
      }
    });
  };

  const handleCloseModal = () => {
    setModalConfig({
      show: false,
      type: null,
      title: '',
      data: null
    });
  };

  const handleSubmit = async (formData) => {
    try {
      if (modalConfig.type === 'edit') {
        await api.updateCustomer(formData.id, formData);
        toast.success('Cliente actualizado exitosamente');
      } else if (modalConfig.type === 'create') {
        await api.createCustomer(formData);
        toast.success('Cliente creado exitosamente');
      } else if (modalConfig.type === 'payment') {
        await api.registerPayment(modalConfig.data.id, formData);
        toast.success('Pago registrado exitosamente');
      }
      handleCloseModal();
      loadCustomers();
    } catch (error) {
      toast.error('Error al procesar la operación');
    }
  };

  const renderModalContent = () => {
    if (!modalConfig.show) return null;

    if (modalConfig.type === 'payment') {
      return (
        <form onSubmit={(e) => {
          e.preventDefault();
          handleSubmit({
            customerId: modalConfig.data.id,
            amount: e.target.amount.value,
            date: e.target.date.value
          });
        }}>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Monto</label>
              <input
                type="number"
                name="amount"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Fecha</label>
              <input
                type="date"
                name="date"
                defaultValue={new Date().toISOString().split('T')[0]}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
          </div>
        </form>
      );
    }

    return (
      <form onSubmit={(e) => {
        e.preventDefault();
        handleSubmit({
          id: modalConfig.data?.id,
          name: e.target.name.value,
          document: e.target.document.value,
          creditLimit: parseFloat(e.target.creditLimit.value),
          status: e.target.status.value
        });
      }}>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Nombre</label>
            <input
              type="text"
              name="name"
              defaultValue={modalConfig.data?.name}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Documento</label>
            <input
              type="text"
              name="document"
              defaultValue={modalConfig.data?.document}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Límite de Crédito</label>
            <input
              type="number"
              name="creditLimit"
              defaultValue={modalConfig.data?.creditLimit}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Estado</label>
            <select
              name="status"
              defaultValue={modalConfig.data?.status}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            >
              <option value="AL_DIA">Al Día</option>
              <option value="EN_MORA">En Mora</option>
              <option value="BLOQUEADO">Bloqueado</option>
            </select>
          </div>
        </div>
      </form>
    );
  };

  const columns = [
    { header: 'Nombre', accessor: 'name' },
    { header: 'Documento', accessor: 'document' },
    {
      header: 'Deuda Total',
      accessor: 'totalDebt',
      cell: (value) => new Intl.NumberFormat('es-PE', {
        style: 'currency',
        currency: 'PEN'
      }).format(value)
    },
    {
      header: 'Límite de Crédito',
      accessor: 'creditLimit',
      cell: (value) => new Intl.NumberFormat('es-PE', {
        style: 'currency',
        currency: 'PEN'
      }).format(value)
    },
    {
      header: 'Estado',
      accessor: 'status',
      cell: (value) => (
        <span className={`px-2 py-1 rounded text-white ${
          value === 'AL_DIA' ? 'bg-green-500' :
          value === 'EN_MORA' ? 'bg-yellow-500' :
          'bg-red-500'
        }`}>
          {value.replace('_', ' ')}
        </span>
      )
    },
    {
      header: 'Último Pago',
      accessor: 'lastPaymentDate',
      cell: (value) => value ? new Date(value).toLocaleDateString('es-ES') : 'No registrado'
    },
    {
      header: 'Acciones',
      accessor: 'id',
      cell: (_, row) => (
        <div className="flex space-x-2">
          <Button
            onClick={() => handleOpenModal('edit', row)}
            variant="icon"
            className="text-blue-500"
          >
            <i className="fas fa-edit" />
          </Button>
          <Button
            onClick={() => handleOpenModal('payment', row)}
            variant="icon"
            className="text-green-500"
          >
            <i className="fas fa-dollar-sign" />
          </Button>
        </div>
      )
    }
  ];

  if (loading) {
    return (
      <div className="flex justify-center items-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500" />
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Clientes</h1>
        <Button
          onClick={() => handleOpenModal('create')}
          variant="primary"
        >
          Nuevo Cliente
        </Button>
      </div>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <input
          type="text"
          placeholder="Buscar por nombre o documento..."
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.search}
          onChange={(e) => setFilters({ ...filters, search: e.target.value })}
        />
        <select
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.status}
          onChange={(e) => setFilters({ ...filters, status: e.target.value })}
        >
          <option value="">Todos</option>
          <option value="AL_DIA">Al Día</option>
          <option value="EN_MORA">En Mora</option>
          <option value="BLOQUEADO">Bloqueado</option>
        </select>
        <select
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.sortBy}
          onChange={(e) => setFilters({ ...filters, sortBy: e.target.value })}
        >
          <option value="name">Nombre</option>
          <option value="debt">Deuda</option>
          <option value="lastPayment">Último Pago</option>
        </select>
      </div>

      <Table
        columns={columns}
        data={customers}
        className="w-full"
      />

      <Modal
        show={modalConfig.show}
        title={modalConfig.title}
        onClose={handleCloseModal}
        onConfirm={() => document.querySelector('form').requestSubmit()}
      >
        {renderModalContent()}
      </Modal>
    </div>
  );
}

export default Customers;
