// src/components/Operaciones.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Row, Col } from 'react-bootstrap';
import { operacionService, sucursalService, userService } from '../services/api';

const Operaciones = () => {
  const [operaciones, setOperaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sucursales, setSucursales] = useState([]);
  const [comerciales, setComerciales] = useState([]);
  const [filtros, setFiltros] = useState({
    sucursalId: '',
    comercialId: '',
    estado: '',
    fechaInicio: '',
    fechaFin: '',
    buscar: ''
  });

  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      
      // Cargar operaciones, sucursales y comerciales en paralelo
      const [operacionesResponse, sucursalesResponse, comercialesResponse] = await Promise.all([
        operacionService.getAll(),
        sucursalService.getAll(),
        userService.getAll()
      ]);
      
      setOperaciones(operacionesResponse.data);
      setSucursales(sucursalesResponse.data);
      
      // Filtrar solo usuarios que son comerciales
      const usuariosComerciales = comercialesResponse.data.filter(
        user => user.role === 'agent' || user.role === 'manager'
      );
      setComerciales(usuariosComerciales);
      
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar datos:', error);
      setLoading(false);
    }
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const aplicarFiltros = async () => {
    try {
      setLoading(true);
      
      // Preparar objeto de parámetros para la API
      const params = {};
      
      if (filtros.sucursalId) params.sucursalId = filtros.sucursalId;
      if (filtros.comercialId) params.comercialId = filtros.comercialId;
      if (filtros.estado) params.estado = filtros.estado;
      if (filtros.fechaInicio) params.fechaDesde = filtros.fechaInicio;
      if (filtros.fechaFin) params.fechaHasta = filtros.fechaFin;
      if (filtros.buscar) params.buscar = filtros.buscar;
      
      // Llamar a la API con los filtros
      const response = await operacionService.getAll(params);
      setOperaciones(response.data);
    } catch (error) {
      console.error('Error al aplicar filtros:', error);
    } finally {
      setLoading(false);
    }
  };

  const verDetalle = (id) => {
    // Aquí iría la lógica para ver el detalle de una operación
    console.log('Ver detalle de operación:', id);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  return (
    <div className="container-fluid">
      <h2 className="h3 mb-4">Operaciones</h2>
      
      <Card className="shadow-sm mb-4">
        <Card.Body>
          <Row className="g-3">
            <Col md={3}>
              <Form.Group>
                <Form.Label>Sucursal</Form.Label>
                <Form.Select
                  name="sucursalId"
                  value={filtros.sucursalId}
                  onChange={handleFiltroChange}
                >
                  <option value="">Todas</option>
                  {sucursales.map(sucursal => (
                    <option key={sucursal._id} value={sucursal._id}>
                      {sucursal.nombreComercial}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Comercial</Form.Label>
                <Form.Select
                  name="comercialId"
                  value={filtros.comercialId}
                  onChange={handleFiltroChange}
                >
                  <option value="">Todos</option>
                  {comerciales.map(comercial => (
                    <option key={comercial._id} value={comercial._id}>
                      {comercial.name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Estado</Form.Label>
                <Form.Select
                  name="estado"
                  value={filtros.estado}
                  onChange={handleFiltroChange}
                >
                  <option value="">Todos</option>
                  <option value="Pendiente">Pendiente</option>
                  <option value="Aprobada">Aprobada</option>
                  <option value="Rechazada">Rechazada</option>
                  <option value="Liquidada">Liquidada</option>
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Buscar</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Buscar..."
                  name="buscar"
                  value={filtros.buscar}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
            
            <Col md={6}>
              <Form.Group>
                <Form.Label>Fecha desde</Form.Label>
                <Form.Control
                  type="date"
                  name="fechaInicio"
                  value={filtros.fechaInicio}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
            
            <Col md={6}>
              <Form.Group>
                <Form.Label>Fecha hasta</Form.Label>
                <Form.Control
                  type="date"
                  name="fechaFin"
                  value={filtros.fechaFin}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
            
            <Col md={12} className="text-end">
              <Button 
                variant="primary" 
                onClick={aplicarFiltros}
                style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
              >
                <i className="fas fa-search me-1"></i> Aplicar filtros
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>
      
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando operaciones...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Cliente</th>
                    <th>Documento</th>
                    <th>Comercial</th>
                    <th>Fecha</th>
                    <th>Monto</th>
                    <th>Estado</th>
                    <th>Código</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {operaciones.length > 0 ? (
                    operaciones.map((operacion) => (
                      <tr key={operacion._id}>
                        <td>{operacion.cliente}</td>
                        <td>{operacion.documento}</td>
                        <td>{operacion.comercialId?.name || 'No asignado'}</td>
                        <td>{new Date(operacion.fechaCierre).toLocaleDateString()}</td>
                        <td>{formatCurrency(operacion.monto)}</td>
                        <td>
                          <span className={`badge ${
                            operacion.estado === 'Aprobada' ? 'bg-success' : 
                            operacion.estado === 'Rechazada' ? 'bg-danger' : 
                            operacion.estado === 'Liquidada' ? 'bg-info' : 'bg-warning'
                          }`}>
                            {operacion.estado}
                          </span>
                        </td>
                        <td>{operacion.codigoEntrega || '-'}</td>
                        <td>
                          <Button 
                            variant="outline-primary" 
                            size="sm"
                            onClick={() => verDetalle(operacion._id)}
                            title="Ver detalles"
                          >
                            <i className="fas fa-eye"></i>
                          </Button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="8" className="text-center py-4">
                        No hay operaciones que coincidan con los filtros
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default Operaciones;
