// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import { Card, Row, Col, Table } from 'react-bootstrap';
import authService from '../services/auth';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    aliados: 0,
    sucursales: 0,
    operaciones: 0,
    liquidaciones: 0
  });
  const [operacionesRecientes, setOperacionesRecientes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        
        // Verificar si el usuario está autenticado
        if (!authService.isAuthenticated()) {
          window.location.href = '/login';
          return;
        }
        
        // Obtener estadísticas del dashboard
        const response = await api.get('/api/v1/dashboard');
        
        if (response.data && response.data.success) {
          setStats({
            aliados: response.data.stats.aliados || 0,
            sucursales: response.data.stats.sucursales || 0,
            operaciones: response.data.stats.operaciones || 0,
            liquidaciones: response.data.stats.liquidaciones || 0
          });
          
          // Asegurarse de que operacionesRecientes sea un array
          setOperacionesRecientes(Array.isArray(response.data.operacionesRecientes) 
            ? response.data.operacionesRecientes 
            : []);
        } else {
          console.error('Error al obtener datos del dashboard:', response.data);
          setError('Error al cargar datos del dashboard');
          
          // Inicializar con arrays vacíos para evitar errores
          setOperacionesRecientes([]);
        }
      } catch (error) {
        console.error('Error al obtener datos del dashboard:', error);
        setError('Error al cargar datos del dashboard');
        
        // Inicializar con arrays vacíos para evitar errores
        setOperacionesRecientes([]);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Formatear números como moneda colombiana
  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(value);
  };

  if (loading) {
    return <div className="text-center p-5">Cargando datos del dashboard...</div>;
  }

  return (
    <div className="dashboard-container">
      <h1 className="mb-4">Dashboard</h1>
      
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      
      <Row className="mb-4">
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-primary rounded-circle p-3 me-3">
                <span className="text-white fw-bold">A</span>
              </div>
              <div>
                <h6 className="mb-0">Aliados</h6>
                <h3 className="mb-0">{stats.aliados}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-success rounded-circle p-3 me-3">
                <span className="text-white fw-bold">S</span>
              </div>
              <div>
                <h6 className="mb-0">Sucursales</h6>
                <h3 className="mb-0">{stats.sucursales}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-warning rounded-circle p-3 me-3">
                <span className="text-white fw-bold">O</span>
              </div>
              <div>
                <h6 className="mb-0">Operaciones</h6>
                <h3 className="mb-0">{stats.operaciones}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-info rounded-circle p-3 me-3">
                <span className="text-white fw-bold">L</span>
              </div>
              <div>
                <h6 className="mb-0">Liquidaciones</h6>
                <h3 className="mb-0">{stats.liquidaciones}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Operaciones Recientes</h5>
        </Card.Header>
        <Card.Body>
          {Array.isArray(operacionesRecientes) && operacionesRecientes.length > 0 ? (
            <Table responsive hover>
              <thead>
                <tr>
                  <th>Empleado</th>
                  <th>Monto</th>
                  <th>Fecha</th>
                  <th>Estado</th>
                </tr>
              </thead>
              <tbody>
                {operacionesRecientes.map((op) => (
                  <tr key={op._id}>
                    <td>{op.empleado}</td>
                    <td>{formatCurrency(op.monto)}</td>
                    <td>{new Date(op.fecha).toLocaleDateString('es-CO')}</td>
                    <td>
                      <span className={`badge ${
                        op.estado === 'Aprobada' ? 'bg-success' :
                        op.estado === 'Rechazada' ? 'bg-danger' : 'bg-warning'
                      }`}>
                        {op.estado}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p className="text-center">No hay operaciones recientes</p>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default Dashboard;
