// src/components/Operaciones.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Row, Col } from 'react-bootstrap';
import { api } from '../services/api';

const Operaciones = () => {
  const [operaciones, setOperaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sucursales, setSucursales] = useState([]);
  const [comerciales, setComerciales] = useState([]);
  const [filtros, setFiltros] = useState({
    sucursal: 'Todos',
    comercial: 'Todos',
    estado: 'Todos',
    fechaInicio: '25/02/2025',
    fechaFin: '12/03/2025',
    buscar: ''
  });

  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      
      // Simulación de carga de datos
      // En implementación real, estas serían llamadas a la API
      
      // Operaciones
      const operacionesData = [
        {
          _id: '1',
          cedula: '123456789',
          cajero: 'María Alejandra Pineda',
          fecha: '27/02/2025',
          nombreCliente: 'Juan Pérez López',
          montoAprobado: 1200000,
          estado: 'Aprobada',
          codigoEntrega: 'A-12345'
        },
        {
          _id: '2',
          cedula: '987654321',
          cajero: 'Oscar David Madrigal',
          fecha: '28/02/2025',
          nombreCliente: 'Ana María Gómez',
          montoAprobado: 850000,
          estado: 'Negada',
          codigoEntrega: 'A-54321'
        },
        {
          _id: '3',
          cedula: '456789123',
          cajero: 'María Alejandra Pineda',
          fecha: '01/03/2025',
          nombreCliente: 'Carlos Rodríguez',
          montoAprobado: 2000000,
          estado: 'Aprobada',
          codigoEntrega: 'A-67890'
        }
      ];
      
      // Sucursales
      const sucursalesData = [
        { _id: '1', nombre: 'DIGITAL CENTER X' },
        { _id: '2', nombre: 'SAJI TIENDA' }
      ];
      
      // Comerciales
      const comercialesData = [
        { _id: '101', nombre: 'María Alejandra Pineda' },
        { _id: '102', nombre: 'Oscar David Madrigal' }
      ];
      
      setOperaciones(operacionesData);
      setSucursales(sucursalesData);
      setComerciales(comercialesData);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar datos:', error);
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const filtrarOperaciones = () => {
    // En una implementación real, esto podría ser una llamada a la API con los filtros
    // Por ahora, simulamos el filtrado en el frontend
    return operaciones.filter(op => {
      // Filtro por sucursal
      if (filtros.sucursal !== 'Todos' && op.sucursal !== filtros.sucursal) return false;
      
      // Filtro por comercial/cajero
      if (filtros.comercial !== 'Todos' && op.cajero !== filtros.comercial) return false;
      
      // Filtro por estado
      if (filtros.estado !== 'Todos' && op.estado !== filtros.estado) return false;
      
      // Filtro por búsqueda
      if (filtros.buscar && !op.nombreCliente.toLowerCase().includes(filtros.buscar.toLowerCase()) &&
          !op.cedula.includes(filtros.buscar) &&
          !op.codigoEntrega.includes(filtros.buscar)) {
        return false;
      }
      
      return true;
    });
  };

  const operacionesFiltradas = filtrarOperaciones();

  return (
    <div className="container-fluid">
      <h2 className="h3 mb-4">Operaciones</h2>
      
      <Card className="shadow-sm mb-4">
        <Card.Body>
          <Row className="g-3">
            <Col md={3}>
              <Form.Group>
                <Form.Label>Sucursal</Form.Label>
                <Form.Select
                  name="sucursal"
                  value={filtros.sucursal}
                  onChange={handleFiltroChange}
                >
                  <option value="Todos">Todos</option>
                  {sucursales.map(sucursal => (
                    <option key={sucursal._id} value={sucursal.nombre}>
                      {sucursal.nombre}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Comercial</Form.Label>
                <Form.Select
                  name="comercial"
                  value={filtros.comercial}
                  onChange={handleFiltroChange}
                >
                  <option value="Todos">Todos</option>
                  {comerciales.map(comercial => (
                    <option key={comercial._id} value={comercial.nombre}>
                      {comercial.nombre}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Fecha desde - hasta</Form.Label>
                <div className="d-flex">
                  <Form.Control
                    type="text"
                    className="me-2"
                    name="fechaInicio"
                    value={filtros.fechaInicio}
                    onChange={handleFiltroChange}
                  />
                  <Form.Control
                    type="text"
                    name="fechaFin"
                    value={filtros.fechaFin}
                    onChange={handleFiltroChange}
                  />
                </div>
              </Form.Group>
            </Col>
            
            <Col md={3}>
              <Form.Group>
                <Form.Label>Buscar</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Buscar..."
                  name="buscar"
                  value={filtros.buscar}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>
      
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando operaciones...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Cédula</th>
                    <th>Cajero</th>
                    <th>Fecha</th>
                    <th>Nombre cliente</th>
                    <th>Monto aprobado</th>
                    <th>Estado</th>
                    <th>Código entrega</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {operacionesFiltradas.length > 0 ? (
                    operacionesFiltradas.map((operacion) => (
                      <tr key={operacion._id}>
                        <td>{operacion.cedula}</td>
                        <td>{operacion.cajero}</td>
                        <td>{operacion.fecha}</td>
                        <td>{operacion.nombreCliente}</td>
                        <td>{formatCurrency(operacion.montoAprobado)}</td>
                        <td>
                          <span className={`badge ${operacion.estado === 'Aprobada' ? 'bg-success' : 'bg-danger'}`}>
                            {operacion.estado}
                          </span>
                        </td>
                        <td>{operacion.codigoEntrega}</td>
                        <td>
                          <Button 
                            variant="outline-info" 
                            size="sm"
                            title="Ver detalles"
                          >
                            <i className="fas fa-eye"></i>
                          </Button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="8" className="text-center py-4">
                        No hay datos disponibles
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default Operaciones;
