import React, { useState, useEffect, useContext } from 'react';
import { Row, Col, Card, Form, Button, Table, Alert, InputGroup, Modal } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCalculator,
  faDollarSign,
  faCalendarAlt,  // 'faCalendarAlt' is defined but never used.
  faCheck,        // 'faCheck' is defined but never used.
  faCircleCheck
} from '@fortawesome/free-solid-svg-icons';
import Layout from '../components/layout/Layout';
import { simularCredito, validarCliente, crearCredito } from '../services/apiService';
import { OfflineContext } from '../context/OfflineContext';
import { AuthContext } from '../context/AuthContext';
import { toast } from 'react-toastify';

const SimuladorPage = () => {
  // Estado para simulación
  const [monto, setMonto] = useState('');
  const [plazo, setPlazo] = useState(3);
  const [simulacion, setSimulacion] = useState(null);
  const [loading, setLoading] = useState(false);

  // Estado para validación de cliente
  const [showValidacion, setShowValidacion] = useState(false);
  const [cedula, setCedula] = useState('');
  const [telefono, setTelefono] = useState('');
  
  // Estado para datos del cliente obtenidos de Metamap
  const [clienteVerificado, setClienteVerificado] = useState(false);
  const [datosCliente, setDatosCliente] = useState({
    nombre: '',
    apellido: '',
    email: '',
    direccion: ''
  });
  
  // Estado para la solicitud de crédito
  const [showSolicitud, setShowSolicitud] = useState(false);
  const [solicitudEnviada, setSolicitudEnviada] = useState(false);
  
  // Datos de verificación de Metamap
  const [identityId, setIdentityId] = useState(null);
  const [verificationId, setVerificationId] = useState(null);
  
  // Contextos
  const { offline } = useContext(OfflineContext);
  const { user } = useContext(AuthContext);

  // Simular crédito
  const handleSimular = async () => {
    if (!monto) {
      toast.error('Por favor ingrese un monto');
      return;
    }

    setLoading(true);
    try {
      const result = await simularCredito(monto, plazo);
      setSimulacion(result);
    } catch (error) {
      console.error('Error al simular:', error);
      toast.error('Ocurrió un error al simular el crédito');
    } finally {
      setLoading(false);
    }
  };

  // Abrir modal de validación
  const handleSolicitar = () => {
    setShowValidacion(true);
  };

  // Validar cliente
  const handleValidarCliente = () => {
    if (!cedula || !telefono) {
      toast.error('Por favor complete todos los campos');
      return;
    }
    
    // No cerramos el modal de validación aquí, esperamos a que Metamap complete la verificación
  };
  
  // Función para manejar eventos de Metamap
  useEffect(() => {
    // Esta función se ejecuta después de que el script de Metamap se carga
    // y el componente metamap-button está disponible en el DOM
    const setupMetamapEvents = () => {
      const metamapButton = document.querySelector('metamap-button');
      
      if (metamapButton) {
        // Evento cuando el usuario inicia la verificación
        metamapButton.addEventListener('metamap:userStartedSdk', (event) => {
          const { detail } = event;
          console.log('Verificación iniciada:', detail);
          setIdentityId(detail.identityId);
          setVerificationId(detail.verificationId);
        });
        
        // Evento cuando el usuario completa la verificación
        metamapButton.addEventListener('metamap:userFinishedSdk', async (event) => {
          const { detail } = event;
          console.log('Verificación completada:', detail);
          setIdentityId(detail.identityId);
          setVerificationId(detail.verificationId);
          
          // Simular obtención de datos del cliente (en producción, harías una llamada a tu API)
          // que consultaría los datos de la verificación usando el identityId o verificationId
          const datosObtenidos = {
            nombre: "Juan Carlos",
            apellido: "Pérez Gómez",
            email: "juancarlos@example.com",
            direccion: "Calle Principal 123, Ciudad"
          };
          
          setDatosCliente(datosObtenidos);
          setClienteVerificado(true);
          setShowValidacion(false);
          setShowSolicitud(true);
          
          toast.success('Verificación completada exitosamente');
        });
        
        // Evento cuando el usuario sale del proceso de verificación
        metamapButton.addEventListener('metamap:exitedSdk', (event) => {
          console.log('Usuario salió de la verificación:', event.detail);
          toast.warning('Verificación cancelada');
        });
      }
    };
    
    // Verificar si el script de Metamap ya está cargado
    if (window.MetamapVerification) {
      setupMetamapEvents();
    } else {
      // Si no está cargado, esperar a que se cargue
      const script = document.createElement('script');
      script.src = "https://web-button.metamap.com/button.js";
      script.async = true;
      script.onload = setupMetamapEvents;
      document.body.appendChild(script);
      
      // Limpiar el script al desmontar
      return () => {
        document.body.removeChild(script);
      };
    }
  }, []);
  
  // Enviar solicitud de crédito
  const handleEnviarSolicitud = async () => {
    if (!clienteVerificado) {
      toast.error('Es necesario verificar la identidad del cliente');
      return;
    }

    setLoading(true);
    try {
      const solicitudData = {
        monto,
        plazo,
        cedula,
        telefono,
        ...datosCliente,
        identityId,
        verificationId
      };
      
      await crearCredito(solicitudData);
      setSolicitudEnviada(true);
      toast.success('Solicitud enviada exitosamente');
      
      // Limpiar formulario después de enviar
      setTimeout(() => {
        setShowSolicitud(false);
        setMonto('');
        setPlazo(3);
        setSimulacion(null);
        setCedula('');
        setTelefono('');
        setDatosCliente({
          nombre: '',
          apellido: '',
          email: '',
          direccion: ''
        });
        setClienteVerificado(false);
        setSolicitudEnviada(false);
      }, 3000);
    } catch (error) {
      console.error('Error al enviar solicitud:', error);
      toast.error('Ocurrió un error al enviar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      <Row>
        <Col>
          <Card>
            <Card.Header as="h2">
              Simulador
            </Card.Header>
            <Card.Body>
              <Form>
                <Form.Group as={Row} className="mb-3">
                  <Form.Label column sm={4}>Monto a financiar</Form.Label>
                  <Col sm={8}>
                    <InputGroup>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faDollarSign} />
                      </InputGroup.Text>
                      <Form.Control
                        type="number"
                        value={monto}
                        onChange={(e) => setMonto(e.target.value)}
                        placeholder="Ingrese monto"
                      />
                    </InputGroup>
                  </Col>
                </Form.Group>

                <Form.Group as={Row} className="mb-3">
                  <Form.Label column sm={4}>Plazo (en cuotas)</Form.Label>
                  <Col sm={8}>
                    <div className="d-flex">
                      {[3, 6, 9, 12].map((p) => (
                        <Button
                          key={p}
                          variant={plazo === p ? "primary" : "outline-primary"}
                          className="me-2"
                          onClick={() => setPlazo(p)}
                        >
                          {p}
                        </Button>
                      ))}
                    </div>
                  </Col>
                </Form.Group>

                <div className="d-grid">
                  <Button
                    variant="primary"
                    size="lg"
                    onClick={handleSimular}
                    disabled={loading || !monto}
                  >
                    <FontAwesomeIcon icon={faCalculator} className="me-2" />
                    Calcular
                  </Button>
                </div>
              </Form>

              {simulacion && (
                <div className="mt-4">
                  <div className="p-3 bg-light border rounded">
                    <h4 className="text-center">Valor de cuota:</h4>
                    <h2 className="text-center text-primary">$ {simulacion.valorCuota.toLocaleString()}</h2>
                  </div>
                  
                  <div className="d-grid mt-3">
                    <Button
                      variant="success"
                      size="lg"
                      onClick={handleSolicitar}
                      disabled={loading}
                    >
                      Solicitar
                    </Button>
                  </div>
                </div>
              )}

              <div className="mt-4">
                <Alert variant="info">
                  <Alert.Heading>Información importante</Alert.Heading>
                  <p>Esta simulación es solo informativa y los valores pueden variar según la evaluación del cliente. La tasa de interés aplicada es del 2.5% mensual.</p>
                </Alert>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Modal de Validación de Cliente */}
      <Modal show={showValidacion} onHide={() => setShowValidacion(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Validación de Cliente</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Número de Cédula</Form.Label>
              <Form.Control
                type="text"
                value={cedula}
                onChange={(e) => setCedula(e.target.value)}
                placeholder="Ingrese número de cédula"
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Teléfono</Form.Label>
              <Form.Control
                type="text"
                value={telefono}
                onChange={(e) => setTelefono(e.target.value)}
                placeholder="Ingrese número de teléfono"
              />
            </Form.Group>
            
            <div className="text-center mt-4">
              <p>Para continuar con la solicitud, se requiere verificar su identidad.</p>
              
              {/* Componente Metamap Button */}
              <metamap-button
                clientid="67e844f12f057303ed9e7b54"
                flowid="67e844f155e2997603974699"
                metadata={JSON.stringify({
                  cedula: cedula,
                  telefono: telefono,
                  source: "simulador_creditos"
                })}
              ></metamap-button>
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowValidacion(false)}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleValidarCliente}>
            Continuar
          </Button>
        </Modal.Footer>
      </Modal>
      
      {/* Modal de Solicitud de Crédito con los datos verificados */}
      <Modal show={showSolicitud} onHide={() => setShowSolicitud(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Solicitud de Crédito</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {clienteVerificado && (
            <>
              <Alert variant="success" className="mb-4">
                <FontAwesomeIcon icon={faCircleCheck} className="me-2" />
                Identidad verificada exitosamente
              </Alert>
              
              <h5>Datos del Cliente:</h5>
              <Form>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Cédula</Form.Label>
                      <Form.Control value={cedula} readOnly />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Teléfono</Form.Label>
                      <Form.Control value={telefono} readOnly />
                    </Form.Group>
                  </Col>
                </Row>
                
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Nombre</Form.Label>
                      <Form.Control value={datosCliente.nombre} readOnly />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Apellido</Form.Label>
                      <Form.Control value={datosCliente.apellido} readOnly />
                    </Form.Group>
                  </Col>
                </Row>
                
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control value={datosCliente.email} readOnly />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Dirección</Form.Label>
                  <Form.Control value={datosCliente.direccion} readOnly />
                </Form.Group>
              </Form>
              
              <h5 className="mt-4">Resumen del crédito:</h5>
              <Table striped bordered className="mt-2">
                <tbody>
                  <tr>
                    <td>Monto:</td>
                    <td className="text-end">$ {parseInt(monto).toLocaleString()}</td>
                  </tr>
                  <tr>
                    <td>Plazo:</td>
                    <td className="text-end">{plazo} meses</td>
                  </tr>
                  <tr>
                    <td>Valor cuota:</td>
                    <td className="text-end">$ {simulacion?.valorCuota.toLocaleString()}</td>
                  </tr>
                </tbody>
              </Table>
            </>
          )}
          
          {solicitudEnviada && (
            <Alert variant="success" className="mt-3">
              ¡Solicitud enviada exitosamente! Se procesará a la brevedad.
            </Alert>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowSolicitud(false)}>
            Cancelar
          </Button>
          {clienteVerificado && !solicitudEnviada && (
            <Button 
              variant="success" 
              onClick={handleEnviarSolicitud}
              disabled={loading}
            >
              Aprobar Crédito
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    </Layout>
  );
};

export default SimuladorPage;
