import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { Line, Bar, Doughnut } from 'react-chartjs-2';

// Registrar componentes de Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

export function Dashboard() {
  const [stats, setStats] = useState({
    totalProducts: 0,
    lowStockProducts: 0,
    totalSales: 0,
    totalRevenue: 0,
    customerStats: {
      totalCustomers: 0,
      totalDebt: 0,
      customersByStatus: {
        AL_DIA: 0,
        EN_MORA: 0,
        BLOQUEADO: 0
      }
    },
    monthlySales: [],
    topProducts: [],
    topDebtors: []
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      const response = await api.getStats();
      if (response.success) {
        setStats({
          ...stats,
          ...response,
          customerStats: {
            ...stats.customerStats,
            ...response.customerStats
          }
        });
      } else {
        setError('Error al cargar los datos');
      }
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar datos del dashboard:', error);
      setError('Error al cargar los datos del dashboard');
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(value);
  };

  // Configuración de gráficos
  const salesChartData = {
    labels: stats.monthlySales?.map(sale => sale.month) || [],
    datasets: [
      {
        label: 'Ventas Mensuales',
        data: stats.monthlySales?.map(sale => sale.total) || [],
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
        fill: false
      }
    ]
  };

  const customerStatusData = {
    labels: ['Al Día', 'En Mora', 'Bloqueados'],
    datasets: [{
      data: [
        stats.customerStats?.customersByStatus?.AL_DIA || 0,
        stats.customerStats?.customersByStatus?.EN_MORA || 0,
        stats.customerStats?.customersByStatus?.BLOQUEADO || 0
      ],
      backgroundColor: [
        'rgb(75, 192, 192)',
        'rgb(255, 205, 86)',
        'rgb(255, 99, 132)'
      ]
    }]
  };

  const topProductsData = {
    labels: stats.topProducts?.map(product => product.name) || [],
    datasets: [{
      label: 'Productos Más Vendidos',
      data: stats.topProducts?.map(product => product.totalSold) || [],
      backgroundColor: 'rgba(54, 162, 235, 0.5)'
    }]
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid mt-4">
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Cards de resumen */}
      <div className="row g-4 mb-4">
        <div className="col-md-3">
          <div className="card bg-primary text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Total Productos</h5>
              <h2 className="display-4">{stats.totalProducts}</h2>
              <p className="card-text">
                <small>{stats.lowStockProducts} productos con bajo stock</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-success text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Ventas Totales</h5>
              <h2 className="display-4">{stats.totalSales}</h2>
              <p className="card-text">
                <small>Ingresos: {formatCurrency(stats.totalRevenue)}</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-warning text-dark h-100">
            <div className="card-body">
              <h5 className="card-title">Total Clientes</h5>
              <h2 className="display-4">{stats?.customerStats?.totalCustomers || 0}</h2>
              <p className="card-text">
                <small>Deuda total: {formatCurrency(stats?.customerStats?.totalDebt || 0)}</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-info text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Cartera Vencida</h5>
              <h2 className="display-4">
                {(stats?.customerStats?.customersByStatus?.EN_MORA || 0) + 
                 (stats?.customerStats?.customersByStatus?.BLOQUEADO || 0)}
              </h2>
              <p className="card-text">
                <small>Clientes en mora o bloqueados</small>
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Gráficos */}
      <div className="row g-4">
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Ventas Mensuales</h5>
              <Line data={salesChartData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                  title: {
                    display: true,
                    text: 'Tendencia de Ventas'
                  }
                }
              }} />
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Estado de Clientes</h5>
              <Doughnut data={customerStatusData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'bottom'
                  }
                }
              }} />
            </div>
          </div>
        </div>
      </div>

      <div className="row g-4 mt-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Productos Más Vendidos</h5>
              <Bar data={topProductsData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    display: false
                  }
                }
              }} />
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Principales Deudores</h5>
              <div className="table-responsive">
                <table className="table table-sm">
                  <thead>
                    <tr>
                      <th>Cliente</th>
                      <th>Deuda</th>
                      <th>Estado</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.topDebtors.map((debtor, index) => (
                      <tr key={index}>
                        <td>{debtor.name}</td>
                        <td>{formatCurrency(debtor.debt)}</td>
                        <td>
                          <span className={`badge bg-${
                            debtor.status === 'AL_DIA' ? 'success' :
                            debtor.status === 'EN_MORA' ? 'warning' : 'danger'
                          }`}>
                            {debtor.status === 'AL_DIA' ? 'Al día' :
                             debtor.status === 'EN_MORA' ? 'En mora' : 'Bloqueado'}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
