// En tu componente Layout.js
import React, { useContext } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignOutAlt, faUser } from '@fortawesome/free-solid-svg-icons';

const Layout = ({ children }) => {
  const { userInfo, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="d-flex">
      <Navbar bg="light" expand="lg" className="border-bottom">
        <Container fluid>
          <Navbar.Brand as={Link} to="/dashboard">CREDITOS</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
            <Nav>
              {userInfo && (
                <div className="d-flex align-items-center">
                  <span className="me-3">
                    <FontAwesomeIcon icon={faUser} className="me-2" />
                    {userInfo.nombre} {userInfo.apellido}
                  </span>
                  <Button 
                    variant="outline-danger" 
                    size="sm" 
                    onClick={handleLogout}
                  >
                    <FontAwesomeIcon icon={faSignOutAlt} className="me-2" />
                    Cerrar Sesión
                  </Button>
                </div>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div className="d-flex flex-grow-1">
        {/* Sidebar */}
        <div className="bg-light border-end" style={{ width: "220px" }}>
          <Nav className="flex-column pt-3">
            <Nav.Link as={Link} to="/dashboard" className="px-3 py-2">Dashboard</Nav.Link>
            <Nav.Link as={Link} to="/simulador" className="px-3 py-2">Simulador</Nav.Link>
            {userInfo && (userInfo.tipo === 'Administrativo' || userInfo.tipo === 'Super Usuario') && (
              <>
                <Nav.Link as={Link} to="/operaciones" className="px-3 py-2">Operaciones</Nav.Link>
                <Nav.Link as={Link} to="/sucursales" className="px-3 py-2">Sucursales</Nav.Link>
                <Nav.Link as={Link} to="/liquidacion" className="px-3 py-2">Liquidación</Nav.Link>
                <Nav.Link as={Link} to="/clientes" className="px-3 py-2">Clientes</Nav.Link>
              </>
            )}
            {userInfo && (userInfo.tipo === 'Super Usuario' || userInfo.tipo === 'Administrativo') && (
              <Nav.Link as={Link} to="/usuarios" className="px-3 py-2">Usuarios</Nav.Link>
            )}
          </Nav>
        </div>

        {/* Content */}
        <div className="flex-grow-1 p-4">
          {children}
        </div>
      </div>
    </div>
  );
};

export default Layout;
