// src/components/Sucursales.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
// Eliminamos la importación de api que no se usa ya que usamos datos simulados
// import { api } from '../services/api';

const Sucursales = () => {
  const [sucursales, setSucursales] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [currentSucursal, setCurrentSucursal] = useState({
    commercialName: '',
    responsible: '',
    address: '',
    status: 'Activo'
  });
  const [modalMode, setModalMode] = useState('create'); // 'create' o 'edit'

  useEffect(() => {
    loadSucursales();
    loadEmpleados();
  }, []);

  const loadSucursales = async () => {
    try {
      setLoading(true);
      // Simulación de datos para demostración
      // En implementación real, esto vendría de una llamada API: const response = await api.getSucursales();
      const sucursalesData = [
        {
          _id: '1',
          commercialName: 'DIGITAL CENTER X',
          responsible: {
            _id: '101',
            name: 'OSCAR DAVID MADRIGAL FONNEGRA'
          },
          address: 'CL SANTANDER CON CR 52 A CENTRO',
          status: 'Activo'
        },
        {
          _id: '2',
          commercialName: 'SAJI TIENDA',
          responsible: {
            _id: '102',
            name: 'MARIA ALEJANDRA PINEDA ESPINAL'
          },
          address: 'CL 48 N°51-97 CLL NUEVA',
          status: 'Activo'
        }
      ];
      
      setSucursales(sucursalesData);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar sucursales:', error);
      setError('Error al cargar las sucursales');
      setLoading(false);
    }
  };

  const loadEmpleados = async () => {
    try {
      // Simulación de datos para demostración
      // En implementación real, esto vendría de una llamada API: const response = await api.getEmpleados();
      const empleadosData = [
        {
          _id: '101',
          name: 'OSCAR DAVID MADRIGAL FONNEGRA',
          document: '1032259508',
          role: 'GERENTE GENERAL',
          status: 'Activo'
        },
        {
          _id: '102',
          name: 'MARIA ALEJANDRA PINEDA ESPINAL',
          document: '1032252344',
          role: 'SOCIA',
          status: 'Activo'
        }
      ];
      
      setEmpleados(empleadosData);
    } catch (error) {
      console.error('Error al cargar empleados:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!currentSucursal.commercialName || !currentSucursal.responsible || !currentSucursal.address) {
      setError('Por favor complete todos los campos obligatorios');
      return;
    }
    
    try {
      if (modalMode === 'create') {
        // Simulación de creación
        // En implementación real: await api.createSucursal(currentSucursal);
        const newSucursal = {
          _id: Date.now().toString(),
          ...currentSucursal,
          responsible: empleados.find(emp => emp._id === currentSucursal.responsible) || { _id: currentSucursal.responsible, name: 'Empleado' },
          status: 'Activo'
        };
        
        setSucursales([...sucursales, newSucursal]);
        setSuccess('Sucursal registrada exitosamente');
      } else {
        // Simulación de actualización
        // En implementación real: await api.updateSucursal(currentSucursal._id, currentSucursal);
        const updatedSucursales = sucursales.map(sucursal => 
          sucursal._id === currentSucursal._id ? {
            ...currentSucursal,
            responsible: empleados.find(emp => emp._id === currentSucursal.responsible) || 
                        { _id: currentSucursal.responsible, name: 'Empleado' }
          } : sucursal
        );
        
        setSucursales(updatedSucursales);
        setSuccess('Sucursal actualizada exitosamente');
      }
      
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al guardar sucursal:', error);
      setError('Error al procesar la solicitud');
    }
  };

  const handleEdit = (sucursal) => {
    setCurrentSucursal({
      _id: sucursal._id,
      commercialName: sucursal.commercialName,
      responsible: sucursal.responsible._id,
      address: sucursal.address,
      status: sucursal.status
    });
    setModalMode('edit');
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Está seguro de eliminar esta sucursal?')) {
      try {
        // Simulación de eliminación
        // En implementación real: await api.deleteSucursal(id);
        const filteredSucursales = sucursales.filter(sucursal => sucursal._id !== id);
        setSucursales(filteredSucursales);
        setSuccess('Sucursal eliminada exitosamente');
      } catch (error) {
        console.error('Error al eliminar sucursal:', error);
        setError('Error al eliminar la sucursal');
      }
    }
  };

  const resetForm = () => {
    setCurrentSucursal({
      commercialName: '',
      responsible: '',
      address: '',
      status: 'Activo'
    });
    setError('');
  };

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="h3 mb-0">Sucursales</h2>
        <Button 
          variant="primary" 
          onClick={() => {
            resetForm();
            setModalMode('create');
            setShowModal(true);
          }}
          style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
        >
          Registrar Sucursal
        </Button>
      </div>
      
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando sucursales...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Nombre Comercial</th>
                    <th>Responsable</th>
                    <th>Dirección</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {sucursales.length > 0 ? (
                    sucursales.map((sucursal) => (
                      <tr key={sucursal._id}>
                        <td>{sucursal.commercialName}</td>
                        <td>{sucursal.responsible.name}</td>
                        <td>{sucursal.address}</td>
                        <td>
                          <span className={`badge ${sucursal.status === 'Activo' ? 'bg-success' : 'bg-danger'}`}>
                            {sucursal.status}
                          </span>
                        </td>
                        <td>
                          <div className="btn-group">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => handleEdit(sucursal)}
                              title="Editar"
                            >
                              <i className="fas fa-edit"></i>
                            </Button>
                            <Button 
                              variant="outline-danger" 
                              size="sm"
                              onClick={() => handleDelete(sucursal._id)}
                              title="Eliminar"
                              className="ms-1"
                            >
                              <i className="fas fa-trash"></i>
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-4">
                        No hay sucursales registradas
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Creación/Edición */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{modalMode === 'create' ? 'Registrar Sucursal' : 'Editar Sucursal'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Nombre Comercial</Form.Label>
              <Form.Control
                type="text"
                value={currentSucursal.commercialName}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  commercialName: e.target.value
                })}
                required
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Responsable</Form.Label>
              <Form.Select
                value={currentSucursal.responsible}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  responsible: e.target.value
                })}
                required
              >
                <option value="">Seleccionar responsable</option>
                {empleados.map((empleado) => (
                  <option key={empleado._id} value={empleado._id}>
                    {empleado.name}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Dirección</Form.Label>
              <Form.Control
                type="text"
                value={currentSucursal.address}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  address: e.target.value
                })}
                required
              />
            </Form.Group>
            
            {modalMode === 'edit' && (
              <Form.Group className="mb-3">
                <Form.Label>Estado</Form.Label>
                <Form.Select
                  value={currentSucursal.status}
                  onChange={(e) => setCurrentSucursal({
                    ...currentSucursal,
                    status: e.target.value
                  })}
                >
                  <option value="Activo">Activo</option>
                  <option value="Inactivo">Inactivo</option>
                </Form.Select>
              </Form.Group>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2"
                onClick={() => setShowModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                variant="primary"
                style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
              >
                {modalMode === 'create' ? 'Registrar' : 'Actualizar'}
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Sucursales;
