import React, { useState, useEffect } from 'react';
import { Table, Button, Container, Row, Col, Form, Badge } from 'react-bootstrap';
import { toast } from 'react-toastify';
import api from '../services/api';
import Modal from './common/Modal';

const Suppliers = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentSupplier, setCurrentSupplier] = useState(null);
  const [search, setSearch] = useState('');
  const [showInactive, setShowInactive] = useState(false);

  const initialFormData = {
    name: '',
    contactName: '',
    email: '',
    phone: '',
    address: '',
    taxId: '',
    paymentTerms: ''
  };

  const [formData, setFormData] = useState(initialFormData);

  const loadSuppliers = async () => {
    try {
      const response = await api.get(`/suppliers?search=${search}&isActive=${!showInactive}`);
      if (response.data.success) {
        setSuppliers(response.data.data);
      }
    } catch (error) {
      console.error('Error al cargar proveedores:', error);
      toast.error('Error al cargar los proveedores');
    }
  };

  useEffect(() => {
    loadSuppliers();
  }, [search, showInactive]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentSupplier) {
        await api.put(`/suppliers/${currentSupplier._id}`, formData);
        toast.success('Proveedor actualizado exitosamente');
      } else {
        await api.post('/suppliers', formData);
        toast.success('Proveedor creado exitosamente');
      }
      setShowModal(false);
      setFormData(initialFormData);
      loadSuppliers();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.response?.data?.message || 'Error al procesar la operaciÃ³n');
    }
  };

  const handleEdit = (supplier) => {
    setCurrentSupplier(supplier);
    setFormData({
      name: supplier.name,
      contactName: supplier.contactName,
      email: supplier.email,
      phone: supplier.phone,
      address: supplier.address,
      taxId: supplier.taxId,
      paymentTerms: supplier.paymentTerms
    });
    setShowModal(true);
  };

  const handleToggleStatus = async (supplier) => {
    try {
      const newStatus = !supplier.isActive;
      const response = await api.put(`/suppliers/${supplier._id}/toggle-status`, {
        isActive: newStatus
      });
      
      if (response.data.success) {
        toast.success(response.data.message);
        loadSuppliers();
      }
    } catch (error) {
      console.error('Error:', error);
      toast.error('Error al cambiar el estado del proveedor');
    }
  };

  return (
    <Container fluid>
      <Row className="mb-3">
        <Col>
          <h2>Proveedores</h2>
        </Col>
        <Col xs="auto">
          <Button variant="primary" onClick={() => {
            setCurrentSupplier(null);
            setFormData(initialFormData);
            setShowModal(true);
          }}>
            Nuevo Proveedor
          </Button>
        </Col>
      </Row>

      <Row className="mb-3">
        <Col md={4}>
          <Form.Control
            type="text"
            placeholder="Buscar proveedor..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </Col>
        <Col md={4}>
          <Form.Check
            type="switch"
            id="show-inactive"
            label="Mostrar proveedores inactivos"
            checked={showInactive}
            onChange={(e) => setShowInactive(e.target.checked)}
          />
        </Col>
      </Row>

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Contacto</th>
            <th>Email</th>
            <th>TelÃ©fono</th>
            <th>RUC/DNI</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier) => (
            <tr key={supplier._id}>
              <td>{supplier.name}</td>
              <td>{supplier.contactName}</td>
              <td>{supplier.email}</td>
              <td>{supplier.phone}</td>
              <td>{supplier.taxId}</td>
              <td>
                <Badge bg={supplier.isActive ? 'success' : 'danger'}>
                  {supplier.isActive ? 'Activo' : 'Inactivo'}
                </Badge>
              </td>
              <td>
                <Button
                  variant="primary"
                  size="sm"
                  className="me-2"
                  onClick={() => handleEdit(supplier)}
                >
                  Editar
                </Button>
                <Button
                  variant={supplier.isActive ? "danger" : "success"}
                  size="sm"
                  onClick={() => handleToggleStatus(supplier)}
                >
                  {supplier.isActive ? 'Desactivar' : 'Activar'}
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        title={currentSupplier ? 'Editar Proveedor' : 'Nuevo Proveedor'}
        onSubmit={handleSubmit}
      >
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Nombre</Form.Label>
            <Form.Control
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Nombre de Contacto</Form.Label>
            <Form.Control
              type="text"
              name="contactName"
              value={formData.contactName}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>TelÃ©fono</Form.Label>
            <Form.Control
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>DirecciÃ³n</Form.Label>
            <Form.Control
              type="text"
              name="address"
              value={formData.address}
              onChange={handleInputChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>RUC/DNI</Form.Label>
            <Form.Control
              type="text"
              name="taxId"
              value={formData.taxId}
              onChange={handleInputChange}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>TÃ©rminos de Pago</Form.Label>
            <Form.Control
              type="text"
              name="paymentTerms"
              value={formData.paymentTerms}
              onChange={handleInputChange}
            />
          </Form.Group>
        </Form>
      </Modal>
    </Container>
  );
};

export default Suppliers;
