import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import { Modal } from './common/Modal';
import { Button } from './common/Button';
import { Table } from './common/Table';

export function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    sortBy: 'name'
  });
  
  const [modalConfig, setModalConfig] = useState({
    show: false,
    type: null, // 'create', 'edit', 'payment'
    title: '',
    data: null
  });

  const [currentCustomer, setCurrentCustomer] = useState({
    name: '',
    document: '',
    creditLimit: '',
    totalDebt: 0,
    status: 'AL_DIA',
    lastPaymentDate: null
  });
  const [showModal, setShowModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [error, setError] = useState('');
  const [payment, setPayment] = useState({
    amount: '',
    date: new Date().toISOString().split('T')[0],
    paymentMethod: 'Efectivo',
    notes: ''
  });

  useEffect(() => {
    loadCustomers();
  }, [filters]);

  const loadCustomers = async () => {
    try {
      const response = await api.getCustomers(filters);
      setCustomers(response.data);
    } catch (error) {
      toast.error('Error al cargar la lista de clientes');
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (type, customer = null) => {
    const titles = {
      create: 'Nuevo Cliente',
      edit: 'Editar Cliente',
      payment: 'Registrar Pago'
    };

    setModalConfig({
      show: true,
      type,
      title: titles[type],
      data: customer || {
        name: '',
        document: '',
        creditLimit: 0,
        status: 'AL_DIA'
      }
    });
  };

  const handleCloseModal = () => {
    setModalConfig({
      show: false,
      type: null,
      title: '',
      data: null
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const customerData = {
        ...currentCustomer,
        creditLimit: Number(currentCustomer.creditLimit)
      };

      if (currentCustomer._id) {
        await api.updateCustomer(currentCustomer._id, customerData);
        toast.success('Cliente actualizado exitosamente');
      } else {
        await api.createCustomer(customerData);
        toast.success('Cliente creado exitosamente');
      }
      setShowModal(false);
      loadCustomers();
      resetForm();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al procesar la operación');
    }
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.registerCustomerPayment(currentCustomer._id, payment);
      toast.success('Pago registrado exitosamente');
      setShowPaymentModal(false);
      loadCustomers();
      resetForm();
    } catch (error) {
      toast.error('Error al registrar el pago');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este cliente?')) {
      try {
        await api.deleteCustomer(id);
        toast.success('Cliente eliminado exitosamente');
        loadCustomers();
      } catch (error) {
        toast.error('Error al eliminar el cliente');
      }
    }
  };

  const resetForm = () => {
    setCurrentCustomer({
      name: '',
      document: '',
      creditLimit: '',
      totalDebt: 0,
      status: 'AL_DIA',
      lastPaymentDate: null
    });
    setPayment({
      amount: '',
      date: new Date().toISOString().split('T')[0],
      paymentMethod: 'Efectivo',
      notes: ''
    });
    setError('');
  };

  const renderModalContent = () => {
    if (!modalConfig.show) return null;

    if (modalConfig.type === 'payment') {
      return (
        <form onSubmit={handlePaymentSubmit}>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Cliente</label>
              <input
                type="text"
                name="name"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={currentCustomer.name}
                disabled
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Deuda Actual</label>
              <input
                type="text"
                name="totalDebt"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={`$${currentCustomer.totalDebt?.toLocaleString()}`}
                disabled
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Monto a Pagar *</label>
              <input
                type="number"
                name="amount"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={payment.amount}
                onChange={(e) => setPayment({
                  ...payment,
                  amount: e.target.value
                })}
                required
                min="0"
                max={currentCustomer.totalDebt}
                step="0.01"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Método de Pago</label>
              <select
                name="paymentMethod"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={payment.paymentMethod}
                onChange={(e) => setPayment({
                  ...payment,
                  paymentMethod: e.target.value
                })}
              >
                <option value="Efectivo">Efectivo</option>
                <option value="Tarjeta">Tarjeta</option>
                <option value="Transferencia">Transferencia</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Fecha de Pago</label>
              <input
                type="date"
                name="date"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={payment.date}
                onChange={(e) => setPayment({
                  ...payment,
                  date: e.target.value
                })}
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Notas</label>
              <textarea
                name="notes"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                value={payment.notes}
                onChange={(e) => setPayment({
                  ...payment,
                  notes: e.target.value
                })}
                rows="3"
              ></textarea>
            </div>
          </div>
        </form>
      );
    }

    return (
      <form onSubmit={handleSubmit}>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Nombre</label>
            <input
              type="text"
              name="name"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              value={currentCustomer.name}
              onChange={(e) => setCurrentCustomer({
                ...currentCustomer,
                name: e.target.value
              })}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Documento</label>
            <input
              type="text"
              name="document"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              value={currentCustomer.document}
              onChange={(e) => setCurrentCustomer({
                ...currentCustomer,
                document: e.target.value
              })}
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Límite de Crédito</label>
            <input
              type="number"
              name="creditLimit"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              value={currentCustomer.creditLimit}
              onChange={(e) => setCurrentCustomer({
                ...currentCustomer,
                creditLimit: e.target.value
              })}
              required
              min="0"
              step="0.01"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Estado</label>
            <select
              name="status"
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              value={currentCustomer.status}
              onChange={(e) => setCurrentCustomer({
                ...currentCustomer,
                status: e.target.value
              })}
            >
              <option value="AL_DIA">Al Día</option>
              <option value="EN_MORA">En Mora</option>
              <option value="BLOQUEADO">Bloqueado</option>
            </select>
          </div>
        </div>
      </form>
    );
  };

  const columns = [
    { header: 'Nombre', accessor: 'name' },
    { header: 'Documento', accessor: 'document' },
    {
      header: 'Deuda Total',
      accessor: 'totalDebt',
      cell: (value) => new Intl.NumberFormat('es-PE', {
        style: 'currency',
        currency: 'PEN'
      }).format(value)
    },
    {
      header: 'Límite de Crédito',
      accessor: 'creditLimit',
      cell: (value) => new Intl.NumberFormat('es-PE', {
        style: 'currency',
        currency: 'PEN'
      }).format(value)
    },
    {
      header: 'Estado',
      accessor: 'status',
      cell: (value) => (
        <span className={`px-2 py-1 rounded text-white ${
          value === 'AL_DIA' ? 'bg-green-500' :
          value === 'EN_MORA' ? 'bg-yellow-500' :
          'bg-red-500'
        }`}>
          {value.replace('_', ' ')}
        </span>
      )
    },
    {
      header: 'Último Pago',
      accessor: 'lastPaymentDate',
      cell: (value) => value ? new Date(value).toLocaleDateString('es-ES') : 'No registrado'
    },
    {
      header: 'Acciones',
      accessor: 'id',
      cell: (_, row) => (
        <div className="flex space-x-2">
          <Button
            onClick={() => handleOpenModal('edit', row)}
            variant="icon"
            className="text-blue-500"
          >
            <i className="fas fa-edit" />
          </Button>
          <Button
            onClick={() => handleOpenModal('payment', row)}
            variant="icon"
            className="text-green-500"
          >
            <i className="fas fa-dollar-sign" />
          </Button>
        </div>
      )
    }
  ];

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Clientes</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          <i className="fas fa-plus me-2"></i>
          Nuevo Cliente
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Documento</th>
                  <th>Deuda Total</th>
                  <th>Límite de Crédito</th>
                  <th>Estado</th>
                  <th>Último Pago</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {customers.map(customer => (
                  <tr key={customer._id}>
                    <td>{customer.name}</td>
                    <td>{customer.document}</td>
                    <td>{new Intl.NumberFormat('es-PE', {
                      style: 'currency',
                      currency: 'PEN'
                    }).format(customer.totalDebt)}</td>
                    <td>{new Intl.NumberFormat('es-PE', {
                      style: 'currency',
                      currency: 'PEN'
                    }).format(customer.creditLimit)}</td>
                    <td>
                      <span className={`badge ${
                        customer.status === 'AL_DIA' ? 'bg-success' :
                        customer.status === 'EN_MORA' ? 'bg-warning' :
                        'bg-danger'
                      }`}>
                        {customer.status.replace('_', ' ')}
                      </span>
                    </td>
                    <td>
                      {customer.lastPaymentDate
                        ? new Date(customer.lastPaymentDate).toLocaleDateString('es-ES')
                        : 'No registrado'}
                    </td>
                    <td>
                      <div className="btn-group">
                        <button
                          className="btn btn-sm btn-outline-primary"
                          onClick={() => {
                            setCurrentCustomer(customer);
                            setShowModal(true);
                          }}
                        >
                          <i className="fas fa-edit"></i>
                        </button>
                        <button
                          className="btn btn-sm btn-outline-success"
                          onClick={() => {
                            setCurrentCustomer(customer);
                            setShowPaymentModal(true);
                          }}
                        >
                          <i className="fas fa-dollar-sign"></i>
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleDelete(customer._id)}
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <Modal
        show={modalConfig.show}
        title={modalConfig.title}
        onClose={handleCloseModal}
        onConfirm={() => document.querySelector('form').requestSubmit()}
      >
        {renderModalContent()}
      </Modal>
    </div>
  );
}

export default Customers;
