import React, { useState, useEffect, useCallback } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import { Modal } from './common/Modal';
import { Button } from './common/Button';
import { Table } from './common/Table';

export function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    sortBy: 'name'
  });
  
  const [modalConfig, setModalConfig] = useState({
    show: false,
    type: null, // 'create', 'edit', 'payment'
    title: '',
    data: null
  });

  const [currentCustomer, setCurrentCustomer] = useState({
    name: '',
    document: '',
    creditLimit: '',
    totalDebt: 0,
    status: 'AL_DIA',
    lastPaymentDate: null
  });

  const [payment, setPayment] = useState({
    amount: '',
    date: new Date().toISOString().split('T')[0],
    paymentMethod: 'Efectivo',
    notes: ''
  });

  const loadCustomers = useCallback(async () => {
    try {
      const response = await api.getCustomers(filters);
      setCustomers(response.data);
    } catch (error) {
      toast.error('Error al cargar la lista de clientes');
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    loadCustomers();
  }, [loadCustomers]);

  const handleOpenModal = (type, customer = null) => {
    const titles = {
      create: 'Nuevo Cliente',
      edit: 'Editar Cliente',
      payment: 'Registrar Pago'
    };

    if (customer) {
      setCurrentCustomer(customer);
    } else {
      resetForm();
    }

    setModalConfig({
      show: true,
      type,
      title: titles[type],
      data: customer
    });
  };

  const handleCloseModal = () => {
    setModalConfig({
      show: false,
      type: null,
      title: '',
      data: null
    });
    resetForm();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const customerData = {
        name: currentCustomer.name,
        document: currentCustomer.document,
        creditLimit: Number(currentCustomer.creditLimit),
        status: currentCustomer.status,
        totalDebt: 0
      };

      if (modalConfig.data?._id) {
        await api.updateCustomer(modalConfig.data._id, customerData);
        toast.success('Cliente actualizado exitosamente');
      } else {
        await api.createCustomer(customerData);
        toast.success('Cliente creado exitosamente');
      }
      handleCloseModal();
      loadCustomers();
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.response?.data?.message || 'Error al procesar la operación');
    }
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.registerCustomerPayment(currentCustomer._id, {
        ...payment,
        amount: Number(payment.amount)
      });
      toast.success('Pago registrado exitosamente');
      handleCloseModal();
      loadCustomers();
    } catch (error) {
      toast.error('Error al registrar el pago');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Está seguro de que desea eliminar este cliente?')) {
      try {
        await api.deleteCustomer(id);
        toast.success('Cliente eliminado exitosamente');
        loadCustomers();
      } catch (error) {
        toast.error('Error al eliminar el cliente');
      }
    }
  };

  const resetForm = () => {
    setCurrentCustomer({
      name: '',
      document: '',
      creditLimit: '',
      totalDebt: 0,
      status: 'AL_DIA',
      lastPaymentDate: null
    });
    setPayment({
      amount: '',
      date: new Date().toISOString().split('T')[0],
      paymentMethod: 'Efectivo',
      notes: ''
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(amount);
  };

  const renderModalContent = () => {
    if (!modalConfig.show) return null;

    if (modalConfig.type === 'payment') {
      return (
        <form onSubmit={handlePaymentSubmit}>
          <div className="mb-3">
            <label className="form-label">Cliente</label>
            <input
              type="text"
              className="form-control"
              value={currentCustomer.name}
              disabled
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Deuda Actual</label>
            <input
              type="text"
              className="form-control"
              value={formatCurrency(currentCustomer.totalDebt)}
              disabled
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Monto a Pagar *</label>
            <input
              type="number"
              className="form-control"
              value={payment.amount}
              onChange={(e) => setPayment({
                ...payment,
                amount: e.target.value
              })}
              required
              min="0"
              max={currentCustomer.totalDebt}
              step="0.01"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Método de Pago</label>
            <select
              className="form-select"
              value={payment.paymentMethod}
              onChange={(e) => setPayment({
                ...payment,
                paymentMethod: e.target.value
              })}
            >
              <option value="Efectivo">Efectivo</option>
              <option value="Tarjeta">Tarjeta</option>
              <option value="Transferencia">Transferencia</option>
            </select>
          </div>
          <div className="mb-3">
            <label className="form-label">Fecha de Pago</label>
            <input
              type="date"
              className="form-control"
              value={payment.date}
              onChange={(e) => setPayment({
                ...payment,
                date: e.target.value
              })}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Notas</label>
            <textarea
              className="form-control"
              value={payment.notes}
              onChange={(e) => setPayment({
                ...payment,
                notes: e.target.value
              })}
              rows="3"
            ></textarea>
          </div>
        </form>
      );
    }

    return (
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Nombre *</label>
          <input
            type="text"
            className="form-control"
            value={currentCustomer.name}
            onChange={(e) => setCurrentCustomer({
              ...currentCustomer,
              name: e.target.value
            })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Documento *</label>
          <input
            type="text"
            className="form-control"
            value={currentCustomer.document}
            onChange={(e) => setCurrentCustomer({
              ...currentCustomer,
              document: e.target.value
            })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Límite de Crédito *</label>
          <input
            type="number"
            className="form-control"
            value={currentCustomer.creditLimit}
            onChange={(e) => setCurrentCustomer({
              ...currentCustomer,
              creditLimit: e.target.value
            })}
            required
            min="0"
            step="0.01"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Estado</label>
          <select
            className="form-select"
            value={currentCustomer.status}
            onChange={(e) => setCurrentCustomer({
              ...currentCustomer,
              status: e.target.value
            })}
          >
            <option value="AL_DIA">Al Día</option>
            <option value="EN_MORA">En Mora</option>
            <option value="BLOQUEADO">Bloqueado</option>
          </select>
        </div>
      </form>
    );
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '60vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid p-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Clientes</h2>
        <button
          className="btn btn-primary"
          onClick={() => handleOpenModal('create')}
        >
          <i className="fas fa-plus me-2"></i>
          Nuevo Cliente
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <div className="row mb-3">
            <div className="col-md-4">
              <input
                type="text"
                className="form-control"
                placeholder="Buscar por nombre o documento..."
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
              />
            </div>
            <div className="col-md-4">
              <select
                className="form-select"
                value={filters.status}
                onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              >
                <option value="">Todos los estados</option>
                <option value="AL_DIA">Al Día</option>
                <option value="EN_MORA">En Mora</option>
                <option value="BLOQUEADO">Bloqueado</option>
              </select>
            </div>
            <div className="col-md-4">
              <select
                className="form-select"
                value={filters.sortBy}
                onChange={(e) => setFilters({ ...filters, sortBy: e.target.value })}
              >
                <option value="name">Ordenar por Nombre</option>
                <option value="totalDebt">Ordenar por Deuda</option>
                <option value="lastPaymentDate">Ordenar por Último Pago</option>
              </select>
            </div>
          </div>

          <div className="table-responsive">
            <table className="table table-hover align-middle">
              <thead className="table-light">
                <tr>
                  <th>Nombre</th>
                  <th>Documento</th>
                  <th>Deuda Total</th>
                  <th>Límite de Crédito</th>
                  <th>Estado</th>
                  <th>Último Pago</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {customers.map(customer => (
                  <tr key={customer._id}>
                    <td>{customer.name}</td>
                    <td>{customer.document}</td>
                    <td>{formatCurrency(customer.totalDebt)}</td>
                    <td>{formatCurrency(customer.creditLimit)}</td>
                    <td>
                      <span className={`badge ${
                        customer.status === 'AL_DIA' ? 'bg-success' :
                        customer.status === 'EN_MORA' ? 'bg-warning' :
                        'bg-danger'
                      }`}>
                        {customer.status.replace('_', ' ')}
                      </span>
                    </td>
                    <td>
                      {customer.lastPaymentDate
                        ? new Date(customer.lastPaymentDate).toLocaleDateString('es-ES')
                        : 'No registrado'}
                    </td>
                    <td>
                      <div className="btn-group">
                        <button
                          className="btn btn-sm btn-outline-primary"
                          onClick={() => handleOpenModal('edit', customer)}
                        >
                          <i className="fas fa-edit"></i>
                        </button>
                        <button
                          className="btn btn-sm btn-outline-success"
                          onClick={() => handleOpenModal('payment', customer)}
                        >
                          <i className="fas fa-dollar-sign"></i>
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleDelete(customer._id)}
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
                {customers.length === 0 && (
                  <tr>
                    <td colSpan="7" className="text-center py-4">
                      No hay clientes registrados
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className={`modal fade ${modalConfig.show ? 'show' : ''}`} 
           style={{ display: modalConfig.show ? 'block' : 'none' }}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">{modalConfig.title}</h5>
              <button
                type="button"
                className="btn-close"
                onClick={handleCloseModal}
              ></button>
            </div>
            <div className="modal-body">
              {renderModalContent()}
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleCloseModal}
              >
                Cancelar
              </button>
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => document.querySelector('form').requestSubmit()}
              >
                {modalConfig.type === 'payment' ? 'Registrar Pago' : 
                 modalConfig.type === 'edit' ? 'Actualizar' : 'Crear'}
              </button>
            </div>
          </div>
        </div>
      </div>
      {modalConfig.show && <div className="modal-backdrop fade show"></div>}
    </div>
  );
}

export default Customers;
