import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';

// Registrar componentes de Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

export function Dashboard() {
  const [stats, setStats] = useState({
    totalProducts: 0,
    lowStockProducts: 0,
    totalSales: 0,
    totalRevenue: 0,
    monthlySales: [],
    customerStats: {
      totalCustomers: 0,
      totalDebt: 0,
      customersByStatus: {
        AL_DIA: 0,
        EN_MORA: 0,
        BLOQUEADO: 0
      }
    },
    topDebtors: []
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const [salesStats, debtStats] = await Promise.all([
        api.getSalesStats(),
        api.getDebtStats()
      ]);

      setStats({
        ...salesStats.data,
        debtStats: debtStats.data
      });
    } catch (error) {
      console.error('Error al cargar estadísticas:', error);
      toast.error('Error al cargar las estadísticas');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(value);
  };

  const monthlySalesData = {
    labels: stats.monthlySales.map(sale => sale.month),
    datasets: [
      {
        label: 'Ventas Mensuales',
        data: stats.monthlySales.map(sale => sale.total),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  const customerStatusData = {
    labels: ['Al Día', 'En Mora', 'Bloqueados'],
    datasets: [
      {
        data: [
          stats.customerStats.customersByStatus.AL_DIA,
          stats.customerStats.customersByStatus.EN_MORA,
          stats.customerStats.customersByStatus.BLOQUEADO
        ],
        backgroundColor: [
          'rgb(75, 192, 192)',
          'rgb(255, 205, 86)',
          'rgb(255, 99, 132)'
        ]
      }
    ]
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid mt-4">
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Cards de resumen */}
      <div className="row g-4 mb-4">
        <div className="col-md-3">
          <div className="card bg-primary text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Total Productos</h5>
              <h2 className="display-4">{stats.totalProducts}</h2>
              <p className="card-text">
                <small>{stats.lowStockProducts} productos con bajo stock</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-success text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Ventas Totales</h5>
              <h2 className="display-4">{stats.totalSales}</h2>
              <p className="card-text">
                <small>Ingresos: {formatCurrency(stats.totalRevenue)}</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-warning text-dark h-100">
            <div className="card-body">
              <h5 className="card-title">Total Clientes</h5>
              <h2 className="display-4">{stats?.customerStats?.totalCustomers || 0}</h2>
              <p className="card-text">
                <small>Deuda total: {formatCurrency(stats?.customerStats?.totalDebt || 0)}</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-info text-white h-100">
            <div className="card-body">
              <h5 className="card-title">Cartera Vencida</h5>
              <h2 className="display-4">
                {(stats?.customerStats?.customersByStatus?.EN_MORA || 0) + 
                 (stats?.customerStats?.customersByStatus?.BLOQUEADO || 0)}
              </h2>
              <p className="card-text">
                <small>Clientes en mora o bloqueados</small>
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Gráficos */}
      <div className="row g-4">
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Ventas Mensuales</h5>
              <Line data={monthlySalesData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'top',
                  },
                  title: {
                    display: true,
                    text: 'Tendencia de Ventas'
                  }
                }
              }} />
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Estado de Clientes</h5>
              <Pie data={customerStatusData} options={{
                responsive: true,
                plugins: {
                  legend: {
                    position: 'bottom'
                  }
                }
              }} />
            </div>
          </div>
        </div>
      </div>

      <div className="row g-4 mt-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Top 5 Deudores</h5>
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Cliente</th>
                      <th>Documento</th>
                      <th>Deuda Total</th>
                      <th>Estado</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.topDebtors?.map(debtor => (
                      <tr key={debtor._id}>
                        <td>{debtor.name}</td>
                        <td>{debtor.document}</td>
                        <td>${debtor.totalDebt.toLocaleString()}</td>
                        <td>
                          <span className={`badge ${
                            debtor.status === 'AL_DIA' ? 'bg-success' :
                            debtor.status === 'EN_MORA' ? 'bg-warning' :
                            'bg-danger'
                          }`}>
                            {debtor.status.replace('_', ' ')}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
