// src/App.js
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/custom.css'; // Archivo personalizado de estilos

// Contextos para estados globales
import AuthState from './context/auth/AuthState';
import AlertState from './context/alert/AlertState';

// Utilidades
import setAuthToken from './utils/setAuthToken';

// Componentes
import Login from './components/Login';
import Layout from './components/Layout';
import Dashboard from './components/Dashboard';
import Cupos from './components/Cupos';
import Simulador from './components/Simulador';
import Operaciones from './components/Operaciones';
import Liquidacion from './components/Liquidacion';
import Sucursales from './components/Sucursales';
import Empleados from './components/Empleados';
import Aliados from './components/Aliados';
import PrivateRoute from './components/PrivateRoute';

// Cargar token del localStorage
if (localStorage.token) {
  setAuthToken(localStorage.token);
}

function App() {
  useEffect(() => {
    // Este efecto se ejecuta cuando el componente se monta
    // Puedes usar esto para verificar si hay un token válido al inicio
  }, []);

  return (
    <AuthState>
      <AlertState>
        <Router>
          <ToastContainer position="top-right" autoClose={3000} />
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route
              path="/"
              element={
                <PrivateRoute>
                  <Layout />
                </PrivateRoute>
              }
            >
              <Route index element={<Navigate to="/dashboard" replace />} />
              <Route path="dashboard" element={<Dashboard />} />
              <Route path="cupos" element={<Cupos />} />
              <Route path="simulador" element={<Simulador />} />
              <Route path="operaciones" element={<Operaciones />} />
              <Route path="liquidacion" element={<Liquidacion />} />
              <Route path="sucursales" element={<Sucursales />} />
              <Route path="empleados" element={<Empleados />} />
              <Route path="aliados" element={<Aliados />} />
            </Route>
          </Routes>
        </Router>
      </AlertState>
    </AuthState>
  );
}

export default App;
