import React, { useState, useEffect, useContext } from 'react';
import { 
  Card, 
  Table, 
  Button, 
  InputGroup, 
  Form, 
  Row, 
  Col,
  Pagination,
  Spinner,
  Badge,
  Modal,
  Alert
} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faSearch, 
  faUserPlus, 
  faEdit, 
  faTrashAlt,
  faSort,
  faFilter,
  faEye
} from '@fortawesome/free-solid-svg-icons';
import api from '../utils/axiosConfig';
import Layout from '../components/layout/Layout';
import { OfflineContext } from '../context/OfflineContext';
import ClienteForm from '../components/clientes/ClienteForm';
import ClienteDetalle from '../components/clientes/ClienteDetalle';

const ClientesPage = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);
  const [total, setTotal] = useState(0);
  const [limit, setLimit] = useState(10);
  const [sort, setSort] = useState('nombre');
  const [order, setOrder] = useState('asc');
  const [showForm, setShowForm] = useState(false);
  const [editingCliente, setEditingCliente] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [clienteToDelete, setClienteToDelete] = useState(null);
  const [showViewModal, setShowViewModal] = useState(false);
  const [clienteToView, setClienteToView] = useState(null);
  
  const { isOnline } = useContext(OfflineContext);
  
  // Cargar clientes
  const loadClientes = async () => {
    try {
      setLoading(true);
      
      if (!isOnline) {
        setError('No hay conexión a internet. Los datos no se pueden cargar.');
        setLoading(false);
        return;
      }
      
      const response = await api.get('/clientes', {
        params: { page, limit, search, sort, order }
      });
      
      const data = response.data;
      setClientes(data.clientes);
      setPage(data.page);
      setPages(data.pages);
      setTotal(data.total);
      setError(null);
    } catch (err) {
      console.error('Error al cargar clientes:', err);
      
      // Mostrar información detallada del error para depuración
      if (err.response) {
        console.error('Respuesta del servidor:', err.response.status, err.response.data);
      }
      
      setError('Error al cargar los clientes: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };
  
  // Cargar al montar y cuando cambien los filtros
  useEffect(() => {
    loadClientes();
  }, [page, limit, sort, order]);
  
  // Manejar búsqueda
  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1); // Resetear a primera página
    loadClientes();
  };
  
  // Manejar ordenamiento
  const handleSort = (column) => {
    if (sort === column) {
      setOrder(order === 'asc' ? 'desc' : 'asc');
    } else {
      setSort(column);
      setOrder('asc');
    }
  };
  
  // Manejar paginación
  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= pages) {
      setPage(newPage);
    }
  };
  
  // Manejar edición
  const handleEdit = (cliente) => {
    setEditingCliente(cliente);
    setShowForm(true);
  };
  
  // Manejar visualización
  const handleView = (cliente) => {
    setClienteToView(cliente);
    setShowViewModal(true);
  };
  
  // Confirmar eliminación
  const handleConfirmDelete = (cliente) => {
    setClienteToDelete(cliente);
    setShowDeleteModal(true);
  };
  
  // Eliminar cliente
  const handleDelete = async () => {
    try {
      if (!isOnline) {
        throw new Error('No hay conexión a internet. No se puede eliminar el cliente.');
      }
      
      await api.delete(`/clientes/${clienteToDelete._id}`);
      setShowDeleteModal(false);
      setClienteToDelete(null);
      loadClientes();
    } catch (err) {
      console.error('Error al eliminar cliente:', err);
      setError('Error al eliminar el cliente: ' + (err.response?.data?.message || err.message));
    }
  };
  
  // Manejar formulario
  const handleFormClose = (refreshData = false) => {
    setShowForm(false);
    setEditingCliente(null);
    if (refreshData) {
      loadClientes();
    }
  };
  
  // Formatear fecha
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    
    const date = new Date(dateString);
    return date.toLocaleDateString('es-CO', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };
  
  // Renderizar paginación
  const renderPagination = () => {
    // Si hay pocas páginas, mostrar todas
    if (pages <= 5) {
      return Array.from({ length: pages }, (_, i) => (
        <Pagination.Item 
          key={i + 1} 
          active={i + 1 === page}
          onClick={() => handlePageChange(i + 1)}
        >
          {i + 1}
        </Pagination.Item>
      ));
    }
    
    // Si hay muchas páginas, mostrar un subconjunto
    const items = [];
    
    // Primera página
    items.push(
      <Pagination.Item 
        key={1} 
        active={1 === page}
        onClick={() => handlePageChange(1)}
      >
        1
      </Pagination.Item>
    );
    
    // Ellipsis si la página actual está lejos del inicio
    if (page > 3) {
      items.push(<Pagination.Ellipsis key="ellipsis1" />);
    }
    
    // Páginas alrededor de la actual
    const startPage = Math.max(2, page - 1);
    const endPage = Math.min(pages - 1, page + 1);
    
    for (let i = startPage; i <= endPage; i++) {
      items.push(
        <Pagination.Item 
          key={i} 
          active={i === page}
          onClick={() => handlePageChange(i)}
        >
          {i}
        </Pagination.Item>
      );
    }
    
    // Ellipsis si la página actual está lejos del final
    if (page < pages - 2) {
      items.push(<Pagination.Ellipsis key="ellipsis2" />);
    }
    
    // Última página
    if (pages > 1) {
      items.push(
        <Pagination.Item 
          key={pages} 
          active={pages === page}
          onClick={() => handlePageChange(pages)}
        >
          {pages}
        </Pagination.Item>
      );
    }
    
    return items;
  };
  
  return (
    <Layout>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="mb-0">Clientes</h2>
        <Button 
          variant="primary" 
          onClick={() => setShowForm(true)}
        >
          <FontAwesomeIcon icon={faUserPlus} className="me-2" />
          Nuevo Cliente
        </Button>
      </div>

      {/* Filtros y búsqueda */}
      <Card className="mb-4 border-0 shadow-sm">
        <Card.Body>
          <Form onSubmit={handleSearch}>
            <Row>
              <Col md={6}>
                <InputGroup>
                  <InputGroup.Text>
                    <FontAwesomeIcon icon={faSearch} />
                  </InputGroup.Text>
                  <Form.Control
                    placeholder="Buscar por cédula, nombre, teléfono..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                  />
                  <Button type="submit" variant="primary">
                    Buscar
                  </Button>
                </InputGroup>
              </Col>
              <Col md={3}>
                <InputGroup>
                  <InputGroup.Text>
                    <FontAwesomeIcon icon={faFilter} />
                  </InputGroup.Text>
                  <Form.Select 
                    value={limit}
                    onChange={(e) => {
                      setLimit(parseInt(e.target.value));
                      setPage(1);
                    }}
                  >
                    <option value="10">10 por página</option>
                    <option value="25">25 por página</option>
                    <option value="50">50 por página</option>
                    <option value="100">100 por página</option>
                  </Form.Select>
                </InputGroup>
              </Col>
              <Col md={3} className="text-end">
                <Badge bg="info" className="me-2">
                  Total: {total} clientes
                </Badge>
                {!isOnline && (
                  <Badge bg="warning">
                    Modo sin conexión
                  </Badge>
                )}
              </Col>
            </Row>
          </Form>
        </Card.Body>
      </Card>

      {/* Tabla de clientes */}
      <Card className="border-0 shadow-sm">
        {error && (
          <Alert variant="danger" className="m-3">
            {error}
          </Alert>
        )}
        
        <Card.Body className="p-0">
          <Table responsive hover className="mb-0">
            <thead className="bg-light">
              <tr>
                <th 
                  onClick={() => handleSort('cedula')}
                  style={{cursor: 'pointer'}}
                >
                  Cédula
                  {sort === 'cedula' && (
                    <FontAwesomeIcon 
                      icon={faSort} 
                      className="ms-1" 
                      size="sm"
                      flip={order === 'desc' ? 'vertical' : undefined}
                    />
                  )}
                </th>
                <th 
                  onClick={() => handleSort('nombre')}
                  style={{cursor: 'pointer'}}
                >
                  Nombre
                  {sort === 'nombre' && (
                    <FontAwesomeIcon 
                      icon={faSort} 
                      className="ms-1" 
                      size="sm"
                      flip={order === 'desc' ? 'vertical' : undefined}
                    />
                  )}
                </th>
                <th 
                  onClick={() => handleSort('apellido')}
                  style={{cursor: 'pointer'}}
                >
                  Apellido
                  {sort === 'apellido' && (
                    <FontAwesomeIcon 
                      icon={faSort} 
                      className="ms-1" 
                      size="sm"
                      flip={order === 'desc' ? 'vertical' : undefined}
                    />
                  )}
                </th>
                <th>Teléfono</th>
                <th>Email</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan="6" className="text-center py-4">
                    <Spinner animation="border" variant="primary" />
                  </td>
                </tr>
              ) : clientes.length === 0 ? (
                <tr>
                  <td colSpan="6" className="text-center py-4">
                    No hay clientes para mostrar
                  </td>
                </tr>
              ) : (
                clientes.map((cliente) => (
                  <tr key={cliente._id}>
                    <td>{cliente.cedula}</td>
                    <td>{cliente.nombre}</td>
                    <td>{cliente.apellido}</td>
                    <td>{cliente.telefono}</td>
                    <td>{cliente.email}</td>
                    <td>
                      <Button
                        variant="outline-info"
                        size="sm"
                        className="me-1"
                        onClick={() => handleView(cliente)}
                      >
                        <FontAwesomeIcon icon={faEye} />
                      </Button>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        className="me-1"
                        onClick={() => handleEdit(cliente)}
                      >
                        <FontAwesomeIcon icon={faEdit} />
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleConfirmDelete(cliente)}
                      >
                        <FontAwesomeIcon icon={faTrashAlt} />
                      </Button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        </Card.Body>
        
        {/* Paginación */}
        {!loading && pages > 1 && (
          <Card.Footer className="bg-white border-0 py-3">
            <div className="d-flex justify-content-between align-items-center">
              <div>
                Mostrando página {page} de {pages}
              </div>
              <Pagination className="mb-0">
                <Pagination.Prev
                  onClick={() => handlePageChange(page - 1)}
                  disabled={page === 1}
                />
                {renderPagination()}
                <Pagination.Next
                  onClick={() => handlePageChange(page + 1)}
                  disabled={page === pages}
                />
              </Pagination>
            </div>
          </Card.Footer>
        )}
      </Card>

      {/* Modal de Formulario */}
      <Modal
        show={showForm}
        onHide={() => handleFormClose()}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {editingCliente ? 'Editar Cliente' : 'Nuevo Cliente'}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <ClienteForm 
            cliente={editingCliente}
            onClose={handleFormClose}
            isOnline={isOnline}
          />
        </Modal.Body>
      </Modal>

      {/* Modal de Eliminar */}
      <Modal
        show={showDeleteModal}
        onHide={() => setShowDeleteModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Eliminación</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {clienteToDelete && (
            <p>
              ¿Está seguro que desea eliminar al cliente <strong>{clienteToDelete.nombre} {clienteToDelete.apellido}</strong> con cédula <strong>{clienteToDelete.cedula}</strong>?
            </p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Eliminar
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal de Ver Cliente */}
      <Modal
        show={showViewModal}
        onHide={() => setShowViewModal(false)}
        centered
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>Detalles del Cliente</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {clienteToView && (
            <ClienteDetalle cliente={clienteToView} />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowViewModal(false)}>
            Cerrar
          </Button>
          <Button 
            variant="primary" 
            onClick={() => {
              setShowViewModal(false);
              handleEdit(clienteToView);
            }}
          >
            Editar
          </Button>
        </Modal.Footer>
      </Modal>
    </Layout>
  );
};

export default ClientesPage;
