// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { operacionService, sucursalService, userService } from '../services/api';

const Dashboard = () => {
  const [stats, setStats] = useState({
    dineroDisponible: 0,
    clientesFinanciados: 0,
    montoTotal: 0,
    operacionesHoy: [],
    aliados: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Cargar datos de operaciones
      const operacionesResponse = await operacionService.getAll();
      const operaciones = operacionesResponse.data;
      
      // Cargar datos de sucursales
      const sucursalesResponse = await sucursalService.getAll();
      const sucursales = sucursalesResponse.data;
      
      // Obtener la fecha de hoy
      const hoy = new Date();
      const inicioHoy = new Date(hoy.setHours(0, 0, 0, 0));
      
      // Filtrar operaciones de hoy
      const operacionesHoy = operaciones.filter(op => 
        new Date(op.fechaCierre) >= inicioHoy
      );
      
      // Calcular estadísticas
      const dineroDisponible = operaciones
        .filter(op => op.estado === 'Aprobada' && !op.liquidada)
        .reduce((sum, op) => sum + op.monto, 0);
        
      const clientesFinanciados = operacionesHoy
        .filter(op => op.estado === 'Aprobada')
        .length;
        
      const montoTotal = operacionesHoy
        .filter(op => op.estado === 'Aprobada')
        .reduce((sum, op) => sum + op.monto, 0);
      
      // Actualizar el estado
      setStats({
        dineroDisponible,
        clientesFinanciados,
        montoTotal,
        operacionesHoy,
        sucursales
      });
      
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar datos del dashboard:', error);
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
        <div className="spinner-border text-purple" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid p-0">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="h3 mb-0 text-gray-800">Dashboard</h1>
        <div>
          <Link to="/operaciones" className="btn btn-sm btn-outline-secondary me-2">
            <i className="fas fa-exchange-alt me-1"></i> Operaciones
          </Link>
          <Link to="/liquidacion" className="btn btn-sm btn-purple" style={{ backgroundColor: '#6a0dad', color: 'white' }}>
            <i className="fas fa-money-bill me-1"></i> Liquidación
          </Link>
        </div>
      </div>

      <div className="row">
        {/* Indicadores */}
        <div className="col-lg-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-body">
              <h5 className="card-title text-muted">Dinero disponible</h5>
              <h2 className="display-5 fw-bold">{formatCurrency(stats.dineroDisponible)}</h2>
              <p className="card-text mt-3">
                <Link to="/liquidacion" className="btn btn-sm btn-purple" style={{ backgroundColor: '#6a0dad', color: 'white' }}>
                  Ver detalles
                </Link>
              </p>
            </div>
          </div>
        </div>
        
        <div className="col-lg-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-body">
              <h5 className="card-title text-muted">Clientes financiados hoy</h5>
              <h2 className="display-5 fw-bold">{stats.clientesFinanciados}</h2>
              <p className="card-text mt-3">
                <Link to="/operaciones" className="btn btn-sm btn-purple" style={{ backgroundColor: '#6a0dad', color: 'white' }}>
                  Ver clientes
                </Link>
              </p>
            </div>
          </div>
        </div>
        
        <div className="col-lg-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-body">
              <h5 className="card-title text-muted">Total monto hoy</h5>
              <h2 className="display-5 fw-bold">{formatCurrency(stats.montoTotal)}</h2>
              <p className="card-text mt-3">
                <Link to="/operaciones" className="btn btn-sm btn-purple" style={{ backgroundColor: '#6a0dad', color: 'white' }}>
                  Ver detalles
                </Link>
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Tabla de Actividad */}
        <div className="col-lg-12 mb-4">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-white py-3">
              <h6 className="m-0 font-weight-bold text-purple" style={{ color: '#6a0dad' }}>Actividad</h6>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead>
                    <tr>
                      <th>Cédula</th>
                      <th>Cajero</th>
                      <th>Fecha</th>
                      <th>Nombre cliente</th>
                      <th>Monto aprobado</th>
                      <th>Estado</th>
                      <th>Código entrega</th>
                    </tr>
                  </thead>
                  <tbody></tbody>
                  <tbody>
                    {stats.operacionesHoy.length > 0 ? (
                      stats.operacionesHoy.map((operacion) => (
                        <tr key={operacion._id}>
                          <td>{operacion.cedula || operacion.documento}</td>
                          <td>{operacion.comercialId?.name || 'No asignado'}</td>
                          <td>{new Date(operacion.fechaCierre).toLocaleDateString()}</td>
                          <td>{operacion.cliente || operacion.nombreCliente}</td>
                          <td>{formatCurrency(operacion.monto || operacion.montoAprobado)}</td>
                          <td>
                            <span className={`badge ${
                              operacion.estado === 'Aprobada' ? 'bg-success' : 
                              operacion.estado === 'Rechazada' ? 'bg-danger' : 
                              operacion.estado === 'Liquidada' ? 'bg-info' : 'bg-warning'
                            }`}>
                              {operacion.estado}
                            </span>
                          </td>
                          <td>{operacion.codigoEntrega || '-'}</td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="7" className="text-center">No hay datos disponibles</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Sucursales */}
      <div className="row mt-2">
        <div className="col-lg-12 mb-4">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
              <h6 className="m-0 font-weight-bold text-purple" style={{ color: '#6a0dad' }}>Sucursales</h6>
              <Link to="/sucursales" className="btn btn-sm btn-purple" style={{ backgroundColor: '#6a0dad', color: 'white' }}>
                Registrar Sucursal
              </Link>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead>
                    <tr>
                      <th>Nombre Comercial</th>
                      <th>Responsable</th>
                      <th>Dirección</th>
                      <th>Estado</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.sucursales.length > 0 ? (
                      stats.sucursales.map((sucursal) => (
                        <tr key={sucursal._id}>
                          <td>{sucursal.nombreComercial}</td>
                          <td>{sucursal.responsableId?.name || 'No asignado'}</td>
                          <td>{sucursal.direccion}</td>
                          <td>
                            <span className={`badge ${sucursal.estado ? 'bg-success' : 'bg-danger'}`}>
                              {sucursal.estado ? 'Activo' : 'Inactivo'}
                            </span>
                          </td>
                          <td>
                            <div className="btn-group">
                              <Link to={`/sucursales/${sucursal._id}`} className="btn btn-sm btn-outline-primary" title="Editar">
                                <i className="fas fa-edit"></i>
                              </Link>
                            </div>
                          </td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="5" className="text-center">No hay sucursales registradas</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
