// src/components/Simulador.js
import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { api } from '../services/api';

const Simulador = () => {
  const [montoFinanciar, setMontoFinanciar] = useState('');
  const [plazo, setPlazo] = useState('3');
  const [valorCuota, setValorCuota] = useState('--');
  const [tasaInteres, setTasaInteres] = useState(0.018); // 1.8% mensual por defecto
  const [simulationResult, setSimulationResult] = useState(null);
  const [error, setError] = useState('');

  // Función para calcular el valor de la cuota
  const calcularCuota = (monto, plazoMeses, tasaMensual) => {
    if (!monto || !plazoMeses) return '--';
    
    // Fórmula para calcular cuota: Monto * (tasa * (1 + tasa)^plazo) / ((1 + tasa)^plazo - 1)
    const tasaDecimal = tasaMensual;
    const numerador = tasaDecimal * Math.pow(1 + tasaDecimal, plazoMeses);
    const denominador = Math.pow(1 + tasaDecimal, plazoMeses) - 1;
    const cuota = monto * (numerador / denominador);
    
    return cuota.toFixed(2);
  };

  useEffect(() => {
    if (montoFinanciar && plazo) {
      const cuota = calcularCuota(parseFloat(montoFinanciar), parseInt(plazo), tasaInteres);
      setValorCuota(cuota);
    } else {
      setValorCuota('--');
    }
  }, [montoFinanciar, plazo, tasaInteres]);

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!montoFinanciar || parseFloat(montoFinanciar) <= 0) {
      setError('Por favor ingrese un monto a financiar válido');
      return;
    }

    // Cálculo del plan de pagos
    const montoPrestamo = parseFloat(montoFinanciar);
    const plazoCuotas = parseInt(plazo);
    const cuotaMensual = parseFloat(valorCuota);
    const totalAPagar = cuotaMensual * plazoCuotas;
    const totalIntereses = totalAPagar - montoPrestamo;

    // Generar amortización mes a mes
    let saldoCapital = montoPrestamo;
    const amortizacion = [];

    for (let i = 1; i <= plazoCuotas; i++) {
      const interesMes = saldoCapital * tasaInteres;
      const abonoCapital = cuotaMensual - interesMes;
      saldoCapital -= abonoCapital;

      amortizacion.push({
        cuota: i,
        fechaPago: new Date(Date.now() + i * 30 * 24 * 60 * 60 * 1000).toLocaleDateString(),
        cuotaMensual: cuotaMensual,
        interes: interesMes,
        abonoCapital: abonoCapital,
        saldoCapital: saldoCapital > 0 ? saldoCapital : 0
      });
    }

    setSimulationResult({
      montoPrestamo,
      plazoCuotas,
      tasaInteres: tasaInteres * 100,
      cuotaMensual,
      totalAPagar,
      totalIntereses,
      amortizacion
    });

    setError('');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const resetSimulation = () => {
    setMontoFinanciar('');
    setPlazo('3');
    setValorCuota('--');
    setSimulationResult(null);
    setError('');
  };

  return (
    <div className="container-fluid">
      <h2 className="h3 mb-4">Simulador</h2>
      
      <div className="row">
        <div className="col-md-6">
          <Card className="shadow-sm mb-4">
            <Card.Body>
              <h5 className="mb-3">Monto a financiar</h5>
              {error && <Alert variant="danger">{error}</Alert>}
              
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Control
                    type="text"
                    value={montoFinanciar}
                    onChange={(e) => {
                      // Solo permitir números
                      const value = e.target.value.replace(/[^0-9]/g, '');
                      setMontoFinanciar(value);
                    }}
                    placeholder="Ingrese monto a financiar"
                    className="form-control-lg"
                  />
                </Form.Group>
                
                <h5 className="mb-3">Plazo (en cuotas)</h5>
                <div className="d-flex flex-wrap gap-2 mb-4">
                  {[3, 6, 9].map((option) => (
                    <div 
                      key={option} 
                      className={`plazo-option ${plazo === option.toString() ? 'selected' : ''}`}
                      onClick={() => setPlazo(option.toString())}
                      style={{
                        padding: '10px 20px',
                        border: plazo === option.toString() ? '2px solid #6a0dad' : '1px solid #ddd',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        backgroundColor: plazo === option.toString() ? '#f8f2ff' : 'white',
                        minWidth: '60px',
                        textAlign: 'center'
                      }}
                    >
                      {option}
                      {plazo === option.toString() && (
                        <span className="ms-2" style={{ color: '#6a0dad' }}>✓</span>
                      )}
                    </div>
                  ))}
                </div>

                <h5 className="mb-3">Valor de cuota:</h5>
                <div className="mb-4">
                  <div 
                    className="p-3 rounded"
                    style={{ backgroundColor: '#f8f2ff', border: '1px dashed #6a0dad' }}
                  >
                    <h3 className="mb-0" style={{ color: '#6a0dad' }}>
                      {valorCuota !== '--' ? formatCurrency(valorCuota) : '--'}
                    </h3>
                  </div>
                </div>
                
                <div className="d-grid">
                  <Button 
                    type="submit" 
                    variant="primary"
                    style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
                  >
                    Calcular
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </div>
        
        <div className="col-md-6">
          {simulationResult && (
            <Card className="shadow-sm">
              <Card.Body>
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <h5 className="mb-0">Resultado de simulación</h5>
                  <Button 
                    variant="outline-secondary" 
                    size="sm"
                    onClick={resetSimulation}
                  >
                    <i className="fas fa-redo me-1"></i> Nueva simulación
                  </Button>
                </div>
                
                <div className="row mb-4">
                  <div className="col-md-6">
                    <div className="mb-3">
                      <small className="text-muted d-block">Monto del préstamo</small>
                      <strong>{formatCurrency(simulationResult.montoPrestamo)}</strong>
                    </div>
                    <div className="mb-3">
                      <small className="text-muted d-block">Plazo</small>
                      <strong>{simulationResult.plazoCuotas} cuotas</strong>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="mb-3">
                      <small className="text-muted d-block">Tasa de interés</small>
                      <strong>{simulationResult.tasaInteres.toFixed(1)}% mensual</strong>
                    </div>
                    <div className="mb-3">
                      <small className="text-muted d-block">Cuota mensual</small>
                      <strong>{formatCurrency(simulationResult.cuotaMensual)}</strong>
                    </div>
                  </div>
                </div>
                
                <div className="d-flex justify-content-between mb-2 p-2" style={{ backgroundColor: '#f8f2ff' }}>
                  <div>
                    <small className="text-muted d-block">Total a pagar</small>
                    <strong>{formatCurrency(simulationResult.totalAPagar)}</strong>
                  </div>
                  <div>
                    <small className="text-muted d-block">Total intereses</small>
                    <strong>{formatCurrency(simulationResult.totalIntereses)}</strong>
                  </div>
                </div>
                
                <h6 className="mt-4 mb-3">Plan de pagos</h6>
                <div className="table-responsive">
                  <table className="table table-sm table-striped">
                    <thead>
                      <tr>
                        <th>Cuota</th>
                        <th>Fecha</th>
                        <th>Valor cuota</th>
                        <th>Interés</th>
                        <th>Abono a capital</th>
                        <th>Saldo capital</th>
                      </tr>
                    </thead>
                    <tbody>
                      {simulationResult.amortizacion.map((row) => (
                        <tr key={row.cuota}>
                          <td>{row.cuota}</td>
                          <td>{row.fechaPago}</td>
                          <td>{formatCurrency(row.cuotaMensual)}</td>
                          <td>{formatCurrency(row.interes)}</td>
                          <td>{formatCurrency(row.abonoCapital)}</td>
                          <td>{formatCurrency(row.saldoCapital)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                
                <div className="d-grid mt-3">
                  <Button 
                    variant="success" 
                    className="mt-3"
                    style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
                  >
                    <i className="fas fa-dollar-sign me-1"></i> Solicitar cupo
                  </Button>
                </div>
              </Card.Body>
            </Card>
          )}
          
          {!simulationResult && (
            <Card className="shadow-sm h-100">
              <Card.Body className="d-flex flex-column justify-content-center align-items-center">
                <div className="text-center" style={{ opacity: 0.6 }}>
                  <i className="fas fa-calculator fa-4x mb-3" style={{ color: '#6a0dad' }}></i>
                  <h5>Ingrese los datos para iniciar la simulación</h5>
                  <p className="text-muted">
                    Complete los campos en el formulario y haga clic en "Calcular" para ver el resultado.
                  </p>
                </div>
              </Card.Body>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
};

export default Simulador;
