// src/components/Empleados.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import { api } from '../services/api';

const Empleados = () => {
  const [empleados, setEmpleados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [currentEmpleado, setCurrentEmpleado] = useState({
    name: '',
    document: '',
    email: '',
    role: 'Comercial',
    status: 'Activo',
    password: '',
    confirmPassword: ''
  });
  const [modalMode, setModalMode] = useState('create'); // 'create' o 'edit'

  useEffect(() => {
    loadEmpleados();
  }, []);

  const loadEmpleados = async () => {
    try {
      setLoading(true);
      // Simulación de datos para demostración
      // En implementación real, esto vendría de una llamada API: const response = await api.getEmpleados();
      const empleadosData = [
        {
          _id: '101',
          name: 'MARIA ALEJANDRA PINEDA ESPINAL',
          document: '1032252344',
          email: 'maria.pineda@ejemplo.com',
          role: 'SOCIA',
          type: 'Comercial',
          status: 'Activo'
        },
        {
          _id: '102',
          name: 'OSCAR DAVID MADRIGAL FONNEGRA',
          document: '1032259508',
          email: 'oscar.madrigal@ejemplo.com',
          role: 'GERENTE GENERAL',
          type: 'Super Usuario',
          status: 'Activo'
        }
      ];
      
      setEmpleados(empleadosData);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar empleados:', error);
      setError('Error al cargar los empleados');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!currentEmpleado.name || !currentEmpleado.document || !currentEmpleado.email) {
      setError('Por favor complete todos los campos obligatorios');
      return;
    }
    
    if (modalMode === 'create' && currentEmpleado.password !== currentEmpleado.confirmPassword) {
      setError('Las contraseñas no coinciden');
      return;
    }
    
    try {
      if (modalMode === 'create') {
        // Simulación de creación
        // En implementación real: await api.createEmpleado(currentEmpleado);
        const newEmpleado = {
          _id: Date.now().toString(),
          ...currentEmpleado,
          status: 'Activo'
        };
        
        setEmpleados([...empleados, newEmpleado]);
        setSuccess('Empleado registrado exitosamente');
      } else {
        // Simulación de actualización
        // En implementación real: await api.updateEmpleado(currentEmpleado._id, currentEmpleado);
        const updatedEmpleados = empleados.map(empleado => 
          empleado._id === currentEmpleado._id ? currentEmpleado : empleado
        );
        
        setEmpleados(updatedEmpleados);
        setSuccess('Empleado actualizado exitosamente');
      }
      
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al guardar empleado:', error);
      setError('Error al procesar la solicitud');
    }
  };

  const handleEdit = (empleado) => {
    setCurrentEmpleado({
      _id: empleado._id,
      name: empleado.name,
      document: empleado.document,
      email: empleado.email,
      role: empleado.role,
      type: empleado.type,
      status: empleado.status,
      password: '',
      confirmPassword: ''
    });
    setModalMode('edit');
    setShowModal(true);
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      // Simulación de cambio de estado
      // En implementación real: await api.updateEmpleadoStatus(id, currentStatus === 'Activo' ? 'Inactivo' : 'Activo');
      const updatedEmpleados = empleados.map(empleado => 
        empleado._id === id ? {...empleado, status: currentStatus === 'Activo' ? 'Inactivo' : 'Activo'} : empleado
      );
      
      setEmpleados(updatedEmpleados);
      setSuccess(`Empleado ${currentStatus === 'Activo' ? 'desactivado' : 'activado'} exitosamente`);
    } catch (error) {
      console.error('Error al cambiar estado:', error);
      setError('Error al cambiar el estado del empleado');
    }
  };

  const resetForm = () => {
    setCurrentEmpleado({
      name: '',
      document: '',
      email: '',
      role: 'Comercial',
      type: 'Comercial',
      status: 'Activo',
      password: '',
      confirmPassword: ''
    });
    setError('');
  };

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="h3 mb-0">Empleados</h2>
        <Button 
          variant="primary" 
          onClick={() => {
            resetForm();
            setModalMode('create');
            setShowModal(true);
          }}
          style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
        >
          Registrar Empleado
        </Button>
      </div>
      
      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}
      
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando empleados...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Documento</th>
                    <th>Nombres y Apellido</th>
                    <th>Cargo</th>
                    <th>Tipo</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {empleados.length > 0 ? (
                    empleados.map((empleado) => (
                      <tr key={empleado._id}>
                        <td>{empleado.document}</td>
                        <td>{empleado.name}</td>
                        <td>{empleado.role}</td>
                        <td>{empleado.type}</td>
                        <td>
                          <span className={`badge ${empleado.status === 'Activo' ? 'bg-success' : 'bg-danger'}`}>
                            {empleado.status}
                          </span>
                        </td>
                        <td>
                          <div className="btn-group">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => handleEdit(empleado)}
                              title="Editar"
                            >
                              <i className="fas fa-edit"></i>
                            </Button>
                            <Button 
                              variant="outline-warning" 
                              size="sm"
                              onClick={() => handleToggleStatus(empleado._id, empleado.status)}
                              title={empleado.status === 'Activo' ? 'Desactivar' : 'Activar'}
                              className="ms-1"
                            >
                              <i className={`fas fa-${empleado.status === 'Activo' ? 'ban' : 'check'}`}></i>
                            </Button>
                            <Button 
                              variant="outline-info" 
                              size="sm"
                              title="Ver detalles"
                              className="ms-1"
                            >
                              <i className="fas fa-eye"></i>
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center py-4">
                        No hay empleados registrados
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Creación/Edición */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>{modalMode === 'create' ? 'Registrar Empleado' : 'Editar Empleado'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Nombres y Apellidos</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentEmpleado.name}
                    onChange={(e) => setCurrentEmpleado({
                      ...currentEmpleado,
                      name: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Documento</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentEmpleado.document}
                    onChange={(e) => setCurrentEmpleado({
                      ...currentEmpleado,
                      document: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={currentEmpleado.email}
                    onChange={(e) => setCurrentEmpleado({
                      ...currentEmpleado,
                      email: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Cargo</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentEmpleado.role}
                    onChange={(e) => setCurrentEmpleado({
                      ...currentEmpleado,
                      role: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Tipo</Form.Label>
                  <Form.Select
                    value={currentEmpleado.type}
                    onChange={(e) => setCurrentEmpleado({
                      ...currentEmpleado,
                      type: e.target.value
                    })}
                    required
                  >
                    <option value="Comercial">Comercial</option>
                    <option value="Super Usuario">Super Usuario</option>
                  </Form.Select>
                </Form.Group>
              </div>
              
              {modalMode === 'edit' && (
                <div className="col-md-6">
                  <Form.Group className="mb-3">
                    <Form.Label>Estado</Form.Label>
                    <Form.Select
                      value={currentEmpleado.status}
                      onChange={(e) => setCurrentEmpleado({
                        ...currentEmpleado,
                        status: e.target.value
                      })}
                    >
                      <option value="Activo">Activo</option>
                      <option value="Inactivo">Inactivo</option>
                    </Form.Select>
                  </Form.Group>
                </div>
              )}
            </div>
            
            {modalMode === 'create' && (
              <div className="row">
                <div className="col-md-6">
                  <Form.Group className="mb-3">
                    <Form.Label>Contraseña</Form.Label>
                    <Form.Control
                      type="password"
                      value={currentEmpleado.password}
                      onChange={(e) => setCurrentEmpleado({
                        ...currentEmpleado,
                        password: e.target.value
                      })}
                      required={modalMode === 'create'}
                    />
                  </Form.Group>
                </div>
                
                <div className="col-md-6">
                  <Form.Group className="mb-3">
                    <Form.Label>Confirmar Contraseña</Form.Label>
                    <Form.Control
                      type="password"
                      value={currentEmpleado.confirmPassword}
                      onChange={(e) => setCurrentEmpleado({
                        ...currentEmpleado,
                        confirmPassword: e.target.value
                      })}
                      required={modalMode === 'create'}
                    />
                  </Form.Group>
                </div>
              </div>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2"
                onClick={() => setShowModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                variant="primary"
                style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
              >
                {modalMode === 'create' ? 'Registrar' : 'Actualizar'}
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Empleados;
