// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Configurar interceptor para el token
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  error => Promise.reject(error)
);

// Interceptor para manejar errores de autenticación
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const api = {
  // Auth
  login: async (credentials) => {
    const { data } = await axios.post(`${API_URL}/users/login`, credentials);
    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data));
    return data;
  },

  // Products
  getProducts: () => axios.get(`${API_URL}/products`).then(res => res.data),
  createProduct: (product) => axios.post(`${API_URL}/products`, product).then(res => res.data),
  updateProduct: (id, product) => axios.put(`${API_URL}/products/${id}`, product).then(res => res.data),
  deleteProduct: (id) => axios.delete(`${API_URL}/products/${id}`).then(res => res.data),

  // Sales
  getSales: () => axios.get(`${API_URL}/sales`).then(res => res.data),
  createSale: (sale) => axios.post(`${API_URL}/sales`, sale).then(res => res.data),

  // Dashboard
  getStats: async () => {
    const [products, sales] = await Promise.all([
      axios.get(`${API_URL}/products/count`),
      axios.get(`${API_URL}/sales/stats`)
    ]);
    return {
      totalProducts: products.data.count,
      lowStockProducts: products.data.lowStockCount,
      totalSales: sales.data.totalSales,
      totalRevenue: sales.data.totalRevenue
    };
  }
};
