// src/components/Aliados.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Row, Col, Modal, Alert } from 'react-bootstrap';
import { aliadoService } from '../services/api';

const Aliados = () => {
  const [aliados, setAliados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [currentAliado, setCurrentAliado] = useState({
    nombre: '',
    contacto: '',
    documento: '',
    email: '',
    telefono: '',
    direccion: '',
    comision: 0,
    limiteCredito: 0,
    estado: true
  });
  const [modalMode, setModalMode] = useState('create'); // 'create' o 'edit'
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [filtros, setFiltros] = useState({
    estado: 'todos',
    buscar: ''
  });

  useEffect(() => {
    loadAliados();
  }, []);

  const loadAliados = async () => {
    try {
      setLoading(true);
      // Cargar desde API en lugar de localStorage
      const response = await aliadoService.getAll();
      setAliados(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar aliados:', error);
      setError('Error al cargar la lista de aliados');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!currentAliado.nombre || !currentAliado.documento) {
      setError('Por favor complete los campos obligatorios');
      return;
    }
    
    try {
      setLoading(true);
      if (modalMode === 'create') {
        // Crear nuevo aliado usando la API
        const response = await aliadoService.create(currentAliado);
        
        // Actualizar estado
        setAliados([...aliados, response.data]);
        setSuccess('Aliado registrado exitosamente');
      } else {
        // Actualizar aliado existente usando la API
        const response = await aliadoService.update(currentAliado._id, currentAliado);
        
        // Actualizar estado
        const updatedAliados = aliados.map(aliado => 
          aliado._id === currentAliado._id ? response.data : aliado
        );
        
        setAliados(updatedAliados);
        setSuccess('Aliado actualizado exitosamente');
      }
      
      // Limpiar formulario y cerrar modal
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al guardar aliado:', error);
      setError('Error al procesar la solicitud: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (aliado) => {
    setCurrentAliado(aliado);
    setModalMode('edit');
    setShowModal(true);
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      setLoading(true);
      // Actualizar usando la API
      await aliadoService.updateEstado(id, !currentStatus);
      
      // Actualizar estado
      const updatedAliados = aliados.map(aliado => 
        aliado._id === id ? {...aliado, estado: !aliado.estado} : aliado
      );
      
      setAliados(updatedAliados);
      setSuccess(`Aliado ${currentStatus ? 'desactivado' : 'activado'} exitosamente`);
    } catch (error) {
      console.error('Error al cambiar estado:', error);
      setError('Error al cambiar el estado del aliado: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setCurrentAliado({
      nombre: '',
      contacto: '',
      documento: '',
      email: '',
      telefono: '',
      direccion: '',
      comision: 0,
      limiteCredito: 0,
      estado: true
    });
    setError('');
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  // Filtrar aliados según los filtros aplicados
  const filtrarAliados = () => {
    return aliados.filter(aliado => {
      // Filtro por estado
      if (filtros.estado !== 'todos') {
        const estadoActivo = filtros.estado === 'activos';
        if (aliado.estado !== estadoActivo) return false;
      }
      
      // Filtro por búsqueda
      if (filtros.buscar && !aliado.nombre.toLowerCase().includes(filtros.buscar.toLowerCase()) &&
          !aliado.contacto?.toLowerCase().includes(filtros.buscar.toLowerCase()) &&
          !aliado.documento.includes(filtros.buscar)) {
        return false;
      }
      
      return true;
    });
  };

  const aliadosFiltrados = filtrarAliados();

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="h3 mb-0">Aliados</h2>
        <Button 
          variant="primary" 
          onClick={() => {
            resetForm();
            setModalMode('create');
            setShowModal(true);
          }}
          style={{ backgroundColor: '#2e7d32', borderColor: '#2e7d32' }}
        >
          Registrar Aliado
        </Button>
      </div>
      
      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}
      
      {/* Filtros */}
      <Card className="shadow-sm mb-4">
        <Card.Body>
          <Row className="g-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Estado</Form.Label>
                <Form.Select
                  name="estado"
                  value={filtros.estado}
                  onChange={handleFiltroChange}
                >
                  <option value="todos">Todos</option>
                  <option value="activos">Activos</option>
                  <option value="inactivos">Inactivos</option>
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={6}>
              <Form.Group>
                <Form.Label>Buscar</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Buscar por nombre, contacto o documento..."
                  name="buscar"
                  value={filtros.buscar}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>
      
      {/* Lista de aliados */}
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando aliados...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Nombre</th>
                    <th>Contacto</th>
                    <th>Documento</th>
                    <th>Email</th>
                    <th>Teléfono</th>
                    <th>Comisión</th>
                    <th>Límite de Crédito</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {aliadosFiltrados.length > 0 ? (
                    aliadosFiltrados.map((aliado) => (
                      <tr key={aliado._id}>
                        <td>{aliado.nombre}</td>
                        <td>{aliado.contacto}</td>
                        <td>{aliado.documento}</td>
                        <td>{aliado.email}</td>
                        <td>{aliado.telefono}</td>
                        <td>{aliado.comision}%</td>
                        <td>{formatCurrency(aliado.limiteCredito)}</td>
                        <td>
                          <span className={`badge ${aliado.estado ? 'bg-success' : 'bg-danger'}`}>
                            {aliado.estado ? 'Activo' : 'Inactivo'}
                          </span>
                        </td>
                        <td>
                          <div className="btn-group">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => handleEdit(aliado)}
                              title="Editar"
                            >
                              <i className="fas fa-edit"></i>
                            </Button>
                            <Button 
                              variant="outline-warning" 
                              size="sm"
                              onClick={() => handleToggleStatus(aliado._id, aliado.estado)}
                              title={aliado.estado ? 'Desactivar' : 'Activar'}
                              className="ms-1"
                            >
                              <i className={`fas fa-${aliado.estado ? 'ban' : 'check'}`}></i>
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="9" className="text-center py-4">
                        No hay aliados que coincidan con los filtros
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Creación/Edición */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>{modalMode === 'create' ? 'Registrar Aliado' : 'Editar Aliado'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Nombre Comercial *</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.nombre}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      nombre: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Documento (NIT) *</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.documento}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      documento: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Nombre de Contacto</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.contacto}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      contacto: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={currentAliado.email}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      email: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Teléfono</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.telefono}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      telefono: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Dirección</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.direccion}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      direccion: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Porcentaje de Comisión (%)</Form.Label>
                  <Form.Control
                    type="number"
                    value={currentAliado.comision}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      comision: parseFloat(e.target.value)
                    })}
                    min="0"
                    max="100"
                    step="0.1"
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Límite de Crédito</Form.Label>
                  <Form.Control
                    type="number"
                    value={currentAliado.limiteCredito}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      limiteCredito: parseFloat(e.target.value)
                    })}
                    min="0"
                  />
                </Form.Group>
              </div>
            </div>
            
            {modalMode === 'edit' && (
              <Form.Group className="mb-3">
                <Form.Check 
                  type="checkbox"
                  id="estado"
                  label="Aliado Activo"
                  checked={currentAliado.estado}
                  onChange={(e) => setCurrentAliado({
                    ...currentAliado,
                    estado: e.target.checked
                  })}
                />
              </Form.Group>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2"
                onClick={() => setShowModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                variant="success"
                style={{ backgroundColor: '#2e7d32', borderColor: '#2e7d32' }}
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Procesando...
                  </>
                ) : (
                  modalMode === 'create' ? 'Registrar' : 'Actualizar'
                )}
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Aliados;
