// src/components/Sucursales.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import { sucursalService, userService } from '../services/api';

const Sucursales = () => {
  const [sucursales, setSucursales] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [currentSucursal, setCurrentSucursal] = useState({
    nombreComercial: '',
    responsableId: '',
    direccion: '',
    estado: true
  });
  const [modalMode, setModalMode] = useState('create'); // 'create' o 'edit'

  useEffect(() => {
    loadSucursales();
    loadEmpleados();
  }, []);

  const loadSucursales = async () => {
    try {
      setLoading(true);
      // Cargar desde API
      const response = await sucursalService.getAll();
      setSucursales(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar sucursales:', error);
      setError('Error al cargar las sucursales: ' + (error.response?.data?.message || error.message));
      setLoading(false);
    }
  };

  const loadEmpleados = async () => {
    try {
      // Cargar desde API
      const response = await userService.getAll();
      setEmpleados(response.data);
    } catch (error) {
      console.error('Error al cargar empleados:', error);
      setError('Error al cargar empleados: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!currentSucursal.nombreComercial || !currentSucursal.responsableId || !currentSucursal.direccion) {
      setError('Por favor complete todos los campos obligatorios');
      return;
    }
    
    try {
      setLoading(true);
      if (modalMode === 'create') {
        // Crear nueva sucursal
        const response = await sucursalService.create(currentSucursal);
        
        // Actualizar estado
        setSucursales([...sucursales, response.data]);
        setSuccess('Sucursal registrada exitosamente');
      } else {
        // Actualizar sucursal existente
        const response = await sucursalService.update(currentSucursal._id, currentSucursal);
        
        // Actualizar estado
        const updatedSucursales = sucursales.map(sucursal => 
          sucursal._id === currentSucursal._id ? response.data : sucursal
        );
        
        setSucursales(updatedSucursales);
        setSuccess('Sucursal actualizada exitosamente');
      }
      
      // Limpiar formulario y cerrar modal
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al guardar sucursal:', error);
      setError('Error al procesar la solicitud: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (sucursal) => {
    setCurrentSucursal({
      _id: sucursal._id,
      nombreComercial: sucursal.nombreComercial,
      responsableId: sucursal.responsableId._id,
      direccion: sucursal.direccion,
      estado: sucursal.estado
    });
    setModalMode('edit');
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Está seguro de eliminar esta sucursal?')) {
      try {
        setLoading(true);
        // Eliminar usando API
        await sucursalService.delete(id);
        
        // Actualizar estado
        const filteredSucursales = sucursales.filter(sucursal => sucursal._id !== id);
        setSucursales(filteredSucursales);
        setSuccess('Sucursal eliminada exitosamente');
      } catch (error) {
        console.error('Error al eliminar sucursal:', error);
        setError('Error al eliminar la sucursal: ' + (error.response?.data?.message || error.message));
      } finally {
        setLoading(false);
      }
    }
  };

  const resetForm = () => {
    setCurrentSucursal({
      nombreComercial: '',
      responsableId: '',
      direccion: '',
      estado: true
    });
    setError('');
  };

  // Validar si hay empleados disponibles
  const noEmpleados = empleados.length === 0;

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="h3 mb-0">Sucursales</h2>
        <Button 
          variant="primary" 
          onClick={() => {
            resetForm();
            setModalMode('create');
            setShowModal(true);
          }}
          style={{ backgroundColor: '#2e7d32', borderColor: '#2e7d32' }}
          disabled={noEmpleados}
        >
          Registrar Sucursal
        </Button>
      </div>
      
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}
      
      {noEmpleados && (
        <Alert variant="warning">
          No hay empleados registrados. Debe registrar al menos un empleado antes de crear una sucursal.
        </Alert>
      )}
      
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando sucursales...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Nombre Comercial</th>
                    <th>Responsable</th>
                    <th>Dirección</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {sucursales.length > 0 ? (
                    sucursales.map((sucursal) => (
                      <tr key={sucursal._id}>
                        <td>{sucursal.nombreComercial}</td>
                        <td>{sucursal.responsableId.name}</td>
                        <td>{sucursal.direccion}</td>
                        <td>
                          <span className={`badge ${sucursal.estado ? 'bg-success' : 'bg-danger'}`}>
                            {sucursal.estado ? 'Activo' : 'Inactivo'}
                          </span>
                        </td>
                        <td>
                          <div className="btn-group">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => handleEdit(sucursal)}
                              title="Editar"
                            >
                              <i className="fas fa-edit"></i>
                            </Button>
                            <Button 
                              variant="outline-danger" 
                              size="sm"
                              onClick={() => handleDelete(sucursal._id)}
                              title="Eliminar"
                              className="ms-1"
                            >
                              <i className="fas fa-trash"></i>
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-4">
                        No hay sucursales registradas
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Creación/Edición */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{modalMode === 'create' ? 'Registrar Sucursal' : 'Editar Sucursal'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Nombre Comercial</Form.Label>
              <Form.Control
                type="text"
                value={currentSucursal.nombreComercial}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  nombreComercial: e.target.value
                })}
                required
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Responsable</Form.Label>
              <Form.Select
                value={currentSucursal.responsableId}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  responsableId: e.target.value
                })}
                required
              >
                <option value="">Seleccionar responsable</option>
                {empleados.map((empleado) => (
                  <option key={empleado._id} value={empleado._id}>
                    {empleado.name}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Dirección</Form.Label>
              <Form.Control
                type="text"
                value={currentSucursal.direccion}
                onChange={(e) => setCurrentSucursal({
                  ...currentSucursal,
                  direccion: e.target.value
                })}
                required
              />
            </Form.Group>
            
            {modalMode === 'edit' && (
              <Form.Group className="mb-3">
                <Form.Label>Estado</Form.Label>
                <Form.Select
                  value={currentSucursal.estado.toString()}
                  onChange={(e) => setCurrentSucursal({
                    ...currentSucursal,
                    estado: e.target.value === 'true'
                  })}
                >
                  <option value="true">Activo</option>
                  <option value="false">Inactivo</option>
                </Form.Select>
              </Form.Group>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2"
                onClick={() => setShowModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                variant="success"
                style={{ backgroundColor: '#2e7d32', borderColor: '#2e7d32' }}
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Procesando...
                  </>
                ) : (
                  modalMode === 'create' ? 'Registrar' : 'Actualizar'
                )}
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Sucursales;
