// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import AuthProvider from './context/AuthContext';
import OfflineProvider from './context/OfflineContext';
import PrivateRoute from './components/PrivateRoute';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import SimuladorPage from './pages/SimuladorPage';
import OperacionesPage from './pages/OperacionesPage';
import './styles/styles.css';

const App = () => {
  return (
    <AuthProvider>
      <OfflineProvider>
        <BrowserRouter>
          <Routes>
            <Route path="/login" element={<LoginPage />} />
            
            <Route
              path="/dashboard"
              element={
                <PrivateRoute>
                  <DashboardPage />
                </PrivateRoute>
              }
            />
            
            <Route
              path="/simulador"
              element={
                <PrivateRoute>
                  <SimuladorPage />
                </PrivateRoute>
              }
            />
            
            <Route
              path="/operaciones"
              element={
                <PrivateRoute>
                  <OperacionesPage />
                </PrivateRoute>
              }
            />
            
            {/* Rutas que faltan implementar */}
            <Route path="/sucursales" element={<PrivateRoute><div>Sucursales (Pendiente)</div></PrivateRoute>} />
            <Route path="/usuarios" element={<PrivateRoute><div>Usuarios (Pendiente)</div></PrivateRoute>} />
            <Route path="/liquidacion" element={<PrivateRoute><div>LiquidaciÃ³n (Pendiente)</div></PrivateRoute>} />
            <Route path="/clientes" element={<PrivateRoute><div>Clientes (Pendiente)</div></PrivateRoute>} />
            
            {/* Redirigir a dashboard por defecto */}
            <Route path="/" element={<Navigate to="/dashboard" replace />} />
            <Route path="*" element={<Navigate to="/dashboard" replace />} />
          </Routes>
        </BrowserRouter>
      </OfflineProvider>
    </AuthProvider>
  );
};

export default App;
