// src/components/Aliados.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Row, Col, Modal, Alert } from 'react-bootstrap';
// Eliminamos la importación de api que no se usa ya que usamos datos simulados
// import { api } from '../services/api';

const Aliados = () => {
  const [aliados, setAliados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [currentAliado, setCurrentAliado] = useState({
    name: '',
    contactName: '',
    document: '',
    email: '',
    phone: '',
    address: '',
    commissionRate: 0,
    creditLimit: 0,
    isActive: true
  });
  const [modalMode, setModalMode] = useState('create'); // 'create' o 'edit'
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [filtros, setFiltros] = useState({
    isActive: 'todos',
    buscar: ''
  });

  useEffect(() => {
    loadAliados();
  }, []);

  const loadAliados = async () => {
    try {
      setLoading(true);
      // Simulación de datos para la demo
      // En implementación real, esto vendría de una llamada API: const response = await api.getAliados();
      const aliadosData = [
        {
          _id: '1',
          name: 'DIGITAL CENTER X',
          contactName: 'Oscar David Madrigal',
          document: '900123456-7',
          email: 'contacto@digitalcenterx.com',
          phone: '3001234567',
          address: 'CL SANTANDER CON CR 52 A CENTRO',
          commissionRate: 5,
          creditLimit: 5000000,
          isActive: true
        },
        {
          _id: '2',
          name: 'SAJI TIENDA',
          contactName: 'María Alejandra Pineda',
          document: '901234567-8',
          email: 'contacto@sajitienda.com',
          phone: '3109876543',
          address: 'CL 48 N°51-97 CLL NUEVA',
          commissionRate: 4.5,
          creditLimit: 3000000,
          isActive: true
        }
      ];
      
      setAliados(aliadosData);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar aliados:', error);
      setError('Error al cargar la lista de aliados');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!currentAliado.name || !currentAliado.document) {
      setError('Por favor complete los campos obligatorios');
      return;
    }
    
    try {
      if (modalMode === 'create') {
        // Simulación de creación
        // En implementación real: await api.createAliado(currentAliado);
        const newAliado = {
          _id: Date.now().toString(),
          ...currentAliado,
          isActive: true
        };
        
        setAliados([...aliados, newAliado]);
        setSuccess('Aliado registrado exitosamente');
      } else {
        // Simulación de actualización
        // En implementación real: await api.updateAliado(currentAliado._id, currentAliado);
        const updatedAliados = aliados.map(aliado => 
          aliado._id === currentAliado._id ? currentAliado : aliado
        );
        
        setAliados(updatedAliados);
        setSuccess('Aliado actualizado exitosamente');
      }
      
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al guardar aliado:', error);
      setError('Error al procesar la solicitud');
    }
  };

  const handleEdit = (aliado) => {
    setCurrentAliado(aliado);
    setModalMode('edit');
    setShowModal(true);
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      // Simulación de cambio de estado
      // En implementación real: await api.updateAliadoStatus(id, !currentStatus);
      const updatedAliados = aliados.map(aliado => 
        aliado._id === id ? {...aliado, isActive: !aliado.isActive} : aliado
      );
      
      setAliados(updatedAliados);
      setSuccess(`Aliado ${currentStatus ? 'desactivado' : 'activado'} exitosamente`);
    } catch (error) {
      console.error('Error al cambiar estado:', error);
      setError('Error al cambiar el estado del aliado');
    }
  };

  const resetForm = () => {
    setCurrentAliado({
      name: '',
      contactName: '',
      document: '',
      email: '',
      phone: '',
      address: '',
      commissionRate: 0,
      creditLimit: 0,
      isActive: true
    });
    setError('');
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  // Filtrar aliados según los filtros aplicados
  const filtrarAliados = () => {
    return aliados.filter(aliado => {
      // Filtro por estado
      if (filtros.isActive !== 'todos') {
        const isActive = filtros.isActive === 'activos';
        if (aliado.isActive !== isActive) return false;
      }
      
      // Filtro por búsqueda
      if (filtros.buscar && !aliado.name.toLowerCase().includes(filtros.buscar.toLowerCase()) &&
          !aliado.contactName.toLowerCase().includes(filtros.buscar.toLowerCase()) &&
          !aliado.document.includes(filtros.buscar)) {
        return false;
      }
      
      return true;
    });
  };

  const aliadosFiltrados = filtrarAliados();

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="h3 mb-0">Aliados</h2>
        <Button 
          variant="primary" 
          onClick={() => {
            resetForm();
            setModalMode('create');
            setShowModal(true);
          }}
          style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
        >
          Registrar Aliado
        </Button>
      </div>
      
      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}
      
      {/* Filtros */}
      <Card className="shadow-sm mb-4">
        <Card.Body>
          <Row className="g-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Estado</Form.Label>
                <Form.Select
                  name="isActive"
                  value={filtros.isActive}
                  onChange={handleFiltroChange}
                >
                  <option value="todos">Todos</option>
                  <option value="activos">Activos</option>
                  <option value="inactivos">Inactivos</option>
                </Form.Select>
              </Form.Group>
            </Col>
            
            <Col md={6}>
              <Form.Group>
                <Form.Label>Buscar</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Buscar por nombre, contacto o documento..."
                  name="buscar"
                  value={filtros.buscar}
                  onChange={handleFiltroChange}
                />
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>
      
      {/* Lista de aliados */}
      <Card className="shadow-sm">
        <Card.Body>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando aliados...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Nombre</th>
                    <th>Contacto</th>
                    <th>Documento</th>
                    <th>Email</th>
                    <th>Teléfono</th>
                    <th>Comisión</th>
                    <th>Límite de Crédito</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {aliadosFiltrados.length > 0 ? (
                    aliadosFiltrados.map((aliado) => (
                      <tr key={aliado._id}>
                        <td>{aliado.name}</td>
                        <td>{aliado.contactName}</td>
                        <td>{aliado.document}</td>
                        <td>{aliado.email}</td>
                        <td>{aliado.phone}</td>
                        <td>{aliado.commissionRate}%</td>
                        <td>{formatCurrency(aliado.creditLimit)}</td>
                        <td>
                          <span className={`badge ${aliado.isActive ? 'bg-success' : 'bg-danger'}`}>
                            {aliado.isActive ? 'Activo' : 'Inactivo'}
                          </span>
                        </td>
                        <td>
                          <div className="btn-group">
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => handleEdit(aliado)}
                              title="Editar"
                            >
                              <i className="fas fa-edit"></i>
                            </Button>
                            <Button 
                              variant="outline-warning" 
                              size="sm"
                              onClick={() => handleToggleStatus(aliado._id, aliado.isActive)}
                              title={aliado.isActive ? 'Desactivar' : 'Activar'}
                              className="ms-1"
                            >
                              <i className={`fas fa-${aliado.isActive ? 'ban' : 'check'}`}></i>
                            </Button>
                            <Button 
                              variant="outline-info" 
                              size="sm"
                              title="Ver detalles"
                              className="ms-1"
                            >
                              <i className="fas fa-eye"></i>
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="9" className="text-center py-4">
                        No hay aliados que coincidan con los filtros
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Creación/Edición */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>{modalMode === 'create' ? 'Registrar Aliado' : 'Editar Aliado'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Nombre Comercial *</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.name}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      name: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Documento (NIT) *</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.document}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      document: e.target.value
                    })}
                    required
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Nombre de Contacto</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.contactName}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      contactName: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={currentAliado.email}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      email: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Teléfono</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.phone}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      phone: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Dirección</Form.Label>
                  <Form.Control
                    type="text"
                    value={currentAliado.address}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      address: e.target.value
                    })}
                  />
                </Form.Group>
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Porcentaje de Comisión (%)</Form.Label>
                  <Form.Control
                    type="number"
                    value={currentAliado.commissionRate}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      commissionRate: parseFloat(e.target.value)
                    })}
                    min="0"
                    max="100"
                    step="0.1"
                  />
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Límite de Crédito</Form.Label>
                  <Form.Control
                    type="number"
                    value={currentAliado.creditLimit}
                    onChange={(e) => setCurrentAliado({
                      ...currentAliado,
                      creditLimit: parseFloat(e.target.value)
                    })}
                    min="0"
                  />
                </Form.Group>
              </div>
            </div>
            
            {modalMode === 'edit' && (
              <Form.Group className="mb-3">
                <Form.Check 
                  type="checkbox"
                  id="isActive"
                  label="Aliado Activo"
                  checked={currentAliado.isActive}
                  onChange={(e) => setCurrentAliado({
                    ...currentAliado,
                    isActive: e.target.checked
                  })}
                />
              </Form.Group>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2"
                onClick={() => setShowModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                variant="primary"
                style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
              >
                {modalMode === 'create' ? 'Registrar' : 'Actualizar'}
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Aliados;
