import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

export function Sales() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState('');
  const [sales, setSales] = useState([]); // Estado para las ventas

  useEffect(() => {
    loadProducts();
    loadSales(); // Cargar ventas al montar el componente
  }, []);

  const loadProducts = async () => {
    try {
      const data = await api.getProducts();
      setProducts(data.data.filter(p => p.stock > 0)); // Solo mostrar productos con stock
      setError('');
    } catch (error) {
      console.error('Error loading products:', error);
      setError('Error al cargar productos: ' + error.message);
    }
  };

  const loadSales = async () => {
    try {
      const response = await api.getSales();
      setSales(response.data);
    } catch (error) {
      console.error('Error al cargar ventas:', error);
      setError('Error al cargar el historial de ventas');
    }
  };

  const handleAddToCart = (product) => {
    // Verificar si el producto ya est치 en el carrito
    const existingItem = cart.find(item => item._id === product._id);
    if (existingItem) {
      setError('Este producto ya est치 en el carrito. Modifica su cantidad si deseas m치s unidades.');
      return;
    }

    // Verificar stock disponible
    if (product.stock <= 0) {
      setError('No hay stock disponible para este producto');
      return;
    }

    setCart([...cart, { ...product, quantity: 1 }]);
    setError('');
  };

  const updateCartItemQuantity = (index, newQuantity) => {
    const product = products.find(p => p._id === cart[index]._id);
    if (newQuantity > product.stock) {
      setError(`Solo hay ${product.stock} unidades disponibles de ${product.name}`);
      return;
    }

    const newCart = [...cart];
    newCart[index].quantity = newQuantity;
    setCart(newCart);
    setError('');
  };

  const handleCreateSale = async () => {
    try {
      // Verificar stock antes de crear la venta
      for (const item of cart) {
        const product = products.find(p => p._id === item._id);
        if (!product || product.stock < item.quantity) {
          setError(`Stock insuficiente para ${item.name}`);
          return;
        }
      }

      const sale = {
        items: cart.map(item => ({
          product: item._id,
          quantity: item.quantity,
          price: item.price,
          name: item.name,
          subtotal: item.price * item.quantity
        })),
        paymentMethod: 'Efectivo',
        customer: {
          name: 'Cliente General',
          document: 'General'
        },
        totalAmount: cart.reduce((sum, item) => sum + (item.price * item.quantity), 0),
        status: 'Completada',
        notes: '',
        discount: 0,
        taxes: 19
      };
      
      const response = await api.createSale(sale);
      console.log('Venta creada:', response);
      
      setCart([]);
      setShowModal(false);
      setError('');
      loadProducts(); // Recargar productos para actualizar el stock
      loadSales(); // Recargar la lista de ventas
    } catch (error) {
      console.error('Error detallado:', error.response?.data || error);
      setError('Error al crear la venta: ' + (error.response?.data?.message || error.message));
    }
  };

  // Funci칩n para formatear la fecha
  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString('es-ES', options);
  };

  return (
    <div className="container mt-4">
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      <div className="d-flex justify-content-between mb-4">
        <h2>Ventas</h2>
        <button className="btn btn-primary" onClick={() => setShowModal(true)}>
          Nueva Venta
        </button>
      </div>

      {/* Tabla de historial de ventas */}
      <div className="table-responsive">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Fecha</th>
              <th>Cliente</th>
              <th>Productos</th>
              <th>Total</th>
              <th>Estado</th>
            </tr>
          </thead>
          <tbody>
            {sales.map((sale) => (
              <tr key={sale._id}>
                <td>{formatDate(sale.createdAt)}</td>
                <td>{sale.customer?.name || 'Cliente General'}</td>
                <td>
                  <ul className="list-unstyled mb-0">
                    {sale.items.map((item, index) => (
                      <li key={index}>
                        {item.quantity}x {item.name} - ${item.price}
                      </li>
                    ))}
                  </ul>
                </td>
                <td>${sale.totalAmount}</td>
                <td>
                  <span className={`badge ${sale.status === 'Completada' ? 'bg-success' : 'bg-warning'}`}>
                    {sale.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {sales.length === 0 && (
          <div className="text-center p-3">
            <p>No hay ventas registradas</p>
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Nueva Venta</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => {
                    setShowModal(false);
                    setError('');
                  }}
                ></button>
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <select 
                    className="form-select"
                    onChange={(e) => {
                      const product = products.find(p => p._id === e.target.value);
                      if (product) handleAddToCart(product);
                    }}
                    value=""
                  >
                    <option value="">Seleccionar producto...</option>
                    {products.map(product => (
                      <option key={product._id} value={product._id}>
                        {product.name} - ${product.price} (Stock: {product.stock})
                      </option>
                    ))}
                  </select>
                </div>

                <table className="table">
                  <thead>
                    <tr>
                      <th>Producto</th>
                      <th>Cantidad</th>
                      <th>Precio</th>
                      <th>Subtotal</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {cart.map((item, index) => (
                      <tr key={index}>
                        <td>{item.name}</td>
                        <td>
                          <input
                            type="number"
                            className="form-control"
                            value={item.quantity}
                            onChange={(e) => updateCartItemQuantity(index, parseInt(e.target.value))}
                            min="1"
                            max={products.find(p => p._id === item._id)?.stock}
                          />
                        </td>
                        <td>${item.price}</td>
                        <td>${item.price * item.quantity}</td>
                        <td>
                          <button
                            className="btn btn-danger btn-sm"
                            onClick={() => {
                              const newCart = cart.filter((_, i) => i !== index);
                              setCart(newCart);
                            }}
                          >
                            Eliminar
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                  <tfoot>
                    <tr>
                      <td colSpan="3" className="text-end"><strong>Total:</strong></td>
                      <td colSpan="2">
                        <strong>
                          ${cart.reduce((sum, item) => sum + (item.price * item.quantity), 0)}
                        </strong>
                      </td>
                    </tr>
                  </tfoot>
                </table>

                <button 
                  className="btn btn-primary w-100"
                  onClick={handleCreateSale}
                  disabled={cart.length === 0}
                >
                  Completar Venta
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Sales;
