import React, { useState, useEffect, useContext } from 'react';
import { Row, Col, Card, Form, Button, Table, Alert, InputGroup, Modal } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCalculator,
  faDollarSign,
  faCalendarAlt,  // 'faCalendarAlt' is defined but never used.
  faCheck,        // 'faCheck' is defined but never used.
  faCircleCheck
} from '@fortawesome/free-solid-svg-icons';
import Layout from '../components/layout/Layout';
import { simularCredito, validarCliente, crearCredito } from '../services/apiService';
import { OfflineContext } from '../context/OfflineContext';
import { AuthContext } from '../context/AuthContext';
import { toast } from 'react-toastify';

const SimuladorPage = () => {
  // Estado para simulación
  const [monto, setMonto] = useState('1000000');
  const [plazo, setPlazo] = useState(3);
  const [simulacion, setSimulacion] = useState({
    valorCuota: 350137,
    intereses: 50412,
    seguroVida: 5000,
    totalCredito: 1055412
  });
  const [loading, setLoading] = useState(false);

  // Estado para validación de cliente
  const [showValidacion, setShowValidacion] = useState(false);
  const [cedula, setCedula] = useState('');
  const [telefono, setTelefono] = useState('');
  
  // Estado para datos del cliente
  const [clienteVerificado, setClienteVerificado] = useState(false);
  const [datosCliente, setDatosCliente] = useState({
    nombre: '',
    apellido: '',
    email: '',
    direccion: ''
  });
  
  // Estado para la solicitud de crédito
  const [showSolicitud, setShowSolicitud] = useState(false);
  
  // Contextos
  const { offline } = useContext(OfflineContext);
  const { user } = useContext(AuthContext);

  // Simular crédito
  const handleSimular = async () => {
    if (!monto) {
      toast.error('Por favor ingrese un monto');
      return;
    }

    setLoading(true);
    try {
      // Aquí harías la llamada a tu API
      // const result = await simularCredito(monto, plazo);
      
      // Simulación para mostrar
      const result = {
        valorCuota: parseInt(monto) * 0.35,
        intereses: parseInt(monto) * 0.05,
        seguroVida: 5000,
        totalCredito: parseInt(monto) * 1.05 + 5000
      };
      
      setSimulacion(result);
    } catch (error) {
      console.error('Error al simular:', error);
      toast.error('Ocurrió un error al simular el crédito');
    } finally {
      setLoading(false);
    }
  };

  // Efecto para simular al montar
  useEffect(() => {
    handleSimular();
    
    // Configuración de Metamap cuando se carga el componente
    const script = document.createElement('script');
    script.src = "https://web-button.metamap.com/button.js";
    script.async = true;
    
    // Agregar script al documento
    document.body.appendChild(script);
    
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  // Abrir modal de validación
  const handleSolicitar = () => {
    setShowValidacion(true);
  };

  // Validar cliente
  const handleValidarCliente = () => {
    if (!cedula || !telefono) {
      toast.error('Por favor complete todos los campos');
      return;
    }
    
    // Continuar con la validación en Metamap
    // (el botón Verificame se encarga de esto)
  };
  
  // Configurar eventos de Metamap cuando se muestra el modal
  useEffect(() => {
    if (!showValidacion) return;
    
    // Esperar a que el botón de Metamap esté disponible en el DOM
    const checkForMetamapButton = setInterval(() => {
      const metamapButton = document.querySelector('metamap-button');
      if (metamapButton) {
        clearInterval(checkForMetamapButton);
        
        // Configurar eventos
        metamapButton.addEventListener('metamap:userStartedSdk', (event) => {
          console.log('Verificación iniciada:', event.detail);
        });
        
        metamapButton.addEventListener('metamap:userFinishedSdk', (event) => {
          console.log('Verificación completada:', event.detail);
          
          // Ocultar el botón de Metamap
          metamapButton.style.display = 'none';
          
          // Mostrar mensaje de éxito
          const verificacionMsg = document.createElement('p');
          verificacionMsg.className = 'text-success';
          verificacionMsg.innerHTML = '<strong>✓ Verificación exitosa.</strong> Haga clic en "Continuar" para proceder.';
          
          const verificacionContainer = document.querySelector('#verificacion-container');
          if (verificacionContainer) {
            verificacionContainer.innerHTML = '';
            verificacionContainer.appendChild(verificacionMsg);
          }
          
          // Habilitar botón Continuar
          const continuarBtn = document.querySelector('#continuar-btn');
          if (continuarBtn) {
            continuarBtn.disabled = false;
          }
          
          // Simular obtención de datos del cliente
          setClienteVerificado(true);
          setDatosCliente({
            nombre: "Oscar David",
            apellido: "Madrigal Fonnegra",
            email: "Oxkr96@gmail.com",
            direccion: "Calle Principal 123, Medellín"
          });
        });
      }
    }, 500);
    
    return () => {
      clearInterval(checkForMetamapButton);
    };
  }, [showValidacion]);
  
  // Continuar con la solicitud después de la validación
  const handleContinuar = () => {
    if (clienteVerificado) {
      setShowValidacion(false);
      setShowSolicitud(true);
    } else {
      toast.error('Debe completar la verificación primero');
    }
  };
  
  // Aprobar crédito
  const handleAprobarCredito = async () => {
    setLoading(true);
    try {
      // Aquí harías la llamada a tu API
      // await crearCredito({...datosCliente, monto, plazo});
      
      toast.success('Crédito aprobado exitosamente');
      setShowSolicitud(false);
      
      // Limpiar formulario
      setMonto('1000000');
      setPlazo(3);
      setCedula('');
      setTelefono('');
      setClienteVerificado(false);
      setDatosCliente({
        nombre: '',
        apellido: '',
        email: '',
        direccion: ''
      });
      
      // Volver a simular
      handleSimular();
    } catch (error) {
      console.error('Error al aprobar crédito:', error);
      toast.error('Ocurrió un error al aprobar el crédito');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      <div className="page-title">
        <h1>Simulador</h1>
      </div>
      
      <Row>
        {/* Columna izquierda - Formulario de simulación */}
        <Col md={6}>
          <Card>
            <Card.Header>
              <h3>Simulador</h3>
            </Card.Header>
            <Card.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Monto a financiar</Form.Label>
                  <div className="d-flex align-items-center">
                    <span className="me-2">$</span>
                    <Form.Control
                      type="number"
                      value={monto}
                      onChange={(e) => setMonto(e.target.value)}
                    />
                  </div>
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Plazo (en cuotas)</Form.Label>
                  <div className="d-flex gap-2">
                    {[3, 6, 9].map((p) => (
                      <Button
                        key={p}
                        variant={plazo === p ? "primary" : "outline-secondary"}
                        onClick={() => setPlazo(p)}
                        className="flex-fill"
                      >
                        {p}
                      </Button>
                    ))}
                  </div>
                </Form.Group>
                
                <Button
                  variant="primary"
                  onClick={handleSimular}
                  disabled={loading}
                  className="w-100 mb-3"
                >
                  <FontAwesomeIcon icon={faCalculator} className="me-2" />
                  Calcular
                </Button>
                
                <div className="text-center mt-4">
                  <h4>Valor de cuota:</h4>
                  <h2 className="text-primary">$ {simulacion.valorCuota.toLocaleString()}</h2>
                </div>
                
                <Button
                  variant="success"
                  onClick={handleSolicitar}
                  className="w-100 mt-3"
                >
                  Solicitar
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        {/* Columna derecha - Detalle de la simulación */}
        <Col md={6}>
          <Card>
            <Card.Header>
              <h3>Detalle de la simulación</h3>
            </Card.Header>
            <Card.Body>
              <Table borderless>
                <tbody>
                  <tr>
                    <td>Monto solicitado</td>
                    <td className="text-end">$ {parseInt(monto).toLocaleString()}</td>
                  </tr>
                  <tr>
                    <td>Plazo</td>
                    <td className="text-end">{plazo} meses</td>
                  </tr>
                  <tr>
                    <td>Intereses</td>
                    <td className="text-end">$ {simulacion.intereses.toLocaleString()}</td>
                  </tr>
                  <tr>
                    <td>Seguro de vida</td>
                    <td className="text-end">$ {simulacion.seguroVida.toLocaleString()}</td>
                  </tr>
                  <tr>
                    <td><strong>Valor cuota mensual</strong></td>
                    <td className="text-end"><strong>$ {simulacion.valorCuota.toLocaleString()}</strong></td>
                  </tr>
                  <tr className="bg-light">
                    <td><strong>Costo total del crédito</strong></td>
                    <td className="text-end"><strong>$ {simulacion.totalCredito.toLocaleString()}</strong></td>
                  </tr>
                </tbody>
              </Table>
              
              <Alert variant="info" className="mt-3">
                <strong>Información importante</strong>
                <p className="mb-0">Esta simulación es solo informativa y los valores pueden variar según la evaluación del cliente. La tasa de interés aplicada es del 2.5% mensual.</p>
              </Alert>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Modal de Validación de Cliente */}
      <Modal show={showValidacion} onHide={() => setShowValidacion(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Validación de Cliente</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Número de Cédula</Form.Label>
              <Form.Control
                type="text"
                value={cedula}
                onChange={(e) => setCedula(e.target.value)}
                placeholder="Ingrese número de cédula"
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Teléfono</Form.Label>
              <Form.Control
                type="text"
                value={telefono}
                onChange={(e) => setTelefono(e.target.value)}
                placeholder="Ingrese número de teléfono"
              />
            </Form.Group>
            
            <div id="verificacion-container" className="text-center mt-4">
              <p>Para continuar con la solicitud, se requiere verificar su identidad.</p>
              
              {/* Componente Metamap Button */}
              <metamap-button
                clientid="67e844f12f057303ed9e7b54"
                flowid="67e844f155e2997603974699"
                metadata={JSON.stringify({
                  cedula: cedula || "1032259508",
                  telefono: telefono || "3152075705",
                  source: "simulador_creditos"
                })}
              ></metamap-button>
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowValidacion(false)}>
            Cancelar
          </Button>
          <Button 
            variant="primary" 
            onClick={handleContinuar}
            id="continuar-btn"
            disabled={!clienteVerificado}
          >
            Continuar
          </Button>
        </Modal.Footer>
      </Modal>
      
      {/* Modal de Solicitud de Crédito */}
      <Modal show={showSolicitud} onHide={() => setShowSolicitud(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Solicitud de Crédito</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Alert variant="success" className="mb-4">
            <FontAwesomeIcon icon={faCircleCheck} className="me-2" />
            Identidad verificada exitosamente
          </Alert>
          
          <h5>Datos del Cliente:</h5>
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Cédula</Form.Label>
                <Form.Control value={cedula || "1032259508"} readOnly />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Teléfono</Form.Label>
                <Form.Control value={telefono || "3152075705"} readOnly />
              </Form.Group>
            </Col>
          </Row>
          
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Nombre</Form.Label>
                <Form.Control value={datosCliente.nombre} readOnly />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Apellido</Form.Label>
                <Form.Control value={datosCliente.apellido} readOnly />
              </Form.Group>
            </Col>
          </Row>
          
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control value={datosCliente.email} readOnly />
          </Form.Group>
          
          <Form.Group className="mb-3">
            <Form.Label>Dirección</Form.Label>
            <Form.Control value={datosCliente.direccion} readOnly />
          </Form.Group>
          
          <h5 className="mt-4">Resumen del crédito:</h5>
          <Table striped bordered>
            <tbody>
              <tr>
                <td>Monto solicitado</td>
                <td className="text-end">$ {parseInt(monto).toLocaleString()}</td>
              </tr>
              <tr>
                <td>Plazo</td>
                <td className="text-end">{plazo} meses</td>
              </tr>
              <tr>
                <td>Intereses</td>
                <td className="text-end">$ {simulacion.intereses.toLocaleString()}</td>
              </tr>
              <tr>
                <td>Seguro de vida</td>
                <td className="text-end">$ {simulacion.seguroVida.toLocaleString()}</td>
              </tr>
              <tr>
                <td><strong>Valor cuota mensual</strong></td>
                <td className="text-end"><strong>$ {simulacion.valorCuota.toLocaleString()}</strong></td>
              </tr>
              <tr>
                <td><strong>Costo total del crédito</strong></td>
                <td className="text-end"><strong>$ {simulacion.totalCredito.toLocaleString()}</strong></td>
              </tr>
            </tbody>
          </Table>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowSolicitud(false)}>
            Cancelar
          </Button>
          <Button 
            variant="success" 
            onClick={handleAprobarCredito}
            disabled={loading}
          >
            Aprobar Crédito
          </Button>
        </Modal.Footer>
      </Modal>
    </Layout>
  );
};

export default SimuladorPage;
