import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import { Modal } from './common/Modal';
import { Button } from './common/Button';
import { Table } from './common/Table';

export function Suppliers() {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    sortBy: 'name'
  });
  
  const [modalConfig, setModalConfig] = useState({
    show: false,
    type: null, // 'create', 'edit'
    title: '',
    data: null
  });

  useEffect(() => {
    loadSuppliers();
  }, [filters]);

  const loadSuppliers = async () => {
    try {
      const response = await api.getSuppliers(filters);
      setSuppliers(response.data);
    } catch (error) {
      toast.error('Error al cargar la lista de proveedores');
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (type, supplier = null) => {
    const titles = {
      create: 'Nuevo Proveedor',
      edit: 'Editar Proveedor'
    };

    setModalConfig({
      show: true,
      type,
      title: titles[type],
      data: supplier || {
        nombre: '',
        contacto: '',
        email: '',
        telefono: '',
        terminosPago: '',
        estado: 'ACTIVO'
      }
    });
  };

  const handleCloseModal = () => {
    setModalConfig({
      show: false,
      type: null,
      title: '',
      data: null
    });
  };

  const handleSubmit = async (formData) => {
    try {
      if (modalConfig.type === 'edit') {
        await api.updateSupplier(formData.id, formData);
        toast.success('Proveedor actualizado exitosamente');
      } else {
        await api.createSupplier(formData);
        toast.success('Proveedor creado exitosamente');
      }
      handleCloseModal();
      loadSuppliers();
    } catch (error) {
      toast.error('Error al procesar la operación');
    }
  };

  const handleToggleStatus = async (supplier) => {
    try {
      const newStatus = supplier.estado === 'ACTIVO' ? 'INACTIVO' : 'ACTIVO';
      await api.updateSupplier(supplier.id, { ...supplier, estado: newStatus });
      toast.success(`Proveedor ${newStatus.toLowerCase()} exitosamente`);
      loadSuppliers();
    } catch (error) {
      toast.error('Error al cambiar el estado del proveedor');
    }
  };

  const renderModalContent = () => {
    if (!modalConfig.show) return null;

    return (
      <form onSubmit={(e) => {
        e.preventDefault();
        handleSubmit({
          id: modalConfig.data?.id,
          nombre: e.target.nombre.value,
          contacto: e.target.contacto.value,
          email: e.target.email.value,
          telefono: e.target.telefono.value,
          terminosPago: e.target.terminosPago.value,
          estado: e.target.estado.value
        });
      }}>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Nombre</label>
            <input
              type="text"
              name="nombre"
              defaultValue={modalConfig.data?.nombre}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Contacto</label>
            <input
              type="text"
              name="contacto"
              defaultValue={modalConfig.data?.contacto}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              name="email"
              defaultValue={modalConfig.data?.email}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Teléfono</label>
            <input
              type="text"
              name="telefono"
              defaultValue={modalConfig.data?.telefono}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Términos de Pago</label>
            <select
              name="terminosPago"
              defaultValue={modalConfig.data?.terminosPago}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            >
              <option value="Contado">Contado</option>
              <option value="15 días">15 días</option>
              <option value="30 días">30 días</option>
              <option value="45 días">45 días</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Estado</label>
            <select
              name="estado"
              defaultValue={modalConfig.data?.estado}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            >
              <option value="ACTIVO">Activo</option>
              <option value="INACTIVO">Inactivo</option>
            </select>
          </div>
        </div>
      </form>
    );
  };

  const columns = [
    { header: 'Nombre', accessor: 'nombre' },
    { header: 'Contacto', accessor: 'contacto' },
    { header: 'Email', accessor: 'email' },
    { header: 'Teléfono', accessor: 'telefono' },
    { header: 'Términos de Pago', accessor: 'terminosPago' },
    {
      header: 'Estado',
      accessor: 'estado',
      cell: (value) => (
        <span className={`px-2 py-1 rounded text-white ${
          value === 'ACTIVO' ? 'bg-green-500' : 'bg-red-500'
        }`}>
          {value}
        </span>
      )
    },
    {
      header: 'Acciones',
      accessor: 'id',
      cell: (_, row) => (
        <div className="flex space-x-2">
          <Button
            onClick={() => handleOpenModal('edit', row)}
            variant="primary"
            className="text-sm"
          >
            Editar
          </Button>
          <Button
            onClick={() => handleToggleStatus(row)}
            variant="danger"
            className="text-sm"
          >
            {row.estado === 'ACTIVO' ? 'Desactivar' : 'Activar'}
          </Button>
        </div>
      )
    }
  ];

  if (loading) {
    return (
      <div className="flex justify-center items-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500" />
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Proveedores</h1>
        <Button
          onClick={() => handleOpenModal('create')}
          variant="primary"
        >
          Nuevo Proveedor
        </Button>
      </div>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <input
          type="text"
          placeholder="Buscar por nombre o contacto..."
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.search}
          onChange={(e) => setFilters({ ...filters, search: e.target.value })}
        />
        <select
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.status}
          onChange={(e) => setFilters({ ...filters, status: e.target.value })}
        >
          <option value="">Todos</option>
          <option value="ACTIVO">Activos</option>
          <option value="INACTIVO">Inactivos</option>
        </select>
        <select
          className="rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          value={filters.sortBy}
          onChange={(e) => setFilters({ ...filters, sortBy: e.target.value })}
        >
          <option value="nombre">Nombre</option>
          <option value="email">Email</option>
          <option value="terminosPago">Términos de Pago</option>
        </select>
      </div>

      <Table
        columns={columns}
        data={suppliers}
        className="w-full"
      />

      <Modal
        show={modalConfig.show}
        title={modalConfig.title}
        onClose={handleCloseModal}
        onConfirm={() => document.querySelector('form').requestSubmit()}
      >
        {renderModalContent()}
      </Modal>
    </div>
  );
}

export default Suppliers;
