import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { api } from '../services/api';
import { toast } from 'react-toastify';
import axios from 'axios';

export function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    sortBy: 'name'
  });
  const [showModal, setShowModal] = useState(false);
  const [currentCustomer, setCurrentCustomer] = useState({
    nombre: '',
    documento: '',
    limiteCredito: 0,
  });
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [payment, setPayment] = useState({
    monto: 0,
    fecha: new Date().toISOString().split('T')[0],
  });

  useEffect(() => {
    loadCustomers();
  }, [filters]);

  const loadCustomers = async () => {
    try {
      const response = await api.getCustomers(filters);
      setCustomers(response.data);
    } catch (error) {
      console.error('Error al cargar clientes:', error);
      toast.error('Error al cargar la lista de clientes');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(amount);
  };

  const formatDate = (date) => {
    if (!date) return 'No registrado';
    return new Date(date).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentCustomer.id) {
        await axios.put(`http://localhost:3001/api/customers/${currentCustomer.id}`, currentCustomer);
        toast.success('Cliente actualizado exitosamente');
      } else {
        await axios.post('http://localhost:3001/api/customers', currentCustomer);
        toast.success('Cliente creado exitosamente');
      }
      setShowModal(false);
      loadCustomers();
    } catch (error) {
      toast.error('Error al guardar el cliente');
    }
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:3001/api/customers/${currentCustomer.id}/payments`, payment);
      toast.success('Pago registrado exitosamente');
      setShowPaymentModal(false);
      loadCustomers();
    } catch (error) {
      toast.error('Error al registrar el pago');
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Clientes</h1>
        <button
          onClick={() => {
            setCurrentCustomer({});
            setShowModal(true);
          }}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Nuevo Cliente
        </button>
      </div>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <input
          type="text"
          placeholder="Buscar por nombre o documento..."
          className="border p-2 rounded"
          value={filters.search}
          onChange={(e) => setFilters({
            ...filters,
            search: e.target.value
          })}
        />
        <select
          className="border p-2 rounded"
          value={filters.status}
          onChange={(e) => setFilters({
            ...filters,
            status: e.target.value
          })}
        >
          <option value="">Todos</option>
          <option value="AL_DIA">Al Día</option>
          <option value="EN_MORA">En Mora</option>
          <option value="BLOQUEADO">Bloqueado</option>
        </select>
        <select
          className="border p-2 rounded"
          value={filters.sortBy}
          onChange={(e) => setFilters({
            ...filters,
            sortBy: e.target.value
          })}
        >
          <option value="name">Nombre</option>
          <option value="debt">Deuda</option>
          <option value="lastPayment">Último Pago</option>
        </select>
      </div>

      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="p-3">Nombre</th>
            <th className="p-3">Documento</th>
            <th className="p-3">Deuda Total</th>
            <th className="p-3">Límite de Crédito</th>
            <th className="p-3">Estado</th>
            <th className="p-3">Último Pago</th>
            <th className="p-3">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr key={customer._id}>
              <td className="p-3">{customer.name}</td>
              <td className="p-3">{customer.document}</td>
              <td className="p-3">{formatCurrency(customer.totalDebt)}</td>
              <td className="p-3">{formatCurrency(customer.creditLimit)}</td>
              <td className="p-3">
                <span className={`px-2 py-1 rounded ${
                  customer.status === 'AL_DIA' ? 'bg-green-500' :
                  customer.status === 'EN_MORA' ? 'bg-yellow-500' :
                  'bg-red-500'
                } text-white`}>
                  {customer.status.replace('_', ' ')}
                </span>
              </td>
              <td className="p-3">{formatDate(customer.lastPaymentDate)}</td>
              <td className="p-3">
                <button
                  onClick={() => {
                    setCurrentCustomer(customer);
                    setShowModal(true);
                  }}
                  className="mr-2 text-blue-500"
                >
                  <i className="fas fa-edit"></i>
                </button>
                <button
                  onClick={() => {
                    setCurrentCustomer(customer);
                    setShowPaymentModal(true);
                  }}
                  className="text-green-500"
                >
                  <i className="fas fa-dollar-sign"></i>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal de Cliente */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg w-96">
            <h2 className="text-xl font-bold mb-4">
              {currentCustomer.id ? 'Editar Cliente' : 'Nuevo Cliente'}
            </h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block mb-2">Nombre</label>
                <input
                  type="text"
                  className="w-full border p-2 rounded"
                  value={currentCustomer.name || ''}
                  onChange={(e) =>
                    setCurrentCustomer({ ...currentCustomer, name: e.target.value })
                  }
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block mb-2">Documento</label>
                <input
                  type="text"
                  className="w-full border p-2 rounded"
                  value={currentCustomer.document || ''}
                  onChange={(e) =>
                    setCurrentCustomer({ ...currentCustomer, document: e.target.value })
                  }
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block mb-2">Límite de Crédito</label>
                <input
                  type="number"
                  className="w-full border p-2 rounded"
                  value={currentCustomer.creditLimit || ''}
                  onChange={(e) =>
                    setCurrentCustomer({
                      ...currentCustomer,
                      creditLimit: parseFloat(e.target.value),
                    })
                  }
                  required
                />
              </div>
              <div className="flex justify-end gap-2">
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="px-4 py-2 border rounded"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-500 text-white rounded"
                >
                  Guardar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Modal de Pago */}
      {showPaymentModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg w-96">
            <h2 className="text-xl font-bold mb-4">Registrar Pago</h2>
            <form onSubmit={handlePayment}>
              <div className="mb-4">
                <label className="block mb-2">Monto</label>
                <input
                  type="number"
                  className="w-full border p-2 rounded"
                  value={payment.monto}
                  onChange={(e) =>
                    setPayment({ ...payment, monto: parseFloat(e.target.value) })
                  }
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block mb-2">Fecha</label>
                <input
                  type="date"
                  className="w-full border p-2 rounded"
                  value={payment.fecha}
                  onChange={(e) =>
                    setPayment({ ...payment, fecha: e.target.value })
                  }
                  required
                />
              </div>
              <div className="flex justify-end gap-2">
                <button
                  type="button"
                  onClick={() => setShowPaymentModal(false)}
                  className="px-4 py-2 border rounded"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-green-500 text-white rounded"
                >
                  Registrar Pago
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Customers;
