import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import { toast } from 'react-toastify';

export function Suppliers() {
  const [suppliers, setSuppliers] = useState([]);
  const [currentSupplier, setCurrentSupplier] = useState({
    nombre: '',
    contacto: '',
    email: '',
    telefono: '',
    terminosPago: 'Contado',
    estado: 'ACTIVO'
  });
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadSuppliers();
  }, []);

  const loadSuppliers = async () => {
    try {
      const response = await api.getSuppliers();
      setSuppliers(response.data);
    } catch (error) {
      setError('Error al cargar la lista de proveedores');
      toast.error('Error al cargar proveedores');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentSupplier._id) {
        await api.updateSupplier(currentSupplier._id, currentSupplier);
        toast.success('Proveedor actualizado exitosamente');
      } else {
        await api.createSupplier(currentSupplier);
        toast.success('Proveedor creado exitosamente');
      }
      setShowModal(false);
      loadSuppliers();
      resetForm();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al procesar la operación');
    }
  };

  const handleToggleStatus = async (supplier) => {
    try {
      const newStatus = supplier.estado === 'ACTIVO' ? 'INACTIVO' : 'ACTIVO';
      await api.updateSupplier(supplier._id, { ...supplier, estado: newStatus });
      toast.success(`Proveedor ${newStatus.toLowerCase()} exitosamente`);
      loadSuppliers();
    } catch (error) {
      toast.error('Error al cambiar el estado del proveedor');
    }
  };

  const resetForm = () => {
    setCurrentSupplier({
      nombre: '',
      contacto: '',
      email: '',
      telefono: '',
      terminosPago: 'Contado',
      estado: 'ACTIVO'
    });
    setError('');
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-4">
        <h2>Proveedores</h2>
        <button 
          className="btn btn-primary" 
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          Nuevo Proveedor
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="table-responsive">
        <table className="table">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Contacto</th>
              <th>Email</th>
              <th>Teléfono</th>
              <th>Términos de Pago</th>
              <th>Estado</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.map(supplier => (
              <tr key={supplier._id}>
                <td>{supplier.nombre}</td>
                <td>{supplier.contacto}</td>
                <td>{supplier.email}</td>
                <td>{supplier.telefono}</td>
                <td>{supplier.terminosPago}</td>
                <td>
                  <span className={`badge ${supplier.estado === 'ACTIVO' ? 'bg-success' : 'bg-danger'}`}>
                    {supplier.estado}
                  </span>
                </td>
                <td>
                  <button 
                    className="btn btn-sm btn-primary me-2"
                    onClick={() => {
                      setCurrentSupplier(supplier);
                      setShowModal(true);
                    }}
                  >
                    Editar
                  </button>
                  <button 
                    className="btn btn-sm btn-danger"
                    onClick={() => handleToggleStatus(supplier)}
                  >
                    {supplier.estado === 'ACTIVO' ? 'Desactivar' : 'Activar'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {currentSupplier._id ? 'Editar Proveedor' : 'Nuevo Proveedor'}
                </h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => {
                    setShowModal(false);
                    resetForm();
                  }}
                ></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Nombre *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={currentSupplier.nombre}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        nombre: e.target.value
                      })}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Contacto *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={currentSupplier.contacto}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        contacto: e.target.value
                      })}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Email *</label>
                    <input
                      type="email"
                      className="form-control"
                      value={currentSupplier.email}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        email: e.target.value
                      })}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Teléfono *</label>
                    <input
                      type="text"
                      className="form-control"
                      value={currentSupplier.telefono}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        telefono: e.target.value
                      })}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Términos de Pago *</label>
                    <select
                      className="form-select"
                      value={currentSupplier.terminosPago}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        terminosPago: e.target.value
                      })}
                      required
                    >
                      <option value="Contado">Contado</option>
                      <option value="15 días">15 días</option>
                      <option value="30 días">30 días</option>
                      <option value="45 días">45 días</option>
                    </select>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Estado</label>
                    <select
                      className="form-select"
                      value={currentSupplier.estado}
                      onChange={(e) => setCurrentSupplier({
                        ...currentSupplier,
                        estado: e.target.value
                      })}
                    >
                      <option value="ACTIVO">Activo</option>
                      <option value="INACTIVO">Inactivo</option>
                    </select>
                  </div>

                  <button type="submit" className="btn btn-primary w-100">
                    {currentSupplier._id ? 'Actualizar' : 'Crear'}
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Suppliers;
