// src/components/Cupos.js
import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Modal } from 'react-bootstrap';
import { api } from '../services/api';

const Cupos = () => {
  const [numCedula, setNumCedula] = useState('');
  const [telefono, setTelefono] = useState('');
  const [montoSolicitado, setMontoSolicitado] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [cupoAprobado, setCupoAprobado] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    // Validaciones básicas
    if (!numCedula || numCedula.length < 8) {
      setError('Por favor ingrese un número de cédula válido');
      setLoading(false);
      return;
    }

    if (!telefono || telefono.length < 10) {
      setError('Por favor ingrese un número de teléfono válido');
      setLoading(false);
      return;
    }

    if (!montoSolicitado || parseFloat(montoSolicitado) <= 0) {
      setError('Por favor ingrese un monto solicitado válido');
      setLoading(false);
      return;
    }

    try {
      // Simulación de una llamada a la API
      // En implementación real, se llamaría a api.solicitarCupo(...)
      setTimeout(() => {
        // Simulamos una aprobación automática para la demo
        const montoAprobado = Math.round(parseFloat(montoSolicitado) * 0.8);
        
        setCupoAprobado({
          cedula: numCedula,
          telefono: telefono,
          montoSolicitado: parseFloat(montoSolicitado),
          montoAprobado: montoAprobado,
          codigo: `CP-${Math.floor(1000 + Math.random() * 9000)}`
        });
        
        setShowModal(true);
        setLoading(false);
        resetForm();
      }, 1500);
    } catch (error) {
      console.error('Error al solicitar cupo:', error);
      setError('Ocurrió un error al procesar la solicitud. Por favor intente nuevamente.');
      setLoading(false);
    }
  };

  const resetForm = () => {
    setNumCedula('');
    setTelefono('');
    setMontoSolicitado('');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  return (
    <div className="container-fluid">
      <h2 className="h3 mb-4">Solicitar cupo de compra</h2>
      
      <div className="row">
        <div className="col-md-6 mx-auto">
          <Card className="shadow-sm mb-4">
            <Card.Body>
              {error && <Alert variant="danger">{error}</Alert>}
              {success && <Alert variant="success">{success}</Alert>}
              
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Número de Cédula</Form.Label>
                  <Form.Control
                    type="text"
                    value={numCedula}
                    onChange={(e) => {
                      // Solo permitir números
                      const value = e.target.value.replace(/[^0-9]/g, '');
                      setNumCedula(value);
                    }}
                    placeholder="Ingrese número de cédula"
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Teléfono</Form.Label>
                  <Form.Control
                    type="text"
                    value={telefono}
                    onChange={(e) => {
                      // Solo permitir números
                      const value = e.target.value.replace(/[^0-9]/g, '');
                      setTelefono(value);
                    }}
                    placeholder="Ingrese número de teléfono"
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-4">
                  <Form.Label>Monto a financiar</Form.Label>
                  <div className="input-group">
                    <span className="input-group-text">$</span>
                    <Form.Control
                      type="text"
                      value={montoSolicitado}
                      onChange={(e) => {
                        // Solo permitir números
                        const value = e.target.value.replace(/[^0-9]/g, '');
                        setMontoSolicitado(value);
                      }}
                      placeholder="Ingrese monto a financiar"
                      required
                    />
                  </div>
                </Form.Group>
                
                <div className="d-grid">
                  <Button 
                    type="submit" 
                    variant="primary"
                    disabled={loading}
                    style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Procesando...
                      </>
                    ) : (
                      'Solicitar'
                    )}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </div>
      </div>

      {/* Modal de Aprobación */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton style={{ borderBottom: 'none' }}>
          <Modal.Title>Resultado de Solicitud</Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center py-4">
          <div className="mb-3">
            <div className="rounded-circle mx-auto mb-3 d-flex align-items-center justify-content-center" 
                 style={{ 
                   width: '80px', 
                   height: '80px', 
                   backgroundColor: '#e6f7e6', 
                   color: '#28a745' 
                 }}>
              <i className="fas fa-check-circle fa-3x"></i>
            </div>
            <h4>¡Cupo Aprobado!</h4>
            <p className="text-muted">
              Hemos aprobado un cupo de compra para el cliente.
            </p>
          </div>
          
          <div className="card mb-3">
            <div className="card-body text-start">
              <div className="row mb-2">
                <div className="col-6 text-muted">Cédula:</div>
                <div className="col-6 fw-bold">{cupoAprobado?.cedula || '---'}</div>
              </div>
              <div className="row mb-2">
                <div className="col-6 text-muted">Teléfono:</div>
                <div className="col-6 fw-bold">{cupoAprobado?.telefono || '---'}</div>
              </div>
              <div className="row mb-2">
                <div className="col-6 text-muted">Monto Solicitado:</div>
                <div className="col-6 fw-bold">{cupoAprobado ? formatCurrency(cupoAprobado.montoSolicitado) : '---'}</div>
              </div>
              <div className="row mb-2">
                <div className="col-6 text-muted">Monto Aprobado:</div>
                <div className="col-6 fw-bold text-success">{cupoAprobado ? formatCurrency(cupoAprobado.montoAprobado) : '---'}</div>
              </div>
              <div className="row">
                <div className="col-6 text-muted">Código de operación:</div>
                <div className="col-6 fw-bold">{cupoAprobado?.codigo || '---'}</div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer style={{ borderTop: 'none' }}>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cerrar
          </Button>
          <Button 
            variant="primary" 
            onClick={() => {
              setShowModal(false);
              // Aquí podría redirigir a otra página o realizar otra acción
            }}
            style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
          >
            Ir a Operaciones
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

