// src/pages/SimuladorPage.js
import React, { useState, useEffect, useContext } from 'react';
import { Row, Col, Card, Form, Button, Table, Alert, InputGroup, Modal } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faCalculator, 
  faDollarSign, 
  faCalendarAlt,
  faCheck,
  faCircleCheck
} from '@fortawesome/free-solid-svg-icons';
import Layout from '../components/layout/Layout';
import { simularCredito, validarCliente, crearCredito } from '../services/apiService';
import { OfflineContext } from '../context/OfflineContext';
import { AuthContext } from '../context/AuthContext';
import { toast } from 'react-toastify';

const SimuladorPage = () => {
  // Estado para simulación
  const [monto, setMonto] = useState('');
  const [plazo, setPlazo] = useState(3);
  const [simulacion, setSimulacion] = useState(null);
  const [loading, setLoading] = useState(false);
  
  // Estado para validación de cliente
  const [showValidacion, setShowValidacion] = useState(false);
  const [cedula, setCedula] = useState('');
  const [telefono, setTelefono] = useState('');
  const [validacionLoading, setValidacionLoading] = useState(false);
  const [resultadoValidacion, setResultadoValidacion] = useState(null);
  
  // Estado para formulario de crédito
  const [showFormulario, setShowFormulario] = useState(false);
  const [clienteData, setClienteData] = useState({
    cedula: '',
    nombre: '',
    apellido: '',
    telefono: '',
    email: '',
    direccion: ''
  });
  const [createLoading, setCreateLoading] = useState(false);
  const [creditoCreado, setCreditoCreado] = useState(null);
  
  const { isOnline } = useContext(OfflineContext);
  const { userInfo } = useContext(AuthContext);
  
  // Limpiar simulación cuando cambia el monto o plazo
  useEffect(() => {
    setSimulacion(null);
  }, [monto, plazo]);
  
  // Función para simular crédito
  const handleSimular = async (e) => {
    e.preventDefault();
    
    if (!monto) {
      toast.error('Ingrese un monto válido');
      return;
    }
    
    try {
      setLoading(true);
      const resultado = await simularCredito({ monto, plazo }, isOnline);
      setSimulacion(resultado);
    } catch (error) {
      toast.error('Error al simular crédito: ' + error.message);
    } finally {
      setLoading(false);
    }
  };
  
  // Función para abrir modal de validación
  const handleSolicitar = () => {
    if (!simulacion) {
      toast.error('Primero debe realizar una simulación');
      return;
    }
    
    setShowValidacion(true);
  };
  
  // Función para validar cliente
  const handleValidarCliente = async (e) => {
    e.preventDefault();
    
    if (!cedula || !telefono) {
      toast.error('Ingrese cédula y teléfono');
      return;
    }
    
    try {
      setValidacionLoading(true);
      const resultado = await validarCliente({ cedula, telefono }, isOnline);
      setResultadoValidacion(resultado);
      
      // Si es aprobado, preparar datos para formulario
      if (resultado.aprobado) {
        setClienteData({
          ...clienteData,
          cedula,
          telefono
        });
      }
    } catch (error) {
      toast.error('Error al validar cliente: ' + error.message);
    } finally {
      setValidacionLoading(false);
    }
  };
  
  // Función para continuar al formulario
  const handleContinuar = () => {
    setShowValidacion(false);
    setShowFormulario(true);
  };
  
  // Función para manejar cambios en formulario
  const handleClienteChange = (e) => {
    const { name, value } = e.target;
    setClienteData({
      ...clienteData,
      [name]: value
    });
  };
  
  // Función para crear crédito
  const handleCrearCredito = async (e) => {
    e.preventDefault();
    
    // Validar campos requeridos
    if (!clienteData.nombre || !clienteData.apellido || !clienteData.email) {
      toast.error('Todos los campos son requeridos');
      return;
    }
    
    try {
      setCreateLoading(true);
      
      const creditoData = {
        clienteData,
        monto,
        plazo,
        valorCuota: simulacion.valorCuota,
        codigoVerificacion: resultadoValidacion.codigoVerificacion
      };
      
      const resultado = await crearCredito(creditoData, isOnline);
      setCreditoCreado(resultado);
      toast.success('Crédito creado exitosamente');
      
      // Limpiar formulario y simulación
      setMonto('');
      setPlazo(3);
      setSimulacion(null);
      setCedula('');
      setTelefono('');
      setResultadoValidacion(null);
      setClienteData({
        cedula: '',
        nombre: '',
        apellido: '',
        telefono: '',
        email: '',
        direccion: ''
      });
    } catch (error) {
      toast.error('Error al crear crédito: ' + error.message);
    } finally {
      setCreateLoading(false);
    }
  };
  
  // Formatear valor monetario
  const formatMoney = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };
  
  // Cerrar modal de crédito creado
  const handleCerrarCreditoCreado = () => {
    setCreditoCreado(null);
    setShowFormulario(false);
  };
  
  return (
    <Layout>
      <h2 className="mb-4">Simulador</h2>
      
      <Row>
        <Col md={5} lg={4}>
          <Card className="border-0 shadow-sm mb-4">
            <Card.Header className="bg-white py-3">
              <h5 className="mb-0">
                <FontAwesomeIcon icon={faCalculator} className="me-2 text-primary" />
                Simulador
              </h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSimular}>
                <Form.Group className="mb-3">
                  <Form.Label>Monto a financiar</Form.Label>
                  <InputGroup>
                    <InputGroup.Text>
                      <FontAwesomeIcon icon={faDollarSign} />
                    </InputGroup.Text>
                    <Form.Control
                      type="number"
                      placeholder="0"
                      value={monto}
                      onChange={(e) => setMonto(e.target.value)}
                      min="100000"
                      max="10000000"
                      required
                    />
                  </InputGroup>
                </Form.Group>
                
                <Form.Group className="mb-4">
                  <Form.Label>Plazo (en cuotas)</Form.Label>
                  <div className="d-flex">
                    {[3, 6, 9].map((option) => (
                      <div 
                        key={option}
                        className={`plazo-option flex-grow-1 text-center p-2 border rounded mx-1 cursor-pointer ${plazo === option ? 'border-primary bg-primary text-white' : ''}`}
                        onClick={() => setPlazo(option)}
                        style={{ cursor: 'pointer' }}
                      >
                        {option}
                      </div>
                    ))}
                  </div>
                </Form.Group>
                
                <Button
                  variant="primary"
                  type="submit"
                  className="w-100"
                  disabled={!monto || loading}
                >
                  {loading ? 'Calculando...' : 'Calcular'}
                </Button>
              </Form>
              
              {simulacion && (
                <div className="mt-4">
                  <h6 className="text-center mb-3">Valor de cuota:</h6>
                  <div className="text-center mb-4">
                    <h3 className="text-primary mb-0">{formatMoney(simulacion.valorCuota)}</h3>
                  </div>
                  
                  <Button
                    variant="success"
                    className="w-100"
                    onClick={handleSolicitar}
                  >
                    Solicitar
                  </Button>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={7} lg={8}>
          <Card className="border-0 shadow-sm h-100">
            <Card.Header className="bg-white py-3">
              <h5 className="mb-0">Detalle de la simulación</h5>
            </Card.Header>
            
            {!simulacion ? (
              <Card.Body className="d-flex align-items-center justify-content-center text-muted">
                <div className="text-center p-5">
                  <FontAwesomeIcon icon={faCalculator} className="fa-3x mb-3" />
                  <h5>Ingrese los datos para simular</h5>
                  <p>La información de la simulación aparecerá aquí</p>
                </div>
              </Card.Body>
            ) : (
              <Card.Body>
                <Table className="mb-0" striped>
                  <tbody>
                    <tr>
                      <td className="fw-bold">Monto solicitado</td>
                      <td className="text-end">{formatMoney(simulacion.monto)}</td>
                    </tr>
                    <tr>
                      <td className="fw-bold">Plazo</td>
                      <td className="text-end">{simulacion.plazo} meses</td>
                    </tr>
                    <tr>
                      <td className="fw-bold">Intereses</td>
                      <td className="text-end">{formatMoney(simulacion.intereses)}</td>
                    </tr>
                    <tr>
                      <td className="fw-bold">Seguro de vida</td>
                      <td className="text-end">{formatMoney(simulacion.seguroVida)}</td>
                    </tr>
                    <tr className="table-active">
                      <td className="fw-bold">Valor cuota mensual</td>
                      <td className="text-end fw-bold">{formatMoney(simulacion.valorCuota)}</td>
                    </tr>
                    <tr className="table-primary">
                      <td className="fw-bold">Costo total del crédito</td>
                      <td className="text-end fw-bold">{formatMoney(simulacion.costoTotal)}</td>
                    </tr>
                  </tbody>
                </Table>
                
                <Alert variant="info" className="mt-4">
                  <Alert.Heading>Información importante</Alert.Heading>
                  <p className="mb-0">
                    Esta simulación es solo informativa y los valores pueden variar según la evaluación del cliente.
                    La tasa de interés aplicada es del 2.5% mensual.
                  </p>
                </Alert>
              </Card.Body>
            )}
          </Card>
        </Col>
      </Row>
      
      {/* Modal de Validación */}
      <Modal show={showValidacion} onHide={() => setShowValidacion(false)} backdrop="static">
        <Modal.Header closeButton>
          <Modal.Title>Validación de Cliente</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {resultadoValidacion ? (
            <div className="text-center py-3">
              {resultadoValidacion.aprobado ? (
                <>
                  <div className="text-success mb-3">
                    <FontAwesomeIcon icon={faCircleCheck} className="fa-5x" />
                  </div>
                  <h4 className="mb-3">Cliente validado correctamente</h4>
                  <p className="mb-4">El cliente ha pasado la validación de identidad.</p>
                  <Button variant="success" onClick={handleContinuar}>
                    Continuar con solicitud
                  </Button>
                </>
              ) : (
                <>
                  <div className="text-danger mb-3">
                    <i className="fa fa-times-circle fa-5x"></i>
                  </div>
                  <h4 className="mb-3">No aprobado</h4>
                  <p className="mb-4">{resultadoValidacion.mensaje}</p>
                  <Button variant="secondary" onClick={() => setShowValidacion(false)}>
                    Cerrar
                  </Button>
                </>
              )}
            </div>
          ) : (
            <Form onSubmit={handleValidarCliente}>
              <p className="mb-3">
                Ingrese los datos del cliente para validar su identidad antes de proceder con la solicitud.
              </p>
              
              <Form.Group className="mb-3">
                <Form.Label>Número de Cédula</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Ingrese número de cédula"
                  value={cedula}
                  onChange={(e) => setCedula(e.target.value)}
                  required
                />
              </Form.Group>
              
              <Form.Group className="mb-4">
                <Form.Label>Teléfono</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Ingrese número de teléfono"
                  value={telefono}
                  onChange={(e) => setTelefono(e.target.value)}
                  required
                />
              </Form.Group>
              
              <div className="d-flex justify-content-between">
                <Button variant="secondary" onClick={() => setShowValidacion(false)}>
                  Cancelar
                </Button>
                <Button 
                  variant="primary" 
                  type="submit"
                  disabled={validacionLoading}
                >
                  {validacionLoading ? 'Validando...' : 'Validar Cliente'}
                </Button>
              </div>
            </Form>
          )}
        </Modal.Body>
      </Modal>
      
      {/* Modal de Formulario de Crédito */}
      <Modal 
        show={showFormulario} 
        onHide={() => (!createLoading && setShowFormulario(false))}
        backdrop="static"
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>Solicitud de Crédito</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {creditoCreado ? (
            <div className="text-center py-4">
              <div className="text-success mb-3">
                <FontAwesomeIcon icon={faCircleCheck} className="fa-5x" />
              </div>
              <h4 className="mb-3">¡Crédito Aprobado!</h4>
              <p className="mb-2">El crédito ha sido aprobado exitosamente.</p>
              <p className="mb-4">
                <strong>Código de entrega:</strong>{' '}
                <span className="badge bg-dark fs-6">{creditoCreado.codigoEntrega}</span>
              </p>
              <Button variant="primary" onClick={handleCerrarCreditoCreado}>
                Finalizar
              </Button>
            </div>
          ) : (
            <Form onSubmit={handleCrearCredito}>
              <Row className="mb-3">
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Cédula</Form.Label>
                    <Form.Control
                      type="text"
                      name="cedula"
                      value={clienteData.cedula}
                      disabled
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Teléfono</Form.Label>
                    <Form.Control
                      type="text"
                      name="telefono"
                      value={clienteData.telefono}
                      disabled
                    />
                  </Form.Group>
                </Col>
              </Row>
              
              <Row className="mb-3">
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control
                      type="text"
                      name="nombre"
                      placeholder="Ingrese nombre"
                      value={clienteData.nombre}
                      onChange={handleClienteChange}
                      required
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Apellido</Form.Label>
                    <Form.Control
                      type="text"
                      name="apellido"
                      placeholder="Ingrese apellido"
                      value={clienteData.apellido}
                      onChange={handleClienteChange}
                      required
                    />
                  </Form.Group>
                </Col>
              </Row>
              
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Ingrese email"
                  value={clienteData.email}
                  onChange={handleClienteChange}
                  required
                />
              </Form.Group>
              
              <Form.Group className="mb-4">
                <Form.Label>Dirección</Form.Label>
                <Form.Control
                  type="text"
                  name="direccion"
                  placeholder="Ingrese dirección"
                  value={clienteData.direccion}
                  onChange={handleClienteChange}
                  required
                />
              </Form.Group>
              
              <div className="bg-light p-3 rounded mb-4">
                <h6 className="mb-3">Resumen del crédito</h6>
                <Row>
                  <Col xs={6}>
                    <p className="mb-1"><strong>Monto:</strong> {formatMoney(monto)}</p>
                    <p className="mb-1"><strong>Plazo:</strong> {plazo} meses</p>
                  </Col>
                  <Col xs={6}>
                    <p className="mb-1"><strong>Valor cuota:</strong> {formatMoney(simulacion?.valorCuota)}</p>
                  </Col>
                </Row>
              </div>
              
              <div className="d-flex justify-content-between">
                <Button 
                  variant="secondary" 
                  onClick={() => (!createLoading && setShowFormulario(false))}
                  disabled={createLoading}
                >
                  Cancelar
                </Button>
                <Button 
                  variant="success" 
                  type="submit"
                  disabled={createLoading}
                >
                  {createLoading ? 'Procesando...' : 'Aprobar Crédito'}
                </Button>
              </div>
            </Form>
          )}
        </Modal.Body>
      </Modal>
    </Layout>
  );
};

export default SimuladorPage;
