// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Configurar interceptor para el token
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  }
);

// Interceptor para manejar errores de autenticación
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const api = {
  // Auth
  login: async (credentials) => {
    try {
      const { data } = await axios.post(`${API_URL}/users/Login`, credentials);
      if (data.token) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data));
      }
      return data;
    } catch (error) {
      throw error.response?.data || error;
    }
  },

  logout: () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  },

  // Usuarios
  getUsers: async () => {
    const { data } = await axios.get(`${API_URL}/users`);
    return data;
  },

  getUserById: async (id) => {
    const { data } = await axios.get(`${API_URL}/users/${id}`);
    return data;
  },

  createUser: async (userData) => {
    const { data } = await axios.post(`${API_URL}/users/register`, userData);
    return data;
  },

  updateUser: async (id, userData) => {
    const { data } = await axios.put(`${API_URL}/users/${id}`, userData);
    return data;
  },

  deleteUser: async (id) => {
    const { data } = await axios.delete(`${API_URL}/users/${id}`);
    return data;
  },

  // Aliados
  getAliados: async () => {
    const { data } = await axios.get(`${API_URL}/allies`);
    return data;
  },

  getAliadoById: async (id) => {
    const { data } = await axios.get(`${API_URL}/allies/${id}`);
    return data;
  },

  createAliado: async (aliadoData) => {
    const { data } = await axios.post(`${API_URL}/allies`, aliadoData);
    return data;
  },

  updateAliado: async (id, aliadoData) => {
    const { data } = await axios.put(`${API_URL}/allies/${id}`, aliadoData);
    return data;
  },

  deleteAliado: async (id) => {
    const { data } = await axios.delete(`${API_URL}/allies/${id}`);
    return data;
  },

  // Sucursales
  getSucursales: async () => {
    const { data } = await axios.get(`${API_URL}/branches`);
    return data;
  },

  getSucursalById: async (id) => {
    const { data } = await axios.get(`${API_URL}/branches/${id}`);
    return data;
  },

  createSucursal: async (sucursalData) => {
    const { data } = await axios.post(`${API_URL}/branches`, sucursalData);
    return data;
  },

  updateSucursal: async (id, sucursalData) => {
    const { data } = await axios.put(`${API_URL}/branches/${id}`, sucursalData);
    return data;
  },

  deleteSucursal: async (id) => {
    const { data } = await axios.delete(`${API_URL}/branches/${id}`);
    return data;
  },

  // Cupos de crédito
  solicitarCupo: async (cupoData) => {
    const { data } = await axios.post(`${API_URL}/credit-quotas`, cupoData);
    return data;
  },

  getCupos: async () => {
    const { data } = await axios.get(`${API_URL}/credit-quotas`);
    return data;
  },

  getCupoById: async (id) => {
    const { data } = await axios.get(`${API_URL}/credit-quotas/${id}`);
    return data;
  },

  updateCupo: async (id, cupoData) => {
    const { data } = await axios.put(`${API_URL}/credit-quotas/${id}`, cupoData);
    return data;
  },

  // Simulaciones
  guardarSimulacion: async (simulacionData) => {
    const { data } = await axios.post(`${API_URL}/simulations`, simulacionData);
    return data;
  },

  getSimulaciones: async () => {
    const { data } = await axios.get(`${API_URL}/simulations`);
    return data;
  },

  // Operaciones
  getOperaciones: async (filtros = {}) => {
    const queryParams = new URLSearchParams(filtros).toString();
    const { data } = await axios.get(`${API_URL}/operations${queryParams ? `?${queryParams}` : ''}`);
    return data;
  },

  getOperacionById: async (id) => {
    const { data } = await axios.get(`${API_URL}/operations/${id}`);
    return data;
  },

  createOperacion: async (operacionData) => {
    const { data } = await axios.post(`${API_URL}/operations`, operacionData);
    return data;
  },

  cancelarOperacion: async (id) => {
    const { data } = await axios.put(`${API_URL}/operations/${id}/cancel`);
    return data;
  },

  // Liquidaciones
  getLiquidaciones: async (filtros = {}) => {
    const queryParams = new URLSearchParams(filtros).toString();
    const { data } = await axios.get(`${API_URL}/liquidations${queryParams ? `?${queryParams}` : ''}`);
    return data;
  },

  getLiquidacionById: async (id) => {
    const { data } = await axios.get(`${API_URL}/liquidations/${id}`);
    return data;
  },

  createLiquidacion: async (liquidacionData) => {
    const { data } = await axios.post(`${API_URL}/liquidations`, liquidacionData);
    return data;
  },

  procesarLiquidacion: async (id) => {
    const { data } = await axios.put(`${API_URL}/liquidations/${id}/process`);
    return data;
  },

  // Estadísticas para Dashboard
  getDashboardStats: async () => {
    const { data } = await axios.get(`${API_URL}/stats/dashboard`);
    return data;
  },

  getCustomerStats: async () => {
    const { data } = await axios.get(`${API_URL}/stats/customers`);
    return data;
  },

  getFinancialStats: async () => {
    const { data } = await axios.get(`${API_URL}/stats/financial`);
    return data;
  }
};
