// src/components/Liquidacion.js
import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Row, Col, Modal, Alert } from 'react-bootstrap';
import { api } from '../services/api';

const Liquidacion = () => {
  const [liquidaciones, setLiquidaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [currentLiquidacion, setCurrentLiquidacion] = useState(null);
  const [filtros, setFiltros] = useState({
    tipo: 'Todos',
    fechaCierre: '',
    comercial: 'Todos',
    buscar: ''
  });
  const [resumenDisponible, setResumenDisponible] = useState({
    disponible: 0,
    comision: 0,
    iva: 0,
    aLiquidar: 0
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // Simulación de datos
      // En una implementación real, estos datos vendrían de la API
      
      const liquidacionesData = [
        {
          _id: '1',
          tipo: 'Venta',
          fechaCierre: '01/03/2025',
          comercial: 'María Alejandra Pineda',
          documento: 'FAC-001',
          cliente: 'Juan Pérez',
          operaciones: 3,
          disponible: 1500000,
          comision: 75000,
          comisionPorcentaje: 5,
          iva: 14250,
          aLiquidar: 1410750
        },
        {
          _id: '2',
          tipo: 'Venta',
          fechaCierre: '02/03/2025',
          comercial: 'Oscar David Madrigal',
          documento: 'FAC-002',
          cliente: 'Ana Gómez',
          operaciones: 2,
          disponible: 2000000,
          comision: 100000,
          comisionPorcentaje: 5,
          iva: 19000,
          aLiquidar: 1881000
        }
      ];
      
      // Calcular resumen de disponible
      const resumen = {
        disponible: liquidacionesData.reduce((sum, item) => sum + item.disponible, 0),
        comision: liquidacionesData.reduce((sum, item) => sum + item.comision, 0),
        iva: liquidacionesData.reduce((sum, item) => sum + item.iva, 0),
        aLiquidar: liquidacionesData.reduce((sum, item) => sum + item.aLiquidar, 0)
      };
      
      setLiquidaciones(liquidacionesData);
      setResumenDisponible(resumen);
      setLoading(false);
    } catch (error) {
      console.error('Error al cargar datos:', error);
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const handleLiquidar = () => {
    setShowModal(true);
  };

  const confirmarLiquidacion = async () => {
    try {
      // En una implementación real, esto sería una llamada a la API
      // Simulamos éxito
      
      // Actualizar UI
      setShowModal(false);
      // Mostrar mensaje de éxito, etc.
      
      // Recargar datos
      loadData();
    } catch (error) {
      console.error('Error al liquidar:', error);
    }
  };

  return (
    <div className="container-fluid">
      <h2 className="h3 mb-4">Liquidación</h2>
      
      {/* Tarjetas de resumen */}
      <div className="row mb-4">
        <div className="col-md-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="p-3">
              <div className="d-flex align-items-center mb-2">
                <div className="rounded-circle me-2" style={{ width: '40px', height: '40px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <i className="fas fa-dollar-sign" style={{ color: '#6a0dad' }}></i>
                </div>
                <h6 className="mb-0">Disponible</h6>
              </div>
              <h4 className="mt-2 mb-0">{formatCurrency(resumenDisponible.disponible)}</h4>
            </Card.Body>
          </Card>
        </div>
        
        <div className="col-md-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="p-3">
              <div className="d-flex align-items-center mb-2">
                <div className="rounded-circle me-2" style={{ width: '40px', height: '40px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <i className="fas fa-percentage" style={{ color: '#6a0dad' }}></i>
                </div>
                <h6 className="mb-0">Comisión</h6>
              </div>
              <h4 className="mt-2 mb-0">{formatCurrency(resumenDisponible.comision)}</h4>
            </Card.Body>
          </Card>
        </div>
        
        <div className="col-md-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="p-3">
              <div className="d-flex align-items-center mb-2">
                <div className="rounded-circle me-2" style={{ width: '40px', height: '40px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <i className="fas fa-hand-holding-usd" style={{ color: '#6a0dad' }}></i>
                </div>
                <h6 className="mb-0">IVA</h6>
              </div>
              <h4 className="mt-2 mb-0">{formatCurrency(resumenDisponible.iva)}</h4>
            </Card.Body>
          </Card>
        </div>
        
        <div className="col-md-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="p-3">
              <div className="d-flex align-items-center mb-2">
                <div className="rounded-circle me-2" style={{ width: '40px', height: '40px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                  <i className="fas fa-money-bill-wave" style={{ color: '#6a0dad' }}></i>
                </div>
                <h6 className="mb-0">A liquidar</h6>
              </div>
              <h4 className="mt-2 mb-0">{formatCurrency(resumenDisponible.aLiquidar)}</h4>
            </Card.Body>
          </Card>
        </div>
      </div>
      
      {/* Botón Liquidar */}
      <div className="text-end mb-4">
        <Button 
          variant="primary" 
          onClick={handleLiquidar}
          style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
        >
          Liquidar
        </Button>
      </div>
      
      {/* Tabla de Disponible */}
      <Card className="shadow-sm mb-4">
        <Card.Header className="bg-white py-3">
          <h5 className="mb-0">Disponible</h5>
        </Card.Header>
        <Card.Body>
          {/* Filtros */}
          <div className="mb-4">
            <Row className="g-3">
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Tipo</Form.Label>
                  <Form.Select
                    name="tipo"
                    value={filtros.tipo}
                    onChange={handleFiltroChange}
                  >
                    <option value="Todos">Todos</option>
                    <option value="Venta">Venta</option>
                    <option value="Comisión">Comisión</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Fecha de cierre</Form.Label>
                  <Form.Control
                    type="text"
                    name="fechaCierre"
                    value={filtros.fechaCierre}
                    onChange={handleFiltroChange}
                  />
                </Form.Group>
              </Col>
              
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Comercial / Cajero</Form.Label>
                  <Form.Select
                    name="comercial"
                    value={filtros.comercial}
                    onChange={handleFiltroChange}
                  >
                    <option value="Todos">Todos</option>
                    <option value="María Alejandra Pineda">María Alejandra Pineda</option>
                    <option value="Oscar David Madrigal">Oscar David Madrigal</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              
              <Col md={3}>
                <Form.Group>
                  <Form.Label>Buscar</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Buscar..."
                    name="buscar"
                    value={filtros.buscar}
                    onChange={handleFiltroChange}
                  />
                </Form.Group>
              </Col>
            </Row>
          </div>
          
          {/* Tabla */}
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-purple" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando datos...</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Tipo</th>
                    <th>Fecha de cierre</th>
                    <th>Comercial / Cajero</th>
                    <th>Documento</th>
                    <th>Cliente</th>
                    <th>Operaciones</th>
                    <th>$ Disponible</th>
                    <th>% Comisión</th>
                    <th>$ Comisión</th>
                    <th>IVA 19%</th>
                    <th>$ a liquidar</th>
                    <th>
                      <Form.Check
                        type="checkbox"
                        id="selectAll"
                        label=""
                      />
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {liquidaciones.length > 0 ? (
                    liquidaciones.map((liquidacion) => (
                      <tr key={liquidacion._id}>
                        <td>{liquidacion.tipo}</td>
                        <td>{liquidacion.fechaCierre}</td>
                        <td>{liquidacion.comercial}</td>
                        <td>{liquidacion.documento}</td>
                        <td>{liquidacion.cliente}</td>
                        <td>{liquidacion.operaciones}</td>
                        <td>{formatCurrency(liquidacion.disponible)}</td>
                        <td>{liquidacion.comisionPorcentaje}%</td>
                        <td>{formatCurrency(liquidacion.comision)}</td>
                        <td>{formatCurrency(liquidacion.iva)}</td>
                        <td>{formatCurrency(liquidacion.aLiquidar)}</td>
                        <td>
                          <Form.Check
                            type="checkbox"
                            id={`select-${liquidacion._id}`}
                            label=""
                          />
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="12" className="text-center py-4">
                        No hay datos disponibles
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </Card.Body>
      </Card>
      
      {/* Modal de Confirmación */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Liquidación</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Alert variant="info">
            <i className="fas fa-info-circle me-2"></i>
            Está a punto de liquidar los items seleccionados.
          </Alert>
          
          <div className="my-4">
            <h6>Resumen de liquidación:</h6>
            <table className="table table-sm">
              <tbody>
                <tr>
                  <td>Disponible:</td>
                  <td className="text-end">{formatCurrency(resumenDisponible.disponible)}</td>
                </tr>
                <tr>
                  <td>Comisión:</td>
                  <td className="text-end">{formatCurrency(resumenDisponible.comision)}</td>
                </tr>
                <tr>
                  <td>IVA (19%):</td>
                  <td className="text-end">{formatCurrency(resumenDisponible.iva)}</td>
                </tr>
                <tr className="fw-bold">
                  <td>Total a liquidar:</td>
                  <td className="text-end">{formatCurrency(resumenDisponible.aLiquidar)}</td>
                </tr>
              </tbody>
            </table>
          </div>
          
          <p>¿Está seguro de que desea continuar con la liquidación?</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancelar
          </Button>
          <Button 
            variant="primary" 
            onClick={confirmarLiquidacion}
            style={{ backgroundColor: '#6a0dad', borderColor: '#6a0dad' }}
          >
            Confirmar Liquidación
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Liquidacion;
