// src/components/layout/Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();
  
  const isActive = (path) => location.pathname === path;
  
  return (
    <div className="sidebar bg-white shadow-sm">
      <div className="sidebar-header py-3 px-3 border-bottom">
        <h5 className="m-0">CREDITOS</h5>
      </div>
      <div className="sidebar-menu py-2">
        <Link to="/dashboard" className={`sidebar-item d-block py-2 px-3 ${isActive('/dashboard') ? 'active' : ''}`}>
          Dashboard
        </Link>
        <Link to="/simulador" className={`sidebar-item d-block py-2 px-3 ${isActive('/simulador') ? 'active' : ''}`}>
          Simulador
        </Link>
        <Link to="/operaciones" className={`sidebar-item d-block py-2 px-3 ${isActive('/operaciones') ? 'active' : ''}`}>
          Operaciones
        </Link>
      </div>
    </div>
  );
};

export default Sidebar;
