// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Interceptor para agregar el token a todas las peticiones
axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  error => Promise.reject(error)
);

// Interceptor para manejar errores de autenticación
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const api = {
  // Auth
  login: async (credentials) => {
    const response = await axios.post(`${API_URL}/users/login`, credentials);
    localStorage.setItem('token', response.data.token);
    return response.data;
  },

  logout: () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  },

  // Dashboard
  getStats: () => axios.get(`${API_URL}/dashboard/stats`),

  // Products
  getProducts: () => axios.get(`${API_URL}/products`),
  createProduct: (product) => axios.post(`${API_URL}/products`, product),
  updateProduct: (id, product) => axios.put(`${API_URL}/products/${id}`, product),
  deleteProduct: (id) => axios.delete(`${API_URL}/products/${id}`),

  // Sales
  getSales: () => axios.get(`${API_URL}/sales`),
  createSale: (sale) => axios.post(`${API_URL}/sales`, sale),
};
