// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { operacionService, sucursalService, userService } from '../services/api';
import authService from '../services/auth';
import api from '../services/api';
import { Card, Row, Col, Table } from 'react-bootstrap';
import { FaUsers, FaStore, FaMoneyBillWave, FaChartLine } from 'react-icons/fa';

const Dashboard = () => {
  const [stats, setStats] = useState({
    aliados: 0,
    sucursales: 0,
    operaciones: 0,
    liquidaciones: 0
  });
  const [operacionesRecientes, setOperacionesRecientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        
        // Verificar si el usuario está autenticado
        if (!authService.isAuthenticated()) {
          window.location.href = '/login';
          return;
        }
        
        // Obtener estadísticas del dashboard
        const response = await api.get('/api/v1/dashboard');
        
        if (response.data && response.data.success) {
          setStats({
            aliados: response.data.stats.aliados || 0,
            sucursales: response.data.stats.sucursales || 0,
            operaciones: response.data.stats.operaciones || 0,
            liquidaciones: response.data.stats.liquidaciones || 0
          });
          
          // Asegurarse de que operacionesRecientes sea un array
          setOperacionesRecientes(response.data.operacionesRecientes || []);
        } else {
          console.error('Error al obtener datos del dashboard:', response.data);
          setError('Error al cargar datos del dashboard');
        }
      } catch (error) {
        console.error('Error al obtener datos del dashboard:', error);
        setError('Error al cargar datos del dashboard');
        
        // Usar datos de ejemplo para desarrollo
        setStats({
          aliados: 5,
          sucursales: 12,
          operaciones: 150,
          liquidaciones: 45
        });
        
        setOperacionesRecientes([
          { _id: '1', empleado: 'Juan Pérez', monto: 500000, fecha: '2023-05-15', estado: 'Aprobada' },
          { _id: '2', empleado: 'María López', monto: 750000, fecha: '2023-05-14', estado: 'Pendiente' },
          { _id: '3', empleado: 'Carlos Gómez', monto: 1200000, fecha: '2023-05-13', estado: 'Aprobada' },
          { _id: '4', empleado: 'Ana Martínez', monto: 350000, fecha: '2023-05-12', estado: 'Rechazada' },
          { _id: '5', empleado: 'Pedro Sánchez', monto: 900000, fecha: '2023-05-11', estado: 'Aprobada' }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0
    }).format(value);
  };

  if (loading) {
    return <div className="text-center p-5">Cargando datos del dashboard...</div>;
  }

  return (
    <div className="dashboard-container">
      <h1 className="mb-4">Dashboard</h1>
      
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      
      <Row className="mb-4">
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-primary-light rounded-circle p-3 me-3">
                <FaUsers className="text-primary-custom" size={24} />
              </div>
              <div>
                <h6 className="mb-0">Aliados</h6>
                <h3 className="mb-0">{stats.aliados}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-success-light rounded-circle p-3 me-3">
                <FaStore className="text-success" size={24} />
              </div>
              <div>
                <h6 className="mb-0">Sucursales</h6>
                <h3 className="mb-0">{stats.sucursales}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-warning-light rounded-circle p-3 me-3">
                <FaMoneyBillWave className="text-warning" size={24} />
              </div>
              <div>
                <h6 className="mb-0">Operaciones</h6>
                <h3 className="mb-0">{stats.operaciones}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="indicator-card">
            <Card.Body className="d-flex align-items-center">
              <div className="indicator-icon bg-info-light rounded-circle p-3 me-3">
                <FaChartLine className="text-info" size={24} />
              </div>
              <div>
                <h6 className="mb-0">Liquidaciones</h6>
                <h3 className="mb-0">{stats.liquidaciones}</h3>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Operaciones Recientes</h5>
        </Card.Header>
        <Card.Body>
          {operacionesRecientes && operacionesRecientes.length > 0 ? (
            <Table responsive hover>
              <thead>
                <tr>
                  <th>Empleado</th>
                  <th>Monto</th>
                  <th>Fecha</th>
                  <th>Estado</th>
                </tr>
              </thead>
              <tbody>
                {operacionesRecientes.map((op) => (
                  <tr key={op._id}>
                    <td>{op.empleado}</td>
                    <td>{formatCurrency(op.monto)}</td>
                    <td>{new Date(op.fecha).toLocaleDateString('es-CO')}</td>
                    <td>
                      <span className={`badge ${
                        op.estado === 'Aprobada' ? 'bg-success' :
                        op.estado === 'Rechazada' ? 'bg-danger' : 'bg-warning'
                      }`}>
                        {op.estado}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p className="text-center">No hay operaciones recientes</p>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default Dashboard;
