// src/components/Layout.js
import React, { useState } from 'react';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import { api } from '../services/api';

const Layout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [collapsed, setCollapsed] = useState(false);
  
  const handleLogout = () => {
    api.logout();
    navigate('/login');
  };

  // Verificar si la ruta actual coincide con el path proporcionado
  const isActive = (path) => {
    return location.pathname.startsWith(path);
  };

  return (
    <div className="d-flex">
      {/* Sidebar */}
      <div className={`sidebar bg-white ${collapsed ? 'collapsed' : ''}`} 
           style={{ 
             width: collapsed ? '60px' : '240px', 
             transition: 'width 0.3s ease',
             height: '100vh',
             position: 'fixed',
             left: 0,
             top: 0,
             boxShadow: '0 0 10px rgba(0,0,0,0.1)',
             zIndex: 1000
           }}>
        <div className="d-flex justify-content-between align-items-center p-3 border-bottom">
          {!collapsed && (
            <Link to="/" className="text-decoration-none">
              <div className="d-flex align-items-center">
                {/* Logo circular con texto C */}
                <div 
                  style={{ 
                    width: '30px', 
                    height: '30px', 
                    backgroundColor: '#2e7d32', /* Color verde */
                    borderRadius: '50%',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    color: 'white',
                    fontWeight: 'bold',
                    marginRight: '8px'
                  }}
                >
                  C
                </div>
                <span className="fs-5 fw-semibold" style={{ color: '#2e7d32' }}>Creditos</span>
              </div>
            </Link>
          )}
          <button 
            className="btn btn-sm border-0" 
            onClick={() => setCollapsed(!collapsed)}
            style={{ color: '#2e7d32' }}
          >
            <i className={`fas fa-${collapsed ? 'expand' : 'compress'}-alt`}></i>
          </button>
        </div>

        <div className="p-2">
          <ul className="nav flex-column">
            <li className="nav-item mb-2">
              <Link 
                to="/dashboard" 
                className={`nav-link ${isActive('/dashboard') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/dashboard') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'background-color 0.3s ease'
                }}
              >
                <i className="fas fa-tachometer-alt me-2"></i>
                {!collapsed && <span>Dashboard</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/cupos" 
                className={`nav-link ${isActive('/cupos') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/cupos') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-dollar-sign me-2"></i>
                {!collapsed && <span>Cupo de Compra</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/simulador" 
                className={`nav-link ${isActive('/simulador') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/simulador') ? '#2e7d32' : '#666', 
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-calculator me-2"></i>
                {!collapsed && <span>Simulador</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/operaciones" 
                className={`nav-link ${isActive('/operaciones') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/operaciones') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-exchange-alt me-2"></i>
                {!collapsed && <span>Operaciones</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/liquidacion" 
                className={`nav-link ${isActive('/liquidacion') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/liquidacion') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-money-bill me-2"></i>
                {!collapsed && <span>Liquidación</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/sucursales" 
                className={`nav-link ${isActive('/sucursales') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/sucursales') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-store me-2"></i>
                {!collapsed && <span>Sucursales</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/aliados" 
                className={`nav-link ${isActive('/aliados') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/aliados') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-handshake me-2"></i>
                {!collapsed && <span>Aliados</span>}
              </Link>
            </li>
            <li className="nav-item mb-2">
              <Link 
                to="/empleados" 
                className={`nav-link ${isActive('/empleados') ? 'active bg-light' : ''}`}
                style={{ 
                  color: isActive('/empleados') ? '#2e7d32' : '#666',
                  padding: '10px 15px',
                  borderRadius: '5px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <i className="fas fa-users me-2"></i>
                {!collapsed && <span>Empleados</span>}
              </Link>
            </li>
          </ul>
        </div>

        <div className="mt-auto p-3 border-top">
          <div className="d-flex align-items-center">
            <div className="rounded-circle text-white p-2" style={{ width: '40px', height: '40px', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#2e7d32' }}>
              <i className="fas fa-user"></i>
            </div>
            {!collapsed && (
              <div className="ms-2">
                <div className="small fw-bold">{user?.name}</div>
                <div className="small text-muted">{user?.role}</div>
              </div>
            )}
          </div>
          {!collapsed && (
            <button 
              onClick={handleLogout} 
              className="btn btn-outline w-100 mt-2"
              style={{ borderColor: '#2e7d32', color: '#2e7d32' }}
            >
              Cerrar Sesión
            </button>
          )}
        </div>
      </div>

      {/* Main content */}
      <div className="main-content" style={{ 
        marginLeft: collapsed ? '60px' : '240px', 
        width: `calc(100% - ${collapsed ? '60px' : '240px'})`, 
        transition: 'margin-left 0.3s ease, width 0.3s ease',
        minHeight: '100vh',
        backgroundColor: '#f5f5f5'
      }}>
        <div className="p-3">
          <Outlet />
        </div>
      </div>
    </div>
  );
};

export default Layout;
