// src/components/layout/Sidebar.js
import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faHome,
  faChartLine,
  faStore,
  faUsers,
  faHandHoldingDollar,
  faFileInvoiceDollar,
  faUserTie,
  faSignOutAlt
} from '@fortawesome/free-solid-svg-icons';
import logo from '../../assets/logo.png'; // Asegúrate de tener un logo en assets

const Sidebar = () => {
  const { userInfo, logout, isAdmin } = useContext(AuthContext);
  const location = useLocation();
  
  // Función para verificar ruta activa
  const isActive = (path) => {
    return location.pathname === path;
  };
  
  // Clases para elemento activo
  const getItemClass = (path) => {
    return `sidebar-item d-flex align-items-center p-3 ${isActive(path) ? 'active' : ''}`;
  };
  
  return (
    <div className="sidebar bg-white shadow-sm d-flex flex-column flex-shrink-0 border-end" style={{ width: '280px', height: '100vh', overflowY: 'auto' }}>
      <div className="p-3 border-bottom">
        <div className="d-flex align-items-center">
          <img src={logo} alt="Logo" className="me-2" style={{ height: '40px' }} />
          <h5 className="m-0 fw-bold text-primary">CREDITOS</h5>
        </div>
      </div>
      
      <div className="p-3 border-bottom bg-light">
        <div className="d-flex align-items-center">
          <div className="avatar bg-primary rounded-circle d-flex align-items-center justify-content-center me-2" style={{ width: '40px', height: '40px' }}>
            <span className="text-white fw-bold">{userInfo?.nombre?.charAt(0)}</span>
          </div>
          <div>
            <div className="fw-bold">{userInfo?.nombre} {userInfo?.apellido}</div>
            <small className="text-muted">{userInfo?.tipo}</small>
          </div>
        </div>
      </div>
      
      <div className="py-2 flex-grow-1">
        <Link to="/dashboard" className={getItemClass('/dashboard')}>
          <FontAwesomeIcon icon={faHome} className="me-3" />
          Dashboard
        </Link>
        
        <Link to="/simulador" className={getItemClass('/simulador')}>
          <FontAwesomeIcon icon={faChartLine} className="me-3" />
          Simulador
        </Link>
        
        <Link to="/sucursales" className={getItemClass('/sucursales')}>
          <FontAwesomeIcon icon={faStore} className="me-3" />
          Sucursales
        </Link>
        
        {isAdmin() && (
          <Link to="/usuarios" className={getItemClass('/usuarios')}>
            <FontAwesomeIcon icon={faUserTie} className="me-3" />
            Usuarios
          </Link>
        )}
        
        <Link to="/operaciones" className={getItemClass('/operaciones')}>
          <FontAwesomeIcon icon={faHandHoldingDollar} className="me-3" />
          Operaciones
        </Link>
        
        {isAdmin() && (
          <Link to="/liquidacion" className={getItemClass('/liquidacion')}>
            <FontAwesomeIcon icon={faFileInvoiceDollar} className="me-3" />
            Liquidación
          </Link>
        )}
        
        <Link to="/clientes" className={getItemClass('/clientes')}>
          <FontAwesomeIcon icon={faUsers} className="me-3" />
          Clientes
        </Link>
      </div>
      
      <div className="mt-auto border-top p-3">
        <button
          onClick={logout}
          className="btn btn-outline-danger d-flex align-items-center justify-content-center w-100"
        >
          <FontAwesomeIcon icon={faSignOutAlt} className="me-2" />
          Cerrar Sesión
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
